--
-- Cấu trúc bảng cho bảng `posts`
--

CREATE TABLE `posts` (
  `id` bigint(20) UNSIGNED NOT NULL,
  `title` varchar(200) COLLATE utf8_unicode_ci NOT NULL,
  `views` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `short_desc` text COLLATE utf8_unicode_ci NOT NULL,
  `desc` longtext COLLATE utf8_unicode_ci NOT NULL,
  `image` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `post_category_id` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `post_date` varchar(50) COLLATE utf8_unicode_ci NOT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

--
-- Đang đổ dữ liệu cho bảng `posts`
--

INSERT INTO `posts` (`id`, `title`, `views`, `short_desc`, `desc`, `image`, `post_category_id`, `post_date`, `created_at`, `updated_at`) VALUES
(1, 'Chuyên gia nhận định hiện tại là ‘mùa Altcoin’', '378', '<p>Cụ thể, b&agrave;i ph&acirc;n t&iacute;ch cho biết c&aacute;c chỉ b&aacute;o kỹ thuật thể hiện nhu cầu mua Bitcoin trong d&agrave;i hạn đang xấu đi v&agrave; do đ&oacute; &aacute;p lực b&aacute;n c&oacute; thể tăng l&ecirc;n. Bloomberg cho biết chỉ b&aacute;o Moving Average Convergence Divergence (MACD) đang bắt đầu di chuyển xuống trong những tuần gần đ&acirc;y. Chỉ b&aacute;o MACD l&agrave; một chỉ b&aacute;o xu hướng cho thấy mối quan hệ giữa c&aacute;c đường trung b&igrave;nh gi&aacute;.</p>', '<p><strong>Theo một b&agrave;i ph&acirc;n t&iacute;ch mới của Bloomberg, c&aacute;c m&ocirc; h&igrave;nh gi&aacute; của Bitcoin đang thể hiện một xu hướng giảm gi&aacute; sắp tới. Nhưng c&aacute;c chuy&ecirc;n gia vẫn tin rằng hiện tại l&agrave; &ldquo;m&ugrave;a altcoin&rdquo;.</strong></p>\r\n\r\n<ul>\r\n	<li><strong><a href=\"https://blogtien.com/la-mot-trader-nen-lam-full-time-hay-part-time/\" rel=\"noopener noreferrer\" target=\"_blank\">L&agrave; một trader &ndash; n&ecirc;n l&agrave;m full time hay part time?</a></strong></li>\r\n	<li><strong><a href=\"https://blogtien.com/ice-gap-nhieu-kho-khan-trong-viec-ra-mat-san-giao-dich-tien-ma-hoa/\" rel=\"noopener noreferrer\" target=\"_blank\">ICE gặp nhiều kh&oacute; khăn trong việc ra mắt s&agrave;n giao dịch tiền m&atilde; h&oacute;a</a></strong></li>\r\n	<li><strong><a href=\"https://blogtien.com/ky-su-blockchain-nhan-luong-hon-50-trieu-dong-moi-thang/\" rel=\"noopener noreferrer\" target=\"_blank\">Kỹ sư Blockchain nhận lương hơn 50 triệu đồng mỗi th&aacute;ng</a></strong></li>\r\n</ul>\r\n\r\n<p>Cụ thể, b&agrave;i ph&acirc;n t&iacute;ch cho biết c&aacute;c chỉ b&aacute;o kỹ thuật thể hiện nhu cầu mua Bitcoin trong d&agrave;i hạn đang xấu đi v&agrave; do đ&oacute; &aacute;p lực b&aacute;n c&oacute; thể tăng l&ecirc;n. Bloomberg cho biết chỉ b&aacute;o Moving Average Convergence Divergence (MACD) đang bắt đầu di chuyển xuống trong những tuần gần đ&acirc;y. Chỉ b&aacute;o MACD l&agrave; một chỉ b&aacute;o xu hướng cho thấy mối quan hệ giữa c&aacute;c đường trung b&igrave;nh gi&aacute;.</p>\r\n\r\n<p><a href=\"https://blogtien.com/ty-gia/BTC/bitcoin/\" rel=\"noopener noreferrer\" target=\"_blank\"><strong>Gi&aacute; Bitcoin</strong></a>&nbsp;đ&atilde; nằm ở mức 4.000 USD nhiều lần trong tuần trước v&agrave; đến hiện tại vẫn chưa thể vượt qua được mức n&agrave;y. Bloomberg cho biết nếu Bitcoin kh&ocirc;ng vượt qua mức đ&oacute; th&igrave; sẽ bị &aacute;p lực b&aacute;n rất lớn.</p>\r\n\r\n<p><img height=\"338\" sizes=\"(max-width: 700px) 100vw, 700px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/321.jpg\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/321.jpg 700w, https://blogtien.com/wp-content/uploads/2019/03/321-300x145.jpg 300w\" width=\"700\" /></p>\r\n\r\n<p>Bitcoin đang xấu đi nhưng Altcoin c&oacute; lợi nhuận vượt trội</p>\r\n\r\n<p>&ldquo;Tất cả c&aacute;c chỉ số đ&atilde; rất đẹp cho việc giảm gi&aacute;. Bitcoin hiện đang giống th&aacute;ng 11 năm 2018 khi giảm mạnh từ 6.600 USD xuống gần 3.000 USD. Gi&aacute; đang đi trong phạm vi hẹp v&agrave; c&oacute; một v&agrave;i hiện tượng giảm gi&aacute; r&otilde; rệt hơn trong thời gian qua&rdquo;, nh&agrave; ph&acirc;n t&iacute;ch Mike McGlone của Bloomberg cho biết.</p>\r\n\r\n<p>C&aacute;c chuy&ecirc;n gia n&oacute;i rằng họ đang thấy mọi người bỏ Bitcoin để mua v&agrave;o c&aacute;c Altcoin kh&aacute;c.</p>\r\n\r\n<p>&ldquo;C&aacute;c nh&agrave; đầu tư đang nh&igrave;n thấy tiềm năng của nhiều đồng tiền điện tử nhỏ hơn. Mặc d&ugrave; trong thời k&igrave; kh&oacute; khăn nhưng ch&uacute;ng ta đang thấy c&aacute;c Altcoin mang lại lợi nhuận ngoạn mục trong v&agrave;i tuần qua. V&agrave; b&acirc;y giờ ch&uacute;ng ta đang thấy m&ugrave;a altcoin&rdquo;, nh&agrave; ph&acirc;n t&iacute;ch thị trường cao cấp của EToro, Mati Greenspan cho biết.</p>', '96_Altcoin1.jpg', '3', '07-08-2021', '2021-08-04 10:49:24', '2021-08-06 11:16:12'),
(6, 'Sự kiện Hardfork Constantinople có thể tác động mạnh tới giá Ethereum', '420', '<p><a href=\"https://blogtien.com/hardfork-sap-toi-cua-ethereum-la-gi-lieu-chung-ta-co-nen-lo-lang/\" rel=\"noopener noreferrer\" target=\"_blank\">Hardfork Constantinople</a>&nbsp;&ndash; một bản n&acirc;ng cấp được l&ecirc;n kế hoạch cho đồng tiền điện tử lớn thứ hai thế giới sắp diễn ra.&nbsp;Constantinople dự kiến sẽ xảy ra khi khối 7.280.000 tr&ecirc;n blockchain Ethereum được khai th&aacute;c. Hiện tại, chiều cao khối của Ethereum l&agrave; 7.272.826, c&oacute; nghĩa l&agrave; bản n&acirc;ng cấp sẽ xảy ra trong 24 &ndash; 48 giờ tới, với khoảng&nbsp;4.200 khối&nbsp;đ&atilde; được khai th&aacute;c mỗi ng&agrave;y trong hai tuần qua.</p>', '<h2><strong>Biến động gi&aacute; mạnh ở Ethereum c&oacute; thể diễn ra trong những ng&agrave;y tới, nhờ bản n&acirc;ng cấp Ethereum Constantinople sắp được c&ocirc;ng bố.</strong></h2>\r\n\r\n<ul>\r\n	<li><strong><a href=\"https://blogtien.com/hardfork-cua-tron-vao-ngay-28-duoc-thiet-ke-danh-rieng-cho-cac-nha-dau-tu-to-chuc/\" rel=\"noopener noreferrer\" target=\"_blank\">Hardfork của Tron v&agrave;o ng&agrave;y 28 được thiết kế d&agrave;nh ri&ecirc;ng cho c&aacute;c nh&agrave; đầu tư tổ chức.</a></strong></li>\r\n	<li><strong><a href=\"https://blogtien.com/gia-bitcoin-hom-nay-27-2-dien-bien-am-dam-bitcoin-bi-khoa-chat-trong-khoang-hep/\" rel=\"noopener noreferrer\" target=\"_blank\">Gi&aacute; bitcoin h&ocirc;m nay (27/2): Diễn biến ảm đạm, Bitcoin bị kh&oacute;a chặt trong khoảng hẹp</a></strong></li>\r\n	<li><strong><a href=\"https://blogtien.com/gia-bitcoin-se-chi-dao-dong-tu-3-000-den-5-000-usd-trong-nam-nay-justin-sun/\" rel=\"noopener noreferrer\" target=\"_blank\">Đừng tiếp tục ph&iacute; tiền của bạn khi chọn lựa b&aacute;n coins ra VNĐ ở s&agrave;n kh&ocirc;ng th&iacute;ch hợp</a></strong></li>\r\n</ul>\r\n\r\n<p><a href=\"https://blogtien.com/hardfork-sap-toi-cua-ethereum-la-gi-lieu-chung-ta-co-nen-lo-lang/\" rel=\"noopener noreferrer\" target=\"_blank\">Hardfork Constantinople</a>&nbsp;&ndash; một bản n&acirc;ng cấp được l&ecirc;n kế hoạch cho đồng tiền điện tử lớn thứ hai thế giới sắp diễn ra.&nbsp;Constantinople dự kiến sẽ xảy ra khi khối 7.280.000 tr&ecirc;n blockchain Ethereum được khai th&aacute;c. Hiện tại, chiều cao khối của Ethereum l&agrave; 7.272.826, c&oacute; nghĩa l&agrave; bản n&acirc;ng cấp sẽ xảy ra trong 24 &ndash; 48 giờ tới, với khoảng&nbsp;4.200 khối&nbsp;đ&atilde; được khai th&aacute;c mỗi ng&agrave;y trong hai tuần qua.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, lịch sử dữ liệu cho thấy&nbsp;<a href=\"https://blogtien.com/ty-gia/ETH/ethereum/\" rel=\"noopener noreferrer\" target=\"_blank\">gi&aacute; Ethereum</a>&nbsp;c&oacute; thể biến động mạnh trước v&agrave;i giờ trước bản n&acirc;ng cấp phần mềm.</p>\r\n\r\n<p>Trước đ&oacute;, gi&aacute; Ethereum đ&atilde; biến động mạnh khi bản n&acirc;ng cấp&nbsp;Byzantium được thực hiện v&agrave;o ng&agrave;y 16/10/2017, do kết quả kh&ocirc;ng chắc chắn n&ecirc;n c&aacute;c nh&agrave; đầu tư đ&atilde; b&aacute;n ra Ethereum v&agrave; việc n&agrave;y đ&atilde; khiến Ethereum trượt gi&aacute; 20%.</p>\r\n\r\n<p>Trong biểu đồ, c&aacute;c nh&agrave; đầu tư v&agrave;o ng&agrave;y đ&oacute; đ&atilde; kh&ocirc;ng sẵn s&agrave;ng tin tưởng dự &aacute;n trong bối cảnh thay đổi giao thức Ethereum cơ bản.</p>\r\n\r\n<p><img src=\"https://i0.wp.com/blogtienao.com/wp-content/uploads/2019/02/A%CC%89nh-chu%CC%A3p-Ma%CC%80n-hi%CC%80nh-2019-02-27-lu%CC%81c-11.34.38.jpg?resize=650,261\" /></p>\r\n\r\n<p>Gi&aacute; Ethereum đ&atilde; giảm mạnh nhưng sau đ&oacute; đ&atilde; tăng lại v&agrave;o năm 2017</p>\r\n\r\n<p>Nh&igrave;n chung, phải mất tổng cộng 34 ng&agrave;y để gi&aacute; của Ethereum vượt qua k&ecirc;nh đi ngang sau khi bản n&acirc;ng cấp diễn ra, v&igrave; vậy nếu lịch sử lặp lại, gi&aacute; Ethereum c&oacute; thể được đi xuống hoặc đi ngang sau v&agrave;i tuần khi m&agrave; bản n&acirc;ng cấp&nbsp;Constantinople diễn ra.</p>\r\n\r\n<p>ETH/USD đ&atilde; chứng kiến một đợt giảm gi&aacute; đ&aacute;ng kể v&agrave;o Chủ nhật khi gi&aacute; đ&atilde; giảm 17% sau khi đạt mức cao nhất ba th&aacute;ng trước đ&oacute; trong ng&agrave;y.&nbsp; Tuy nhi&ecirc;n, nếu thị trường tiếp tục k&eacute;o gi&aacute; xuống thấp hơn, việc b&aacute;n th&aacute;o c&oacute; thể sẽ đẩy gi&aacute; Ethereum xuống mức 123 USD.</p>\r\n\r\n<p>Trước đ&oacute;, hardfork Constantinople&nbsp;đ&atilde;&nbsp;bị tr&igrave; ho&atilde;n&nbsp;v&agrave;o th&aacute;ng 1 năm nay do những lỗi kh&ocirc;ng lường trước được trong qu&aacute; tr&igrave;nh thử nghiệm.</p>', '87_Ethereum3.jpg', '2', '07-08-2021', '2021-08-04 10:59:13', '2021-08-06 11:14:08'),
(7, 'Phân tích giá 12/03: Bitcoin, Ethereum, Ripple, Litecoin, EOS, Bitcoin Cash, Binance Coin, Stellar, TRON, Bitcoin SV.', '220', '<p>Động th&aacute;i tăng gi&aacute; của Bitcoin kh&ocirc;ng hề thuyết phục. N&oacute; thậm ch&iacute; kh&ocirc;ng thể th&aacute;ch thức mức kh&aacute;ng cự 4.000 USD v&agrave;o ng&agrave;y 9 th&aacute;ng 3. Một sự phục hồi từ ngưỡng hỗ trợ n&agrave;y sẽ một lần nữa gi&uacute;p đồng coin cố gắng tho&aacute;t ra khỏi 4.000 USD v&agrave; đạt đến ngưỡng kh&aacute;ng cự quan trọng 4.255 USD.</p>', '<p dir=\"ltr\"><strong>Đ&agrave; giảm đ&atilde; xuất hiện v&agrave; phe b&ograve; tiếp tục r&uacute;t lui để nhường chỗ cho những ch&uacute; gấu, c&aacute;c đề xuất đ&atilde; c&oacute; sự điều chỉnh mới. Sau đ&acirc;y h&atilde;y c&ugrave;ng Blogtien đến với b&agrave;i ph&acirc;n t&iacute;ch gi&aacute; h&ocirc;m nay (12/03) nh&eacute;.</strong></p>\r\n\r\n<ul>\r\n	<li><a href=\"https://blogtien.com/phan-tich-gia-09-03-bitcoin-ethereum-ripple-eos-litecoin-bitcoin-cash-binance-coin-stellar-tron-bitcoin-sv/\" rel=\"noopener noreferrer\" target=\"_blank\"><strong>Ph&acirc;n t&iacute;ch gi&aacute; 09/03: Bitcoin, Ethereum, Ripple, EOS, Litecoin, Bitcoin Cash, Binance Coin, Stellar, TRON, Bitcoin SV.</strong></a></li>\r\n	<li><strong><a href=\"https://blogtien.com/phan-tich-gia-07-03-bitcoin-ethereum-ripple-eos-litecoin-bitcoin-cash-binance-coin-stellar-tron-bitcoin-sv/\" rel=\"noopener noreferrer\" target=\"_blank\">Ph&acirc;n t&iacute;ch gi&aacute; 07/03: Bitcoin, Ethereum, Ripple, EOS, Litecoin, Bitcoin Cash, Binance Coin, Stellar, Tron, Bitcoin SV.</a></strong></li>\r\n	<li><strong><a href=\"https://blogtien.com/phan-tich-gia-05-03-bitcoin-ethereum-ripple-eos-litecoin-bitcoin-cash-binance-coin-stellar-tron-bitcoin-sv/\" rel=\"noopener noreferrer\" target=\"_blank\">Ph&acirc;n t&iacute;ch gi&aacute; 05/03: Bitcoin, Ethereum, Ripple, EOS, Litecoin, Bitcoin Cash, Binance Coin, Stellar, Tron, Bitcoin SV.</a></strong></li>\r\n</ul>\r\n\r\n<h2><strong>BTC/USD</strong></h2>\r\n\r\n<p dir=\"ltr\">Động th&aacute;i tăng gi&aacute; của Bitcoin kh&ocirc;ng hề thuyết phục. N&oacute; thậm ch&iacute; kh&ocirc;ng thể th&aacute;ch thức mức kh&aacute;ng cự 4.000 USD v&agrave;o ng&agrave;y 9 th&aacute;ng 3. Điều n&agrave;y cho thấy sự đuối sức của những ch&uacute; b&ograve;. Hiện tại, gi&aacute; đ&atilde; quay trở lại EMA 20 ng&agrave;y, đ&acirc;y l&agrave; một v&ugrave;ng hỗ trợ quan trọng. Nếu phe b&ograve; đực muốn giữ lợi thế th&igrave; họ n&ecirc;n bảo vệ EMA 20 ng&agrave;y. Một sự phục hồi từ ngưỡng hỗ trợ n&agrave;y sẽ một lần nữa gi&uacute;p đồng coin cố gắng tho&aacute;t ra khỏi 4.000 USD v&agrave; đạt đến ngưỡng kh&aacute;ng cự quan trọng 4.255 USD.</p>\r\n\r\n<p dir=\"ltr\"><img height=\"501\" sizes=\"(max-width: 1234px) 100vw, 1234px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/11-1.png\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/11-1.png 1234w, https://blogtien.com/wp-content/uploads/2019/03/11-1-300x122.png 300w, https://blogtien.com/wp-content/uploads/2019/03/11-1-1024x416.png 1024w, https://blogtien.com/wp-content/uploads/2019/03/11-1-768x312.png 768w\" width=\"1234\" /></p>\r\n\r\n<p dir=\"ltr\">Nếu cặp BTC/USD duy tr&igrave; tr&ecirc;n 4.255 USD, n&oacute; sẽ ho&agrave;n th&agrave;nh m&ocirc; h&igrave;nh đ&aacute;y đ&ocirc;i với mục ti&ecirc;u l&agrave; 5.273 USD. Trader c&oacute; thể thiết lập c&aacute;c vị thế d&agrave;i tr&ecirc;n một breakout tr&ecirc;n 4.255 USD. Cho đến l&uacute;c đ&oacute;, c&aacute;c trader n&ecirc;n duy tr&igrave; c&aacute;c điểm dừng lỗ tại 3.236 USD.</p>\r\n\r\n<p dir=\"ltr\">Về ph&iacute;a giảm điểm, ở đ&oacute; c&oacute; sự hỗ trợ tại đường EMA 20 ng&agrave;y v&agrave; b&ecirc;n dưới đường n&agrave;y l&agrave; đường xu hướng tăng. Nếu hỗ trợ n&agrave;y bị ph&aacute; vỡ, th&igrave; ngưỡng hỗ trợ tiếp theo sẽ l&agrave; tại SMA 50 ng&agrave;y. Nếu mức hỗ trợ n&agrave;y cũng bị ph&aacute; vỡ, ch&uacute;ng t&ocirc;i dự đo&aacute;n đồng coin sẽ trượt nhanh đến v&ugrave;ng hỗ trợ cuối c&ugrave;ng l&agrave; 3.355 &ndash; 3.236 USD. C&aacute;c h&agrave;nh động gi&aacute; mờ nhạt chỉ ra rằng Bitcoin sẽ bị r&agrave;ng buộc trong phạm vi hẹp trong ngắn hạn.</p>\r\n\r\n<h2><strong>ETH/USD</strong></h2>\r\n\r\n<p dir=\"ltr\">Ethereum đ&atilde; ph&aacute; vỡ đường EMA 20 ng&agrave;y. Nếu kh&ocirc;ng t&igrave;m thấy sự hỗ trợ ở mức 134 USD, n&oacute; c&oacute; thể trượt đến SMA 50 ng&agrave;y. Dưới mức n&agrave;y, đồng coin c&oacute; thể điều chỉnh theo mức hỗ trợ quan trọng tại 116 USD. Do đ&oacute;, c&aacute;c trader c&oacute; thể giữ mức dừng lỗ tr&ecirc;n c&aacute;c vị thế d&agrave;i c&ograve;n lại tại 125 USD.</p>\r\n\r\n<p dir=\"ltr\"><img height=\"501\" sizes=\"(max-width: 1234px) 100vw, 1234px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/12-1.png\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/12-1.png 1234w, https://blogtien.com/wp-content/uploads/2019/03/12-1-300x122.png 300w, https://blogtien.com/wp-content/uploads/2019/03/12-1-1024x416.png 1024w, https://blogtien.com/wp-content/uploads/2019/03/12-1-768x312.png 768w\" width=\"1234\" /></p>\r\n\r\n<p dir=\"ltr\">Nếu cặp ETH/USD tho&aacute;t khỏi 134 USD hoặc SMA 50 ng&agrave;y, n&oacute; sẽ cố gắng đẩy gi&aacute; l&ecirc;n tr&ecirc;n 144 USD. Cặp coin c&oacute; khả năng lấy đ&agrave; tăng trưởng mới sau khi gi&aacute; duy tr&igrave; tr&ecirc;n ngưỡng n&agrave;y.</p>\r\n\r\n<p dir=\"ltr\">Tuy nhi&ecirc;n, với việc cả hai đường trung b&igrave;nh động đều bằng phẳng v&agrave; chỉ số RSI gần điểm giữa th&igrave; xu hướng trong ngắn hạn sẽ l&agrave; củng cố. Mức kh&aacute;ng cự của phạm vi c&oacute; thể nằm ngay tại đ&acirc;y 144 USD, trong khi mức hỗ trợ ch&uacute;ng ta sẽ xem x&eacute;t trong v&agrave;i ng&agrave;y tới.</p>\r\n\r\n<h2><strong>XRP/USD</strong></h2>\r\n\r\n<p dir=\"ltr\">Ripple đ&atilde; bị giới hạn trong phạm vi 0.275 &ndash; 0.331 USD. C&aacute;c đường trung b&igrave;nh động bằng phẳng v&agrave; chỉ số RSI gần 50 cho thấy sự hợp nhất c&oacute; thể sẽ tiếp tục trong v&agrave;i ng&agrave;y nữa.</p>\r\n\r\n<p dir=\"ltr\"><img height=\"501\" sizes=\"(max-width: 1234px) 100vw, 1234px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/13.png\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/13.png 1234w, https://blogtien.com/wp-content/uploads/2019/03/13-300x122.png 300w, https://blogtien.com/wp-content/uploads/2019/03/13-1024x416.png 1024w, https://blogtien.com/wp-content/uploads/2019/03/13-768x312.png 768w\" width=\"1234\" /></p>\r\n\r\n<p dir=\"ltr\">Một breakout tại phạm vi tr&ecirc;n sẽ đẩy cặp XRP/USD về ph&iacute;a ngưỡng kh&aacute;ng cự của k&ecirc;nh giảm dần. Nếu gi&aacute; duy tr&igrave; tr&ecirc;n k&ecirc;nh, n&oacute; sẽ cho thấy một sự đảo chiều trong xu hướng.</p>\r\n\r\n<p dir=\"ltr\">Tuy nhi&ecirc;n, nếu phe gấu nhấn ch&igrave;m đồng coin dưới 0.275 USD, n&oacute; c&oacute; thể giảm xuống mức thấp h&agrave;ng năm l&agrave; 0.245 USD. Do đ&oacute;, c&aacute;c trader c&oacute; thể giữ điểm dừng lỗ ngay dưới 0.275 USD.</p>\r\n\r\n<h2><strong>LTC/USD</strong></h2>\r\n\r\n<p dir=\"ltr\">Litecoin đ&atilde; một lần nữa đột ph&aacute; l&ecirc;n 56.91 USD v&agrave;o ng&agrave;y 9 th&aacute;ng 3, nhưng phe b&ograve; kh&ocirc;ng thể tận dụng cơ hội. Một lần nữa, gi&aacute; đ&atilde; điều chỉnh trở lại dưới 56.91 USD, đ&acirc;y l&agrave; một dấu hiệu ti&ecirc;u cực.</p>\r\n\r\n<p dir=\"ltr\"><img height=\"501\" sizes=\"(max-width: 1234px) 100vw, 1234px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/14-1.png\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/14-1.png 1234w, https://blogtien.com/wp-content/uploads/2019/03/14-1-300x122.png 300w, https://blogtien.com/wp-content/uploads/2019/03/14-1-1024x416.png 1024w, https://blogtien.com/wp-content/uploads/2019/03/14-1-768x312.png 768w\" width=\"1234\" /></p>\r\n\r\n<p dir=\"ltr\">Phe gấu đang cố gắng đưa cặp LTC/USD về EMA 20 ng&agrave;y, đ&acirc;y l&agrave; một v&ugrave;ng hỗ trợ quan trọng. Nếu cặp coin tho&aacute;t ra khỏi ngưỡng hỗ trợ n&agrave;y, phe b&ograve; sẽ đẩy gi&aacute; l&ecirc;n 60 USD. Mục ti&ecirc;u cần theo d&otilde;i ở đ&agrave; tăng l&agrave; 65.56 USD. Đường trung b&igrave;nh động tăng dần v&agrave; chỉ số RSI nằm trong v&ugrave;ng t&iacute;ch cực cho thấy b&ograve; đang c&oacute; lợi thế.</p>\r\n\r\n<p dir=\"ltr\">Tuy nhi&ecirc;n, RSI lại đang hướng đến v&ugrave;ng ti&ecirc;u cực l&agrave; điều khiến ch&uacute;ng t&ocirc;i lo lắng. Do đ&oacute;, ch&uacute;ng t&ocirc;i đề nghị c&aacute;c trader giữ điểm dừng lỗ ở mức 50 USD. Nếu phe gấu th&agrave;nh c&ocirc;ng đưa đồng tiền kỹ thuật số xuống dưới đường EMA 20 ng&agrave;y, n&oacute; c&oacute; thể trượt xuống 47.24 USD v&agrave; thấp hơn nữa l&agrave; đến SMA 50 ng&agrave;y.</p>\r\n\r\n<h2><strong>EOS/USD</strong></h2>\r\n\r\n<p dir=\"ltr\">EOS đ&atilde; kh&ocirc;ng thể tiến l&ecirc;n mức kh&aacute;ng cự tr&ecirc;n 3.87 USD. Sự thất bại của phe b&ograve; trong việc tăng l&ecirc;n tr&ecirc;n 3.87 USD l&agrave; một dấu hiệu ti&ecirc;u cực. Hiện tại, gi&aacute; đ&atilde; điều chỉnh về EMA 20 ng&agrave;y. Nếu hỗ trợ n&agrave;y được giữ, th&igrave; phe b&ograve; sẽ cố gắng đẩy đồng coin l&ecirc;n tr&ecirc;n 3.87 USD một lần nữa.</p>\r\n\r\n<p dir=\"ltr\"><img height=\"501\" sizes=\"(max-width: 1234px) 100vw, 1234px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/15-1.png\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/15-1.png 1234w, https://blogtien.com/wp-content/uploads/2019/03/15-1-300x122.png 300w, https://blogtien.com/wp-content/uploads/2019/03/15-1-1024x416.png 1024w, https://blogtien.com/wp-content/uploads/2019/03/15-1-768x312.png 768w\" width=\"1234\" /></p>\r\n\r\n<p dir=\"ltr\">Tuy nhi&ecirc;n, nếu EMA 20 ng&agrave;y bị ph&aacute; vỡ, cặp EOS/USD c&oacute; thể giảm xuống mức hỗ trợ tiếp theo l&agrave; 3.15 USD. SMA 50 ng&agrave;y cũng gần với mức n&agrave;y; do đ&oacute;, ch&uacute;ng t&ocirc;i hy vọng phe b&ograve; sẽ bảo vệ được ngưỡng n&agrave;y.</p>\r\n\r\n<p dir=\"ltr\">Thất bại trong việc tho&aacute;t khỏi mức hỗ trợ tại SMA 50 ng&agrave;y c&oacute; thể dẫn đến việc giảm xuống c&ograve;n 2.17 USD. Do đ&oacute;, c&aacute;c trader c&oacute; thể giữ c&aacute;c điểm dừng lỗ ở mức 3.10 USD.</p>\r\n\r\n<h2><strong>BCH/USD</strong></h2>\r\n\r\n<p dir=\"ltr\">Bitcoin Cash tiếp tục giao dịch trong phạm vi hẹp 120-140 USD. Điều n&agrave;y cho thấy sự c&acirc;n bằng giữa cung v&agrave; cầu.&nbsp;C&aacute;c đường trung b&igrave;nh động bằng phẳng v&agrave; chỉ số RSI gần v&ugrave;ng giữa cho thấy sự củng cố.</p>\r\n\r\n<p dir=\"ltr\"><img height=\"501\" sizes=\"(max-width: 1234px) 100vw, 1234px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/16.png\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/16.png 1234w, https://blogtien.com/wp-content/uploads/2019/03/16-300x122.png 300w, https://blogtien.com/wp-content/uploads/2019/03/16-1024x416.png 1024w, https://blogtien.com/wp-content/uploads/2019/03/16-768x312.png 768w\" width=\"1234\" /></p>\r\n\r\n<p dir=\"ltr\">Một đ&agrave; đột ph&aacute; hoặc ph&aacute; vỡ trong phạm vi n&agrave;y sẽ bắt đầu một xu hướng mới cho cặp BCH/USD. Tr&ecirc;n 140 USD, đ&agrave; tăng sẽ tiến đến khu vực tiếp theo l&agrave; 157 v&agrave; 163 USD. Nếu phạm vi n&agrave;y cũng vượt qua th&igrave; BCH c&oacute; thể di chuyển l&ecirc;n đến 175 USD. Do đ&oacute;, c&aacute;c trader c&oacute; thể duy tr&igrave; c&aacute;c điểm dừng lỗ tại 116 USD. Nếu phe gấu nhấn ch&igrave;m đồng coin dưới 120 USD, n&oacute; c&oacute; thể giảm xuống c&ograve;n 105 USD v&agrave; c&oacute; nguy cơ giảm về đến mức thấp h&agrave;ng năm l&agrave; 73.50 USD.</p>\r\n\r\n<h2><strong>BNB/USD</strong></h2>\r\n\r\n<p dir=\"ltr\">Binance Coin đ&atilde; củng cố kể từ khi tho&aacute;t ra khỏi 15 USD v&agrave;o ng&agrave;y 7 th&aacute;ng 3.</p>\r\n\r\n<p dir=\"ltr\"><img height=\"501\" sizes=\"(max-width: 1234px) 100vw, 1234px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/17-1.png\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/17-1.png 1234w, https://blogtien.com/wp-content/uploads/2019/03/17-1-300x122.png 300w, https://blogtien.com/wp-content/uploads/2019/03/17-1-1024x416.png 1024w, https://blogtien.com/wp-content/uploads/2019/03/17-1-768x312.png 768w\" width=\"1234\" /></p>\r\n\r\n<p dir=\"ltr\">Sau khi tăng mạnh, cặp BNB/USD đ&atilde; h&igrave;nh th&agrave;nh một cờ hiệu. Một sự đột ph&aacute; tại đ&acirc;y c&oacute; khả năng tiếp tục xu hướng tăng, với c&aacute;c mục ti&ecirc;u từ 18 USD trở l&ecirc;n. Do đ&oacute;, c&aacute;c trader c&oacute; thể giữ c&aacute;c vị thế d&agrave;i c&ograve;n lại với mức dừng lỗ tại mức h&ograve;a vốn.</p>\r\n\r\n<p dir=\"ltr\">Một đ&agrave; ph&aacute; vỡ tại đ&acirc;y sẽ b&aacute;o hiệu việc điều chỉnh s&acirc;u hơn. Mặc d&ugrave; mục ti&ecirc;u của đ&agrave; ph&aacute; vỡ l&agrave; 9 USD, nhưng ch&uacute;ng t&ocirc;i dự đo&aacute;n sẽ c&oacute; một sự hỗ trợ mạnh mẽ tại EMA 20 ng&agrave;y. Nhưng nếu mức n&agrave;y cũng kh&ocirc;ng giữ được, việc rơi xuống SMA 50 ng&agrave;y l&agrave; c&oacute; thể xảy ra.</p>\r\n\r\n<h2><strong>XLM/USD</strong></h2>\r\n\r\n<p dir=\"ltr\">Stellar đ&atilde; k&iacute;ch hoạt khuyến nghị mua của ch&uacute;ng t&ocirc;i trong ph&acirc;n t&iacute;ch trước. B&acirc;y giờ n&oacute; sẽ di chuyển l&ecirc;n mức kh&aacute;ng cự tiếp theo l&agrave; 0.342 USD.</p>\r\n\r\n<p dir=\"ltr\"><img height=\"501\" sizes=\"(max-width: 1234px) 100vw, 1234px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/18.png\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/18.png 1234w, https://blogtien.com/wp-content/uploads/2019/03/18-300x122.png 300w, https://blogtien.com/wp-content/uploads/2019/03/18-1024x416.png 1024w, https://blogtien.com/wp-content/uploads/2019/03/18-768x312.png 768w\" width=\"1234\" /></p>\r\n\r\n<p dir=\"ltr\">C&aacute;c đường trung b&igrave;nh động đ&atilde; ho&agrave;n th&agrave;nh một sự giao nhau trong xu hướng tăng, đ&oacute; l&agrave; một dấu hiệu tăng gi&aacute;.&nbsp;Đường EMA 20 ng&agrave;y đ&atilde; bắt đầu c&oacute; xu hướng tăng v&agrave; chỉ số RSI cũng tăng gần với v&ugrave;ng qu&aacute; mua. Điều n&agrave;y cho thấy phe b&ograve; c&oacute; lợi thế trong thời gian tới. Cặp&nbsp;XLM/USD c&oacute; thể phải đối mặt với mức kh&aacute;ng cự tại 0.116 USD, nhưng ch&uacute;ng t&ocirc;i hy vọng mức n&agrave;y sẽ được ph&aacute; vỡ.</p>\r\n\r\n<p dir=\"ltr\">Quan điểm tăng gi&aacute; tr&ecirc;n của ch&uacute;ng t&ocirc;i sẽ bị v&ocirc; hiệu nếu gi&aacute; giảm từ một trong những mức kh&aacute;ng cự ph&iacute;a tr&ecirc;n v&agrave; giảm mạnh xuống dưới 0.082 USD. Do đ&oacute;, c&aacute;c trader c&oacute; thể giữ mức dừng lỗ tại 0.08 USD. Ngo&agrave;i ra c&oacute; thể tăng mức dừng lỗ khi gi&aacute; di chuyển l&ecirc;n cao hơn.</p>\r\n\r\n<h2><strong>TRX/USD</strong></h2>\r\n\r\n<p dir=\"ltr\">Tron đang vật lộn để tho&aacute;t khỏi mức hỗ trợ quan trọng ở mức 0.023 USD. Điều n&agrave;y cho thấy sự thiếu hụt người mua ngay cả ở c&aacute;c ngưỡng n&agrave;y. Đường EMA 20 ng&agrave;y đang dốc xuống v&agrave; chỉ số RSI đang suy yếu trong v&ugrave;ng ti&ecirc;u cực, điều n&agrave;y cho thấy gấu đang c&oacute; lợi thế hơn.</p>\r\n\r\n<p dir=\"ltr\"><img height=\"501\" sizes=\"(max-width: 1234px) 100vw, 1234px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/19-1.png\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/19-1.png 1234w, https://blogtien.com/wp-content/uploads/2019/03/19-1-300x122.png 300w, https://blogtien.com/wp-content/uploads/2019/03/19-1-1024x416.png 1024w, https://blogtien.com/wp-content/uploads/2019/03/19-1-768x312.png 768w\" width=\"1234\" /></p>\r\n\r\n<p dir=\"ltr\">Một đ&agrave; ph&aacute; vỡ dưới 0.230 USD sẽ t&igrave;m thấy sự hỗ trợ ở mức 0.020 USD. Nhưng nếu mức n&agrave;y cũng bị ph&aacute; vỡ, đ&agrave; trượt c&oacute; thể đi s&acirc;u tới 0.008 USD, đ&acirc;y l&agrave; một mức hỗ trợ quan trọng.</p>\r\n\r\n<p dir=\"ltr\">Mặt kh&aacute;c, nếu cặp TRX/USD phục hồi mạnh từ mức hiện tại v&agrave; tăng l&ecirc;n tr&ecirc;n cả hai đường trung b&igrave;nh động, n&oacute; c&oacute; thể sẽ leo l&ecirc;n mức 0.028 USD. Ch&uacute;ng t&ocirc;i sẽ c&oacute; đề xuất kh&aacute;c nếu đồng coin ph&aacute; vỡ được phạm vi hiện tại.</p>\r\n\r\n<h2><strong>BSV/USD</strong></h2>\r\n\r\n<p dir=\"ltr\">Bitcoin SV đ&atilde; củng cố trong khoảng từ 71.41 đến 65.03 USD trong năm ng&agrave;y qua. Cả hai đường trung b&igrave;nh động đ&atilde; bị san phẳng v&agrave; chỉ số RSI cũng gần với điểm giữa. Điều n&agrave;y cho thấy sự c&acirc;n bằng giữa phe b&ograve; v&agrave; phe gấu.</p>\r\n\r\n<p dir=\"ltr\"><img height=\"501\" sizes=\"(max-width: 1234px) 100vw, 1234px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/20-1.png\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/20-1.png 1234w, https://blogtien.com/wp-content/uploads/2019/03/20-1-300x122.png 300w, https://blogtien.com/wp-content/uploads/2019/03/20-1-1024x416.png 1024w, https://blogtien.com/wp-content/uploads/2019/03/20-1-768x312.png 768w\" width=\"1234\" /></p>\r\n\r\n<p dir=\"ltr\">Động th&aacute;i tiếp theo sẽ bắt đầu sau khi gi&aacute; tho&aacute;t khỏi phạm vi n&agrave;y. Tr&ecirc;n 71.41 USD, đ&agrave; tăng c&oacute; thể tiến l&ecirc;n 102.58 USD với mức kh&aacute;ng cự nhỏ tại 77.03 USD. Do đ&oacute;, ch&uacute;ng t&ocirc;i sẽ giữ lại khuyến nghị mua được đưa ra trong kỳ ph&acirc;n t&iacute;ch trước.</p>\r\n\r\n<p dir=\"ltr\">Ngược lại, nếu cặp coin giảm xuống dưới v&ugrave;ng hỗ trợ 65.03 &ndash; 58 USD, n&oacute; c&oacute; thể test lại mức thấp 38.52 USD. Do đ&oacute;, c&aacute;c trader n&ecirc;n chờ đợi một breakout được x&aacute;c nhận sau đ&oacute; mới bắt đầu thiết lập c&aacute;c vị thế.</p>', '41_Ethereum2.jpg', '2', '07-08-2021', '2021-08-04 22:44:48', '2021-08-07 07:18:56'),
(8, '80% tổng nguồn cung Ethereum nằm trong 7.572 ví.', '192', '<p>Cụ thể, c&oacute; 80% tổng số Ethereum đang nằm trong c&aacute;c v&iacute; c&oacute; nhiều hơn 1.000 Ethereum. Trong đ&oacute;, c&oacute; 6.490 v&iacute; Ethereum nắm giữ từ 1.000 Ethereum đến 10.000 Ethereum, c&oacute; 923 v&iacute; giữ 10.000 Ethereum đến 100.000 Ethereum v&agrave; 155 v&iacute; từ 100.000 Ethereum đến 1.000.000 Ethereum. Cuối c&ugrave;ng, c&oacute; 4 v&iacute; nắm giữ 1.000.000 Ethereum đến 10.000.000 Ethereum.</p>', '<p><strong>Theo nghi&ecirc;n cứu mới đ&acirc;y được c&ocirc;ng bố bởi Delphi Digital, 80% tổng nguồn cung Ethereum nằm trong 7.572 v&iacute;.</strong></p>\r\n\r\n<ul>\r\n	<li><strong><a href=\"https://blogtien.com/phan-tich-gia-bitcoin-11-03-bitcoin-co-le-van-chua-the-pha-vo-moc-4-000-usd/\" rel=\"noopener noreferrer\" target=\"_blank\">Ph&acirc;n t&iacute;ch gi&aacute; Bitcoin 11/03: Bitcoin c&oacute; lẽ vẫn chưa thể ph&aacute; vỡ mốc 4.000 USD.</a></strong></li>\r\n	<li><strong><a href=\"https://blogtien.com/top-dich-vu-mua-ban-bitcoin-ethereum-usdt-bang-vnd-tai-viet-nam/\" rel=\"noopener noreferrer\" target=\"_blank\">Top Dịch Vụ Mua B&aacute;n Bitcoin, Ethereum, USDT bằng VND tại Việt Nam</a></strong></li>\r\n	<li><strong><a href=\"https://blogtien.com/binance-thong-bao-bao-tri-de-nang-cap-he-thong/\" rel=\"noopener noreferrer\" target=\"_blank\">Binance th&ocirc;ng b&aacute;o bảo tr&igrave; để n&acirc;ng cấp hệ thống</a></strong></li>\r\n</ul>\r\n\r\n<p>Cụ thể, c&oacute; 80% tổng số Ethereum đang nằm trong c&aacute;c v&iacute; c&oacute; nhiều hơn 1.000 Ethereum. Trong đ&oacute;, c&oacute; 6.490 v&iacute; Ethereum nắm giữ từ 1.000 Ethereum đến 10.000 Ethereum, c&oacute; 923 v&iacute; giữ 10.000 Ethereum đến 100.000 Ethereum v&agrave; 155 v&iacute; từ 100.000 Ethereum đến 1.000.000 Ethereum. Cuối c&ugrave;ng, c&oacute; 4 v&iacute; nắm giữ 1.000.000 Ethereum đến 10.000.000 Ethereum.</p>\r\n\r\n<p>C&ugrave;ng với đ&oacute;, Delphi Digital cho biết&nbsp;<a href=\"https://blogtien.com/ty-gia/ETH/ethereum/\" rel=\"noopener noreferrer\" target=\"_blank\">gi&aacute; Ethereum</a>&nbsp;thường giảm 19% sau khi n&acirc;ng cấp phần mềm trong 30 ng&agrave;y từ khi bản n&acirc;ng cấp diễn ra.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, đợt n&acirc;ng cấp gần đ&acirc;y l&agrave; Constantinople v&agrave; St. Petersburg lại kh&ocirc;ng thấy&nbsp;<a href=\"https://blogtien.com/samsung-galaxy-s10-se-chi-ho-tro-ethereum/\" rel=\"noopener noreferrer\" target=\"_blank\">Ethereum</a>&nbsp;giảm mạnh, c&ocirc;ng ty n&agrave;y cho rằng do phần thưởng khối giảm từ 5 Ethereum xuống c&ograve;n 3 Ethereum l&agrave;m giảm ph&aacute;t đồng tiền lớn thứ 2 thị trường n&agrave;y.</p>\r\n\r\n<p>Ngo&agrave;i ra, c&oacute; khoảng 2,3 triệu Ethereum tức 2% tổng cung nằm trong c&aacute;c ứng dụng t&agrave;i ch&iacute;nh phi tập trung. Hầu hết lượng Ethereum n&agrave;y nằm trong hợp đồng th&ocirc;ng minh của MakerDAO cho ph&eacute;p tạo ra đồng stablecoin DAI d&ugrave;ng để vay tiền.</p>\r\n\r\n<p>Cuối c&ugrave;ng, b&aacute;o c&aacute;o n&agrave;y cho biết c&aacute;c rủi ro kỹ thuật trong tương lai của Ethereum sẽ gia tăng th&ecirc;m v&igrave; n&uacute;t mạng Infura của c&ocirc;ng ty ConsenSys được s&aacute;ng lập bởi đồng s&aacute;ng lập Ethereum Joseph Lubin sẽ gi&uacute;p nh&agrave; ph&aacute;t triển dApp m&agrave; kh&ocirc;ng cần lưu trữ tr&ecirc;n n&uacute;t mạng ri&ecirc;ng.</p>\r\n\r\n<p>ConsenSys đang được lưu trữ bởi Amazon Web Services (AWS) &ndash; một nền tảng lưu trữ đ&aacute;m m&acirc;y tập trung của Amazon, điều n&agrave;y đi ngược lại nguy&ecirc;n tắc phi tập trung m&agrave; nh&agrave; s&aacute;ng lập Ethereum&nbsp;<a href=\"https://blogtien.com/vitalik-noi-ethereum-giai-quyet-han-che-cua-bitcoin/\" rel=\"noopener noreferrer\" target=\"_blank\">Vitalik Buterin</a>&nbsp;theo đuổi.</p>', '43_Ethereum1.jpg', '2', '07-08-2021', '2021-08-04 22:45:47', '2021-08-06 11:08:42'),
(9, 'CEO Twitter: Bitcoin là chìa khóa cho tương lai', '459', '<p>Jack Dorsey mới đ&acirc;y khẳng định&nbsp;Bitcoin sẽ l&agrave; một phần quan trọng trong tương lai của Twitter, tại buổi họp tổng kết qu&yacute; 2/2021.V&igrave; vậy, &ocirc;ng muốn ph&aacute;t huy hết tiềm năng của Bitcoin để chuyển đổi hơn nữa c&aacute;c sản phẩm v&agrave; dịch vụ của c&ocirc;ng ty.</p>', '<p><strong>CEO Twitter, Jack Dorsey khẳng định sẽ tiếp tục đầu tư v&agrave; nghi&ecirc;n cứu ứng dụng Bitcoin trong tương lai.&nbsp;</strong></p>\r\n\r\n<p>Jack Dorsey mới đ&acirc;y khẳng định&nbsp;Bitcoin sẽ l&agrave; một phần quan trọng trong tương lai của Twitter, tại buổi họp tổng kết qu&yacute; 2/2021.</p>\r\n\r\n<p>V&igrave; vậy, &ocirc;ng muốn ph&aacute;t huy hết tiềm năng của Bitcoin để chuyển đổi hơn nữa c&aacute;c sản phẩm v&agrave; dịch vụ của c&ocirc;ng ty.</p>\r\n\r\n<p>Trong tương lai, ch&uacute;ng t&ocirc;i sẽ t&iacute;ch hợp Bitcoin v&agrave;o c&aacute;c dịch vụ như thương mại, đăng k&yacute; v&agrave; c&aacute;c t&iacute;nh năng mới như Twitter Tip Jar v&agrave; Super Follows, Dorsey n&oacute;i.</p>\r\n\r\n<p>Vị CEO tiết lộ ba xu hướng ch&iacute;nh m&agrave; Twitter đang ưu ti&ecirc;n ph&aacute;t triển. Đ&oacute; l&agrave; Tr&iacute; tuệ nh&acirc;n tạo, ph&acirc;n quyền v&agrave; Bitcoin.</p>\r\n\r\n<blockquote>\r\n<p><strong>T&ocirc;i nghĩ rằng điều cực kỳ quan trọng đối với Twitter v&agrave; c&aacute;c cổ đ&ocirc;ng của c&ocirc;ng ty l&agrave; ch&uacute;ng t&ocirc;i sẽ tiếp tục xem x&eacute;t v&agrave; đầu tư v&agrave;o Bitcoin</strong></p>\r\n</blockquote>\r\n\r\n<p>Trước đ&acirc;y, Dorsey cho biết Bitcoin như một loại tiền tệ của Internet. Trong cuộc họp, quan điểm n&agrave;y được &ocirc;ng nhấn mạnh một lần nữa.</p>\r\n\r\n<blockquote>\r\n<p><strong>Bitcoin đang ở giai đoạn đầu ph&aacute;t triển tương tự như những g&igrave; m&agrave; Internet trước đ&acirc;y đ&atilde; trải qua. V&igrave; vậy,&nbsp;</strong><strong>n&oacute; vẫn c&ograve;n nhiều tiềm năng để khai th&aacute;c. Đ&acirc;y&nbsp;cũng l&agrave; l&yacute; do ch&uacute;ng t&ocirc;i sẽ tập trung v&agrave;o Bitcoin.</strong></p>\r\n</blockquote>\r\n\r\n<p>Trong The ₿ Word mới đ&acirc;y, CEO Twitter n&oacute;i rằng: Nhiều m&ocirc; h&igrave;nh kinh doanh hiện tại sẽ kh&aacute;c rất nhiều nếu Bitcoin tồn tại trước Twitter hoặc Facebook. Nếu Bitcoin ra đời sớm hơn, ch&uacute;ng t&ocirc;i chắc chắn sẽ kh&ocirc;ng phụ thuộc v&agrave;o m&ocirc; h&igrave;nh kinh doanh quảng c&aacute;o.</p>\r\n\r\n<p>Jack Dorsey được biết đến l&agrave; một trong những tỷ ph&uacute; c&ocirc;ng nghệ c&ocirc;ng khai ủng hộ Bitcoin.</p>\r\n\r\n<p>Cuối năm 2020, &ocirc;ng cho ph&eacute;p c&ocirc;ng ty thanh to&aacute;n kỹ thuật số Square đầu tư 50 triệu USD v&agrave;o Bitcoin.</p>\r\n\r\n<p>V&agrave;o th&aacute;ng 2 năm 2021, &ocirc;ng tiếp tục r&oacute;t th&ecirc;m 170 triệu USD cho đồng tiền điện tử h&agrave;ng đầu n&agrave;y.</p>', '19_bitcoin4.jpg', '1', '07-08-2021', '2021-08-04 22:46:39', '2021-08-06 11:14:37'),
(10, 'Giá Bitcoin ‘giật lên’ 48.000 USD trên Binance, 100 triệu USD lệnh short ‘cháy trong vài phút', '319', '<p>Gi&aacute; Bitcoin tăng mạnh tr&ecirc;n c&aacute;c s&agrave;n trong đầu phi&ecirc;n giao dịch s&aacute;ng thứ Hai tại ch&acirc;u &Aacute;. Cụ thể, Bitcoin đ&atilde;&nbsp;<a href=\"https://blogtien.com/bitcoin-bat-tang-len-38-000-usd-thi-truong-phu-sac-xanh/\">tăng l&ecirc;n gần 40.000 USD</a>&nbsp;tr&ecirc;n một số s&agrave;n giao dịch. Thậm ch&iacute;, đồng tiền điện tử n&agrave;y chạm mốc 48.000 USD tr&ecirc;n hợp đồng tương lai của s&agrave;n Binance, trước khi biểu đồ gi&aacute; thực hiện một c&uacute; &ldquo;r&uacute;t r&acirc;u&rdquo; kinh điển.</p>', '<p><strong>Binance hiện đối mặt với nhiều chỉ tr&iacute;ch khi gi&aacute; Bitcoin vọt l&ecirc;n đến tận 48.000 USD trong hợp đồng tương lai.</strong></p>\r\n\r\n<p>Gi&aacute; Bitcoin tăng mạnh tr&ecirc;n c&aacute;c s&agrave;n trong đầu phi&ecirc;n giao dịch s&aacute;ng thứ Hai tại ch&acirc;u &Aacute;.</p>\r\n\r\n<p>Cụ thể, Bitcoin đ&atilde;&nbsp;<a href=\"https://blogtien.com/bitcoin-bat-tang-len-38-000-usd-thi-truong-phu-sac-xanh/\">tăng l&ecirc;n gần 40.000 USD</a>&nbsp;tr&ecirc;n một số s&agrave;n giao dịch.</p>\r\n\r\n<p>Thậm ch&iacute;, đồng tiền điện tử n&agrave;y chạm mốc 48.000 USD tr&ecirc;n hợp đồng tương lai của s&agrave;n Binance, trước khi biểu đồ gi&aacute; thực hiện một c&uacute; &ldquo;r&uacute;t r&acirc;u&rdquo; kinh điển.</p>\r\n\r\n<p>Trước diễn biến gi&aacute; bất thường n&agrave;y, Binance hiện phải đối mặt với rất nhiều chỉ tr&iacute;ch v&agrave; tức giận từ c&aacute;c nh&agrave; đầu tư.</p>\r\n\r\n<p><a href=\"https://blogtien.com/wp-content/uploads/2021/07/unnamed-file-172.jpg\"><img height=\"656\" sizes=\"(max-width: 1161px) 100vw, 1161px\" src=\"https://blogtien.com/wp-content/uploads/2021/07/unnamed-file-172.jpg\" srcset=\"https://blogtien.com/wp-content/uploads/2021/07/unnamed-file-172.jpg 1161w, https://blogtien.com/wp-content/uploads/2021/07/unnamed-file-172-300x170.jpg 300w, https://blogtien.com/wp-content/uploads/2021/07/unnamed-file-172-1024x579.jpg 1024w, https://blogtien.com/wp-content/uploads/2021/07/unnamed-file-172-768x434.jpg 768w\" width=\"1161\" /></a></p>\r\n\r\n<p>Theo Tradingview, Bitcoin tăng 14% trong v&ograve;ng 6 giờ qua. Hiện đồng tiền số n&agrave;y&nbsp;điều chỉnh v&agrave; giao dịch trong khu vực 38.300 USD.</p>\r\n\r\n<p>Đ&agrave; tăng của Bitcoin&nbsp;bắt đầu xuất hiện v&agrave;o ng&agrave;y thứ Bảy, sau khi Bitcoin th&agrave;nh c&ocirc;ng tho&aacute;t khỏi phạm vi 32.000 USD để tiến l&ecirc;n mức cao nhất trong hai tuần- tr&ecirc;n 34.000 USD.</p>\r\n\r\n<p>Gi&aacute; Bitcoin hiện di chuyển tr&ecirc;n đường MA 50 ng&agrave;y v&agrave; đang nhắm mục ti&ecirc;u đến mức kh&aacute;ng cự tại 41.000 USD. Một khi ph&aacute; vỡ tr&ecirc;n mức n&agrave;y, gi&aacute; tiền điện tử h&agrave;ng đầu c&oacute; thể tăng l&ecirc;n rất nhanh.</p>\r\n\r\n<p>&ldquo;BTC đ&atilde; kh&ocirc;ng ở tr&ecirc;n [đường MA 50 ng&agrave;y] kể từ ng&agrave;y 10/5. Lần cuối c&ugrave;ng t&agrave;i sản vượt l&ecirc;n tr&ecirc;n mức n&agrave;y l&agrave; ng&agrave;y 8/10/2020, thời điểm m&agrave; gi&aacute; đ&atilde; tăng từ 11.000 USD l&ecirc;n 65.000 USD, nh&agrave; ph&acirc;n t&iacute;ch Jonny Moe cho biết.</p>\r\n\r\n<p>Dữ liệu từ Glassnode cho thấy c&oacute; khoảng 111 triệu USD vị thế short bị thanh l&yacute; chỉ trong 10 ph&uacute;t.</p>\r\n\r\n<p>Theo Datamish, BitMEX thanh l&yacute; 38 triệu USD BTC, c&ograve;n Bitfinex l&agrave; 255 BTC trong v&agrave;i giờ qua.</p>\r\n\r\n<p>Ethereum cũng đang tận hưởng đ&agrave; tăng kh&aacute; mạnh sau v&agrave;i tuần giảm gi&aacute;. Gi&aacute; ETH đạt đỉnh tạm thời tại 2.400 USD trước khi giảm trở lại 2.330 USD, theo CoinGecko. Đồng Ethereum đ&atilde; tăng 7% trong ng&agrave;y v&agrave; hơn 20% trong tuần qua.</p>\r\n\r\n<p>Thị trường tiền điện tử nh&igrave;n chung đều tăng trưởng theo Bitcoin. Tổng vốn h&oacute;a thị trường hiện đạt hơn mức 1.500 tỷ USD, tăng hơn 100 tỷ USD so với 24 giờ trước.</p>', '57_bitcoin3.jpg', '1', '07-08-2021', '2021-08-04 22:47:30', '2021-08-06 11:04:32'),
(11, 'Amazon dự kiến chấp nhận thanh toán Bitcoin trong năm nay', '335', '<p>Amazon hiện triển khai kế hoạch chấp nhận thanh to&aacute;n bằng Bitcoin cuối năm nay, một nguồn tin ẩn danh l&agrave;m việc trong c&ocirc;ng ty n&agrave;y mới đ&acirc;y cho biết.Amazon đ&atilde; sẵn s&agrave;ng gia nhập lĩnh vực thanh to&aacute;n tiền m&atilde; h&oacute;a. Sau Bitcoin, Amazon c&oacute; thể bổ sung Ethereum, Cardano v&agrave; Bitcoin Cash v&agrave;o hệ thống thanh to&aacute;n.</p>', '<p><strong>G&atilde; khổng lồ thương mại điện tử Amazon c&oacute; thể sẽ tham gia lĩnh vực thanh to&aacute;n tiền m&atilde; h&oacute;a trong năm 2021.&nbsp;</strong></p>\r\n\r\n<p>Amazon hiện triển khai kế hoạch chấp nhận thanh to&aacute;n bằng Bitcoin cuối năm nay, một nguồn tin ẩn danh l&agrave;m việc trong c&ocirc;ng ty n&agrave;y mới đ&acirc;y cho biết.</p>\r\n\r\n<p>Amazon đ&atilde; sẵn s&agrave;ng gia nhập lĩnh vực thanh to&aacute;n tiền m&atilde; h&oacute;a. Sau Bitcoin, Amazon c&oacute; thể bổ sung Ethereum, Cardano v&agrave; Bitcoin Cash v&agrave;o hệ thống thanh to&aacute;n, người n&agrave;y n&oacute;i.</p>\r\n\r\n<blockquote>\r\n<p><strong>Amazon sẽ kh&ocirc;ng dừng lại với Bitcoin. Kế hoạch chấp nhận thanh to&aacute;n bằng tiền m&atilde; h&oacute;a sẽ sớm diễn ra v&igrave; việc n&agrave;y được chuẩn bị kỹ lưỡng từ năm 2019</strong></p>\r\n</blockquote>\r\n\r\n<p>Ngo&agrave;i việc chấp nhận thanh to&aacute;n bằng Bitcoin, Amazon cũng được cho l&agrave; đang nghi&ecirc;n cứu tạo ra đồng tiền kỹ thuật số của ri&ecirc;ng m&igrave;nh. Kế hoạch n&agrave;y c&oacute; thể ra mắt v&agrave;o năm 2022.</p>\r\n\r\n<p>Nếu th&ocirc;ng tin tr&ecirc;n l&agrave; ch&iacute;nh x&aacute;c, đ&acirc;y c&oacute; thể l&agrave; cột mốc quan trọng trong việc th&uacute;c đẩy chấp nhận thanh to&aacute;n bằng tiền điện tử.</p>\r\n\r\n<p>Th&aacute;ng 2/2021, Amazon &acirc;m thầm x&acirc;y dựng một đội ngũ bao gồm c&aacute;c chuy&ecirc;n gia về blockchain để gi&uacute;p c&ocirc;ng ty ph&aacute;t triển nền tảng Digital and Emerging Payments (DEP).</p>\r\n\r\n<p>Đ&acirc;y l&agrave; dịch vụ gi&uacute;p người d&ugrave;ng chuyển đổi tiền ph&aacute;p định sang tiền điện tử để mua sắm trực tuyến. Hiện DEP chỉ được triển khai ở Mexico.</p>\r\n\r\n<p>Bộ phận thanh to&aacute;n của Amazon mới đ&acirc;y cũng đang r&aacute;o riết tuyển dụng một chuy&ecirc;n gia về tiền điện tử v&agrave; Blockchain, để phục vụ chiến lược sắp tới của c&ocirc;ng ty.</p>\r\n\r\n<p>Amazon tin rằng tương lai sẽ được x&acirc;y dựng dựa tr&ecirc;n c&aacute;c c&ocirc;ng nghệ mới như Blockchain, điều n&agrave;y sẽ mang đến c&aacute;c phương tiện thanh to&aacute;n hiện đại, nhanh ch&oacute;ng v&agrave; &iacute;t tốn k&eacute;m hơn.</p>\r\n\r\n<p>Hiện tại, Amazon c&oacute; một c&ocirc;ng ty con l&agrave; Amazon Web Services &ndash; chuy&ecirc;n cung cấp c&aacute;c nền tảng điện to&aacute;n đ&aacute;m m&acirc;y theo y&ecirc;u cầu cho c&aacute;c c&aacute; nh&acirc;n, c&ocirc;ng ty v&agrave; ch&iacute;nh phủ. Đ&acirc;y l&agrave; một loại dịch vụ được gọi l&agrave; &ldquo;managed blockchain&rdquo;.</p>\r\n\r\n<p>Tuy vậy, Amazon hiện kh&ocirc;ng chấp nhận bất kỳ loại tiền điện tử n&agrave;o l&agrave;m phương tiện thanh to&aacute;n cho c&aacute;c sản phẩm của Amazon Web Services.</p>', '2_bitcoin2.jpg', '1', '07-08-2021', '2021-08-04 22:49:19', '2021-08-06 11:02:36'),
(12, 'Liệu đà giảm của Bitcoin đã kết thúc?', '561', '<p>Sau khi Bitcoin bất ngờ tăng mạnh s&aacute;ng h&ocirc;m nay, nhiều người đang đặt ra c&acirc;u hỏi liệu Bitcoin đ&atilde; ho&agrave;n tất đợt điều chỉnh. Trong đ&oacute;, c&aacute;c nh&agrave; ph&acirc;n t&iacute;ch vẫn cẩn trọng xem x&eacute;t th&ecirc;m t&igrave;nh h&igrave;nh, tr&aacute;nh rơi v&agrave;o ho&agrave;n cảnh &ldquo;thấy hoa nở m&agrave; ngỡ xu&acirc;n về&rdquo;.</p>', '<p><strong>Nếu gi&aacute; duy tr&igrave; trong khu vực hiện tại, khả năng Bitcoin tiến l&ecirc;n khoảng từ 42.000 USD đến 43.000 USD sắp xảy ra.</strong></p>\r\n\r\n<p>Sau khi Bitcoin bất ngờ tăng mạnh s&aacute;ng h&ocirc;m nay, nhiều người đang đặt ra c&acirc;u hỏi liệu Bitcoin đ&atilde; ho&agrave;n tất đợt điều chỉnh. Trong đ&oacute;, c&aacute;c nh&agrave; ph&acirc;n t&iacute;ch vẫn cẩn trọng xem x&eacute;t th&ecirc;m t&igrave;nh h&igrave;nh, tr&aacute;nh rơi v&agrave;o ho&agrave;n cảnh &ldquo;thấy hoa nở m&agrave; ngỡ xu&acirc;n về&rdquo;.</p>\r\n\r\n<p>Theo đ&oacute;, BTC trải qua đ&agrave; tăng kh&aacute; mạnh s&aacute;ng nay, gi&aacute; tăng l&ecirc;n khu vực 40.000 USD, trước khi điều chỉnh nhẹ về v&ugrave;ng 38.400 USD.</p>\r\n\r\n<p><a href=\"https://blogtien.com/wp-content/uploads/2021/07/unnamed-file-182.jpg\"><img height=\"426\" sizes=\"(max-width: 862px) 100vw, 862px\" src=\"https://blogtien.com/wp-content/uploads/2021/07/unnamed-file-182.jpg\" srcset=\"https://blogtien.com/wp-content/uploads/2021/07/unnamed-file-182.jpg 862w, https://blogtien.com/wp-content/uploads/2021/07/unnamed-file-182-300x148.jpg 300w, https://blogtien.com/wp-content/uploads/2021/07/unnamed-file-182-768x380.jpg 768w\" width=\"862\" /></a></p>\r\n\r\n<p>Gi&aacute; BTC đang dao động trong khoảng MA 50 ng&agrave;y đến 200 ng&agrave;y như đ&atilde; thấy trong biểu đồ tr&ecirc;n.</p>\r\n\r\n<p>Hiện tại, rất kh&oacute; để x&aacute;c định đợt điều chỉnh k&eacute;o d&agrave;i 2 th&aacute;ng qua của Bitcoin đ&atilde; kết th&uacute;c hay chưa. Mọi thứ chỉ trở n&ecirc;n r&otilde; r&agrave;ng hơn cho đến khi c&aacute;c mức tr&ecirc;n MA 200 ng&agrave;y, tại 44.720 USD, trở th&agrave;nh mức hỗ trợ.</p>\r\n\r\n<p>Do đ&oacute;, khu vực xung quanh 44.720 USD cực kỳ quan trọng để giữ nguy&ecirc;n đ&agrave; tăng gi&aacute;.</p>\r\n\r\n<p>T&iacute;n hiệu t&iacute;ch cực l&agrave; Bitcoin lấy lại đường MA 50 ng&agrave;y th&agrave;nh c&ocirc;ng.&nbsp;Lần cuối c&ugrave;ng Bitcoin vượt l&ecirc;n tr&ecirc;n mức n&agrave;y l&agrave; ng&agrave;y 8/10/2020. Đ&acirc;y l&agrave; thời điểm gi&aacute; tăng từ 11.000 USD l&ecirc;n 65.000 USD. V&igrave; thế, Bitcoin c&oacute; khả năng sẽ lập đỉnh mới trong kỳ tăng gi&aacute; n&agrave;y.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, chỉ &nbsp;số RSI cũng ph&aacute; vỡ xu hướng giảm được thiết lập kể từ đầu năm để tăng trở lại.</p>\r\n\r\n<p>Một đợt tăng đ&aacute;ng kể tr&ecirc;n 45.000 USD được coi l&agrave; c&oacute; thể đẩy gi&aacute; BTC đến gần cột mốc 100.000 USD hơn bao giờ hết.</p>', '86_bitcoin1.jpg', '1', '07-08-2021', '2021-08-05 10:26:17', '2021-08-06 11:01:00');
INSERT INTO `posts` (`id`, `title`, `views`, `short_desc`, `desc`, `image`, `post_category_id`, `post_date`, `created_at`, `updated_at`) VALUES
(14, 'Crypto.com Chain (CRO) tăng 168% trong 1 ngày. Máy bơm hoạt động hay đồng coin tiềm năng?', '345', '<p>Crypto.com Chain (CRO) l&agrave; một dự &aacute;n blockchain cho ph&eacute;p giao dịch tiền điện tử ngay lập tức. Hiện nay, token CRO đang tăng vọt tới 168%. Sự b&ugrave;ng nổ đi&ecirc;n rồ đ&atilde; đẩy mức vốn h&oacute;a thị trường của dự &aacute;n từ mức khi&ecirc;m tốn 72-75 triệu USD l&ecirc;n tr&ecirc;n 200 triệu USD. T&iacute;nh đến 8:30 s&aacute;ng h&ocirc;m nay, CRO được giao dịch ở mức 0.055 USD.</p>', '<p><strong>Trong khi thị trường ch&igrave;m trong kh&ocirc;ng kh&iacute; ảm đạm th&igrave; CRO nổi l&ecirc;n như một v&igrave; sao s&aacute;ng khi cộng đồng chứng kiến mức tăng 168% của n&oacute;. Nhưng liệu đ&acirc;y c&oacute; phải l&agrave; một coin tiềm năng hay đơn thuần chỉ l&agrave; một đợt pump-dump?</strong></p>\r\n\r\n<ul>\r\n	<li><strong><a href=\"https://blogtien.com/5-sai-lam-tam-ly-thuong-gap-o-trader-moi/\" rel=\"noopener noreferrer\" target=\"_blank\">5 sai lầm t&acirc;m l&yacute; thường gặp ở Trader mới</a></strong></li>\r\n	<li><strong><a href=\"https://blogtien.com/top-10-tien-dien-tu-co-loi-nhuan-cao-nhat-moi-thoi-dai/\" rel=\"noopener noreferrer\" target=\"_blank\">Top 10 tiền điện tử c&oacute; lợi nhuận cao nhất mọi thời đại.</a></strong></li>\r\n	<li><strong><a href=\"https://blogtien.com/5-ly-do-khien-cac-crypto-trader-dau-tu-chua-hieu-qua/\" rel=\"noopener noreferrer\" target=\"_blank\">5 l&yacute; do khiến c&aacute;c crypto trader đầu tư chưa hiệu quả</a></strong></li>\r\n</ul>\r\n\r\n<p>Crypto.com Chain (CRO) l&agrave; một dự &aacute;n blockchain cho ph&eacute;p giao dịch tiền điện tử ngay lập tức. Hiện nay, token CRO đang tăng vọt tới 168%. Sự b&ugrave;ng nổ đi&ecirc;n rồ đ&atilde; đẩy mức vốn h&oacute;a thị trường của dự &aacute;n từ mức khi&ecirc;m tốn 72-75 triệu USD l&ecirc;n tr&ecirc;n 200 triệu USD. T&iacute;nh đến 8:30 s&aacute;ng h&ocirc;m nay, CRO được giao dịch ở mức 0.055 USD.</p>\r\n\r\n<p><img height=\"480\" sizes=\"(max-width: 754px) 100vw, 754px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/23-2.jpg\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/23-2.jpg 754w, https://blogtien.com/wp-content/uploads/2019/03/23-2-300x191.jpg 300w\" width=\"754\" /></p>\r\n\r\n<p>Biểu đồ 7 ng&agrave;y</p>\r\n\r\n<p>Khối lượng giao dịch của CRO nằm trong khoảng 682 triệu USD, cặp giao dịch nhiều nhất l&agrave; với Bitcoin, ETH v&agrave; đồng won H&agrave;n Quốc theo khung thời gian 24 giờ. Đa số c&aacute;c giao dịch của CRO được diễn ra tr&ecirc;n s&agrave;n Bittrex. Được biết mặc d&ugrave; s&agrave;n n&agrave;y vẫn chưa ho&agrave;n to&agrave;n đầy đủ giấy tờ theo quy định, nhưng n&oacute; đ&atilde; tuy&ecirc;n bố rằng n&oacute; vẫn tu&acirc;n thủ luật ph&aacute;p Hoa Kỳ hiện h&agrave;nh. Bittrex cũng c&oacute; sự hiện diện ở Malta, một khu vực được điều tiết lỏng lẻo, thi&ecirc;n đường d&agrave;nh cho c&aacute;c c&ocirc;ng ty tiền điện tử. C&oacute; khả năng kh&aacute;ch h&agrave;ng ở Hoa Kỳ của Bittrex kh&ocirc;ng đ&oacute;ng g&oacute;p nhiều cho cuộc biểu t&igrave;nh đ&aacute;ng ngờ của CRO.</p>\r\n\r\n<p>Một s&agrave;n giao dịch kh&aacute;c diễn ra gần 47% tổng giao dịch CRO hiện nay l&agrave; GOPAX, một s&agrave;n giao dịch tại H&agrave;n Quốc v&agrave; l&agrave; một trong số &iacute;t đ&atilde; vượt qua b&agrave;i kiểm tra tự điều chỉnh, được thực hiện theo hướng dẫn của Ủy ban Dịch vụ T&agrave;i ch&iacute;nh H&agrave;n Quốc (FSC) năm ngo&aacute;i.</p>\r\n\r\n<h2><strong>Danh s&aacute;ch s&agrave;n giao dịch v&agrave; quan hệ đối t&aacute;c</strong></h2>\r\n\r\n<p>Crypto.com gần đ&acirc;y đ&atilde; c&oacute; một cuộc thảo luận về danh s&aacute;ch c&aacute;c s&agrave;n giao dịch. Sau Bittrex, dự &aacute;n&nbsp;đ&atilde; th&ocirc;ng b&aacute;o rằng Indodax sẽ bắt đầu cung cấp c&aacute;c cặp giao dịch hỗ trợ CRO tr&ecirc;n nền tảng của n&oacute; từ ng&agrave;y 14 th&aacute;ng 3. Trong c&ugrave;ng một th&ocirc;ng b&aacute;o, Crypto.com Chain cho biết họ đang hợp t&aacute;c với một c&ocirc;ng ty thương mại điện tử c&oacute; trụ sở tại Indonesia &ndash; Yoshugi Media Group. Quan hệ đối t&aacute;c ch&uacute; trọng đến việc &aacute;p dụng cục bộ cho CRO khi token n&agrave;y sẽ thay thế c&aacute;c t&ugrave;y chọn thanh to&aacute;n tốn k&eacute;m bằng dịch vụ thanh to&aacute;n hỗ trợ blockchain.</p>\r\n\r\n<p>Kris Marszalek &ndash; đồng s&aacute;ng lập v&agrave; đồng thời l&agrave; CEO của Crypto.com cho biết:</p>\r\n\r\n<blockquote>\r\n<p>Indonesia l&agrave; một thị trường s&ocirc;i động với hơn 260 triệu người. Ch&uacute;ng t&ocirc;i mong muốn ph&aacute;t triển mối quan hệ chặt chẽ hơn với cả Indodax v&agrave; Yoshugi Media Group khi ch&uacute;ng t&ocirc;i t&igrave;m c&aacute;ch phục vụ kh&aacute;ch h&agrave;ng của m&igrave;nh trong thị trường ASEAN đang ph&aacute;t triển nhanh ch&oacute;ng. Ch&uacute;ng t&ocirc;i thấy Crypto.com Pay đang th&uacute;c đẩy việc chấp nhận tiền điện tử như một phương thức thanh to&aacute;n v&agrave; token CRO l&agrave; c&aacute;ch để giải quyết thanh to&aacute;n bằng tiền tệ ngay lập tức v&agrave; kh&ocirc;ng tốn k&eacute;m.</p>\r\n</blockquote>\r\n\r\n<h2><strong>Certik x&aacute;c minh</strong></h2>\r\n\r\n<p>C&ocirc;ng ty kiểm to&aacute;n hợp đồng th&ocirc;ng minh Certik đ&atilde; cung cấp một đ&aacute;nh gi&aacute; thuận lợi cho CRO của Crypto.com. C&ocirc;ng ty cũng n&oacute;i th&ecirc;m rằng c&aacute;c chứng nhận đối t&aacute;c sẽ gi&uacute;p CRO dễ d&agrave;ng được ni&ecirc;m yết tr&ecirc;n c&aacute;c s&agrave;n giao dịch lớn, bao gồm c&aacute;c t&ecirc;n tuổi như Binance v&agrave; Waves. Đ&acirc;y l&agrave; danh s&aacute;ch đối t&aacute;c đầy đủ:</p>\r\n\r\n<p><img height=\"476\" sizes=\"(max-width: 1134px) 100vw, 1134px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/25-2.jpg\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/25-2.jpg 1134w, https://blogtien.com/wp-content/uploads/2019/03/25-2-300x126.jpg 300w, https://blogtien.com/wp-content/uploads/2019/03/25-2-1024x430.jpg 1024w, https://blogtien.com/wp-content/uploads/2019/03/25-2-768x322.jpg 768w\" width=\"1134\" /></p>\r\n\r\n<h2><strong>H&agrave;ng ch&iacute;nh h&atilde;ng hay h&agrave;ng giả?</strong></h2>\r\n\r\n<p>Từ những bằng chứng tr&ecirc;n, CRO đ&atilde; c&oacute; đủ mọi l&yacute; do để trỗi dậy khi c&oacute; những ph&aacute;t triển t&iacute;ch cực v&agrave;o ng&agrave;y h&ocirc;m nay. Trong khi đ&agrave; tăng vẫn đang diễn ra cực kỳ mạnh mẽ, c&oacute; khả năng CRO sẽ t&igrave;m thấy sự hỗ trợ tốt trong một kịch bản ngắn hạn.</p>', '45_Altcoin2.jpg', '3', '07-08-2021', '2021-08-06 11:23:01', '2021-08-06 11:23:01'),
(15, 'Litecoin có khả năng hất cẳng XRP trên CoinMarketCap vào năm nay.', '129', '<p>Trong khi c&aacute;c đồng coin phải chật vật trước một m&ugrave;a đ&ocirc;ng tiền điện tử khắc nghiệt th&igrave; Litecoin lại tăng gần gấp đ&ocirc;i gi&aacute; trị kể từ đầu năm. Litecoin hiện đang giao dịch ở mức 55 USD, tăng hơn gấp đ&ocirc;i so với mức thấp gần đ&acirc;y nhất l&agrave; v&agrave;o th&aacute;ng 12 năm 2018; khi đ&oacute;, một coin trị gi&aacute; 23 USD. C&ugrave;ng với Binance Coin (BNB), LTC đ&atilde; g&oacute;p mặt v&agrave;o top coin tăng gi&aacute; kể từ đầu năm 2019.</p>', '<p><strong>Một b&agrave;i viết được đăng tr&ecirc;n Forbes đ&atilde; đưa ra tuy&ecirc;n bố&nbsp;mạnh mẽ&nbsp;rằng Litecoin c&oacute; khả năng hất cẳng XRP tr&ecirc;n CoinMarketCap v&agrave;o cuối năm nay.</strong></p>\r\n\r\n<ul>\r\n	<li><strong><a href=\"https://blogtien.com/phan-tich-gia-bitcoin-11-03-bitcoin-co-le-van-chua-the-pha-vo-moc-4-000-usd/\" rel=\"noopener noreferrer\" target=\"_blank\">Ph&acirc;n t&iacute;ch gi&aacute; Bitcoin 11/03: Bitcoin c&oacute; lẽ vẫn chưa thể ph&aacute; vỡ mốc 4.000 USD.</a></strong></li>\r\n	<li><strong><a href=\"https://blogtien.com/binance-thong-bao-bao-tri-de-nang-cap-he-thong/\" rel=\"noopener noreferrer\" target=\"_blank\">Binance th&ocirc;ng b&aacute;o bảo tr&igrave; để n&acirc;ng cấp hệ thống</a></strong></li>\r\n	<li><strong><a href=\"https://blogtien.com/voi-khoi-luong-dat-muc-cao-nhat-ke-tu-nam-2017-cong-dong-dang-cho-doi-bitcoin-bung-no/\" rel=\"noopener noreferrer\" target=\"_blank\">Với khối lượng đạt mức cao nhất kể từ năm 2017, cộng đồng đang chờ đợi Bitcoin b&ugrave;ng nổ.</a></strong></li>\r\n</ul>\r\n\r\n<p>Trong khi c&aacute;c đồng coin phải chật vật trước một m&ugrave;a đ&ocirc;ng tiền điện tử khắc nghiệt th&igrave; Litecoin lại tăng gần gấp đ&ocirc;i gi&aacute; trị kể từ đầu năm. Litecoin hiện đang giao dịch ở mức 55 USD, tăng hơn gấp đ&ocirc;i so với mức thấp gần đ&acirc;y nhất l&agrave; v&agrave;o th&aacute;ng 12 năm 2018; khi đ&oacute;, một coin trị gi&aacute; 23 USD. C&ugrave;ng với Binance Coin (BNB), LTC đ&atilde; g&oacute;p mặt v&agrave;o top coin tăng gi&aacute; kể từ đầu năm 2019.</p>\r\n\r\n<p>Bambrough, trong b&agrave;i viết của m&igrave;nh được xuất bản tr&ecirc;n Forbes, tin rằng gi&aacute; LTC đang đ&aacute;p ứng những nỗ lực mạnh mẽ của Litecoin Foundation v&agrave; c&aacute;c nh&agrave; ph&aacute;t triển để cải thiện t&iacute;nh bảo mật v&agrave; quyền ri&ecirc;ng tư cho đồng tiền, ngo&agrave;i việc loại bỏ một số mối quan hệ đối t&aacute;c cao cấp.</p>\r\n\r\n<blockquote>\r\n<p>Sự biến động của Litecoin tăng vọt khi Litecoin Foundation c&ocirc;ng bố họ đang xem x&eacute;t việc t&iacute;ch hợp của Mimblewimble &ndash; một giao thức cung cấp quyền ri&ecirc;ng tư v&agrave; fungibility (t&iacute;nh linh hoạt) cho blockchains &ndash; tr&iacute;ch từ bản b&aacute;o c&aacute;o của c&ocirc;ng ty m&ocirc;i giới tiền điện tử SFOX trong tuần n&agrave;y.</p>\r\n\r\n<p>V&agrave;o th&aacute;ng 3, h&atilde;y theo d&otilde;i những khoảnh khắc tin tức ri&ecirc;ng tư t&aacute;c động đến thị trường, chẳng hạn như những tiết lộ gần đ&acirc;y về nh&agrave; cung cấp dịch vụ b&ecirc;n thứ ba của Coinbase đ&atilde; b&aacute;n dữ liệu kh&aacute;ch h&agrave;ng.</p>\r\n</blockquote>\r\n\r\n<p>Bambrough cũng nhấn mạnh đến sự kiện halving của LTC sẽ diễn ra v&agrave;o th&aacute;ng 8 năm nay, sự kiện n&agrave;y sẽ l&agrave;m giảm phần thưởng khối cho c&aacute;c thợ mỏ, do đ&oacute; sẽ l&agrave;m tăng sự khan hiếm của đồng coin. Halving thường tạo ra một đợt tăng gi&aacute; (kh&aacute; mạnh) do nguồn cung thay đổi đột ngột. Nhưng c&oacute; lẽ sẽ rất th&uacute; vị nếu ch&uacute;ng ta được chứng kiến những t&aacute;c động như vậy khi thị trường vẫn c&ograve;n kẹt trong m&ugrave;a đ&ocirc;ng tiền điện tử v&agrave;o năm ngo&aacute;i.</p>\r\n\r\n<p>Đối với XRP, Bambrough tr&iacute;ch dẫn một số tin tức ti&ecirc;u cực của đồng coin v&agrave; c&ocirc;ng ty li&ecirc;n kết&nbsp; &ndash; Ripple. Trong khi c&ocirc;ng ty n&agrave;y l&agrave; trung t&acirc;m của một số c&acirc;u chuyện về việc &aacute;p dụng tr&ecirc;n quy m&ocirc; lớn đối với c&aacute;c tổ chức t&agrave;i ch&iacute;nh, Bambrough tin rằng th&ocirc;ng b&aacute;o ra mắt JPM Coin của JP Morgan đ&atilde; thu h&uacute;t sự quan t&acirc;m của giới đầu tư v&agrave;o Ripple v&agrave; XRP.</p>\r\n\r\n<p>Theo b&aacute;o c&aacute;o của EWN, Binance Research đ&atilde;&nbsp;b&aacute;c bỏ&nbsp;quan điểm JPM Coin sẽ cạnh tranh trực tiếp với Ripple hoặc XRP,. Tuy nhi&ecirc;n, Bambrough tuy&ecirc;n bố sự thay đổi của tiền điện tử, với t&acirc;m l&yacute; t&iacute;ch cực của cộng đồng đối với LTC c&oacute; thể đủ để Litecoin hất cẳng XRP v&agrave;o cuối năm nay. Nhưng nếu nh&igrave;n về kh&iacute;a cạnh vốn h&oacute;a hiện tại th&igrave; sự ch&ecirc;nh lệch giữa XRP với LTC l&ecirc;n đến 9 tỷ USD, một khoảng c&aacute;ch kh&aacute; lớn để LTC vượt mặt XRP.&nbsp;</p>\r\n\r\n<p>Tuy nhi&ecirc;n, Bambrough t&igrave;m thấy l&yacute; do để mức vốn h&oacute;a của LTC tăng mạnh v&agrave; việc sử dụng &ndash; &aacute;p dụng trong thế giới thực của n&oacute; tăng l&ecirc;n; tr&iacute;ch dẫn từ một lưu &yacute; d&agrave;nh cho kh&aacute;ch h&agrave;ng của nh&agrave; ph&acirc;n t&iacute;ch thị trường cao cấp eToro, Mati Greenspan được c&ocirc;ng bố hồi đầu tuần:</p>\r\n\r\n<p>C&aacute;ch sử dụng đầu ti&ecirc;n v&agrave; tr&ecirc;n hết của Litecoin trong thế giới thực ch&iacute;nh l&agrave; một phương thức thanh to&aacute;n cho c&aacute;c trader. Đối với những người thường gửi tiền từ s&agrave;n giao dịch để s&agrave;n chuyển sang v&iacute; ri&ecirc;ng, việc thực hiện điều n&agrave;y với Litecoin sẽ rẻ hơn v&agrave; nhanh hơn nhiều so với Bitcoin.</p>\r\n\r\n<p>Đ&acirc;y l&agrave; l&yacute; do tại sao n&oacute; thực sự xứng đ&aacute;ng với danh hiệu bạc kỹ thuật số v&agrave; với sự kiện halving sắp diễn ra v&agrave;o đầu th&aacute;ng 8, rất c&oacute; thể đ&acirc;y l&agrave; c&acirc;u trả lời cho c&acirc;u hỏi tại sao n&oacute; lại vượt trội hơn so với phần c&ograve;n lại của thị trường tiền điện tử trong thời gian gần đ&acirc;y.</p>', '99_Altcoin3.jpg', '3', '07-08-2021', '2021-08-06 11:24:49', '2021-08-06 11:24:49'),
(16, 'Tìm hiểu về xRapid – Quân cờ quan trọng của Ripple', '125', '<p>Điều đ&aacute;ng ngạc nhi&ecirc;n đối với hầu hết những người đam m&ecirc; tiền m&atilde; h&oacute;a l&agrave; kh&ocirc;ng c&oacute; bất kỳ ng&acirc;n h&agrave;ng n&agrave;o sử dụng XRP (token gốc của Ripple). Trong số tất cả c&aacute;c mối quan hệ đối t&aacute;c n&agrave;y, hầu hết trong số họ vẫn tận dụng lợi thế của dịch vụ dựa tr&ecirc;n Ripple hiện tại c&oacute; t&ecirc;n l&agrave; xCurrent.</p>', '<p><strong>Ripple đ&atilde; bắt đầu thu h&uacute;t được nhiều sự quan t&acirc;m từ c&aacute;c nh&agrave; đầu tư cũng như những người đam m&ecirc; tiền m&atilde; h&oacute;a trong v&agrave;i th&aacute;ng qua. Kể từ khi ra mắt v&agrave;o năm 2017, Ripple đ&atilde; li&ecirc;n tục thiết lập nhiều quan hệ đối t&aacute;c với c&aacute;c ng&acirc;n h&agrave;ng lớn nhất thế giới.</strong></p>\r\n\r\n<ul>\r\n	<li><strong><a href=\"https://blogtien.com/nhung-dieu-toi-uoc-duoc-biet-truoc-khi-tham-gia-giao-dich-crypto/\" rel=\"noopener noreferrer\" target=\"_blank\">Những điều t&ocirc;i ước được biết trước khi tham gia giao dịch Crypto</a></strong></li>\r\n	<li><strong><a href=\"https://blogtien.com/6-bay-tam-ly-ban-can-tranh-khi-dau-tu/\" rel=\"noopener noreferrer\" target=\"_blank\">6 bẫy t&acirc;m l&yacute; bạn cần tr&aacute;nh khi đầu tư.</a></strong></li>\r\n	<li><strong><a href=\"https://blogtien.com/8-quan-niem-sai-lam-trong-trade-coin/\" rel=\"noopener noreferrer\" target=\"_blank\">8 quan niệm sai lầm trong trade coin</a></strong></li>\r\n</ul>\r\n\r\n<p>Hiện tại,&nbsp;<a href=\"https://blogtien.com/ripple-la-gi/\" rel=\"noopener noreferrer\" target=\"_blank\">Ripple</a>&nbsp;đ&atilde; hợp t&aacute;c với 120 ng&acirc;n h&agrave;ng. Danh s&aacute;ch n&agrave;y bao gồm c&aacute;c t&ecirc;n tuổi lớn như Santander, American Express, Westpac, Seb, UniCredit, transferGo, Yes Bank, BBVA, CIBC v&agrave; Western Union&hellip;</p>\r\n\r\n<p><img height=\"148\" sizes=\"(max-width: 652px) 100vw, 652px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/banks-using-ripple.jpg\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/banks-using-ripple.jpg 652w, https://blogtien.com/wp-content/uploads/2019/03/banks-using-ripple-300x68.jpg 300w\" width=\"652\" /></p>\r\n\r\n<p>Điều đ&aacute;ng ngạc nhi&ecirc;n đối với hầu hết những người đam m&ecirc; tiền m&atilde; h&oacute;a l&agrave; kh&ocirc;ng c&oacute; bất kỳ ng&acirc;n h&agrave;ng n&agrave;o sử dụng XRP (token gốc của Ripple). Trong số tất cả c&aacute;c mối quan hệ đối t&aacute;c n&agrave;y, hầu hết trong số họ vẫn tận dụng lợi thế của dịch vụ dựa tr&ecirc;n Ripple hiện tại c&oacute; t&ecirc;n l&agrave; xCurrent.</p>\r\n\r\n<p>xCurrent l&agrave; sản phẩm gi&uacute;p việc giao tiếp giữa người d&ugrave;ng v&agrave; ng&acirc;n h&agrave;ng cũng như c&aacute;c dịch vụ thanh to&aacute;n hiệu quả hơn rất nhiều. Nhiệm vụ của Ripple lu&ocirc;n l&agrave; cải thiện tốc độ tổng thể của c&aacute;c khoản thanh to&aacute;n v&agrave; giao dịch xuy&ecirc;n bi&ecirc;n giới. Với việc c&ocirc;ng bố c&ocirc;ng nghệ mới nhất của Ripple, xRapid, c&ocirc;ng ty hy vọng sẽ l&agrave;m cho c&ocirc;ng nghệ blockchain trở n&ecirc;n đ&aacute;ng tin cậy hơn v&agrave; gi&uacute;p cho giao dịch xuy&ecirc;n bi&ecirc;n giới trở n&ecirc;n nhanh hơn.</p>\r\n\r\n<p>Gửi tiền ra nước ngo&agrave;i qua ng&acirc;n h&agrave;ng thường c&oacute; thể mất v&agrave;i ng&agrave;y v&agrave; tốn nhiều lệ ph&iacute;. xRapid được ph&aacute;t triển như l&agrave; giai đoạn thứ hai của việc mở rộng Ripple, v&agrave; n&oacute; nhằm mục đ&iacute;ch l&agrave;m cho XRP trở th&agrave;nh token cho tất cả c&aacute;c đối t&aacute;c ng&acirc;n h&agrave;ng của Ripple.</p>\r\n\r\n<h2>xRapid l&agrave; g&igrave;?</h2>\r\n\r\n<p>xRapid l&agrave; sản phẩm mới nhất của Ripple, v&agrave; n&oacute; đại diện cho một bước tiến lớn trong sự ph&aacute;t triển của c&ocirc;ng ty. Mục ti&ecirc;u ch&iacute;nh của xRapid l&agrave; cải thiện hệ thống xCurrent bằng c&aacute;ch thực hiện c&aacute;c giao dịch rẻ hơn v&agrave; nhanh hơn với token XRP l&agrave;m trụ cột trung t&acirc;m để giải quyết vấn đề chuyển tiền.</p>\r\n\r\n<p>Để sắp xếp hợp l&yacute; quy tr&igrave;nh n&oacute;i tr&ecirc;n, Ripple dự định sử dụng XRP như một loại tiền tệ bắc cầu. Để giải th&iacute;ch ch&iacute;nh x&aacute;c c&aacute;ch xRapid hoạt động, ch&uacute;ng ta sẽ sử dụng một v&iacute; dụ thực tế. Giả sử một người từ bất kỳ quốc gia Ch&acirc;u &Acirc;u n&agrave;o muốn gửi tiền cho người kh&aacute;c ở ch&acirc;u &Aacute;. Th&ocirc;ng qua hệ thống ng&acirc;n h&agrave;ng truyền thống, thời gian v&agrave; chi ph&iacute; chuyển tiền sẽ rất lớn.</p>\r\n\r\n<p>Với xRapid, việc chuyển tiền n&agrave;y gần như tức thời v&agrave; với chi ph&iacute; gần như bằng kh&ocirc;ng. Người đầu ti&ecirc;n chuyển tiền bằng c&aacute;ch chuyển đổi n&oacute; sang XRP sau đ&oacute; n&oacute; sẽ được tự động chuyển đổi sang loại tiền tệ m&agrave; người nhận ở quốc gia ch&acirc;u &Aacute; y&ecirc;u cầu.</p>\r\n\r\n<p><img height=\"563\" sizes=\"(max-width: 1200px) 100vw, 1200px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/Ripple_Price_XRP_USD_Analysis-11.jpg\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/Ripple_Price_XRP_USD_Analysis-11.jpg 1200w, https://blogtien.com/wp-content/uploads/2019/03/Ripple_Price_XRP_USD_Analysis-11-300x141.jpg 300w, https://blogtien.com/wp-content/uploads/2019/03/Ripple_Price_XRP_USD_Analysis-11-1024x480.jpg 1024w, https://blogtien.com/wp-content/uploads/2019/03/Ripple_Price_XRP_USD_Analysis-11-768x360.jpg 768w\" width=\"1200\" /></p>\r\n\r\n<h2>xRapid liệu c&oacute; đại diện cho tương lai kh&ocirc;ng bi&ecirc;n giới?</h2>\r\n\r\n<p>Đối với một người gửi tiền th&ocirc;ng qua một hệ thống ng&acirc;n h&agrave;ng th&ocirc;ng thường, từ nước n&agrave;y sang nước kh&aacute;c th&igrave; sẽ cần c&oacute; một t&agrave;i khoản nostro (T&agrave;i khoản của ng&acirc;n h&agrave;ng A mở tại ng&acirc;n h&agrave;ng B kh&aacute;c). C&aacute;c t&agrave;i khoản nostro n&agrave;y được t&agrave;i trợ trước với h&agrave;ng triệu đồng nội tệ, để cải thiện t&iacute;nh thanh khoản. Thanh khoản l&agrave; một trong những kh&iacute;a cạnh quan trọng nhất của bất kỳ s&agrave;n giao dịch ngoại hối th&agrave;nh c&ocirc;ng n&agrave;o. C&aacute;c ng&acirc;n h&agrave;ng cũng cần một mức độ thanh khoản tốt để hoạt động c&oacute; hiệu quả.</p>\r\n\r\n<p>Th&ocirc;ng qua phương thức chuyển tiền th&ocirc;ng thường, số tiền gửi từ người thứ nhất ở ch&acirc;u &Acirc;u (v&iacute; dụ tr&ecirc;n) sau đ&oacute; được trao đổi từ một loại tiền tệ n&agrave;y sang một loại tiền tệ mới th&ocirc;ng qua c&aacute;c t&agrave;i khoản nostro được t&agrave;i trợ trước đ&oacute;. C&aacute;c ng&acirc;n h&agrave;ng thường c&oacute; t&agrave;i khoản nostro địa phương cho bất kỳ loại tiền tệ n&agrave;o để tạo điều kiện thuận lợi cho c&aacute;c khoản thanh to&aacute;n qua bi&ecirc;n giới. Như đ&atilde; đề cập trước đ&acirc;y, c&aacute;c loại t&agrave;i khoản nostro n&agrave;y rất k&eacute;m hiệu quả.</p>\r\n\r\n<p>Ripple hướng tới việc thuyết phục c&aacute;c ng&acirc;n h&agrave;ng thay đổi hoặc thay thế c&aacute;c t&agrave;i khoản nostro n&agrave;y bằng tiền m&atilde; h&oacute;a, cụ thể l&agrave; với token XRP. Điều n&agrave;y sẽ loại bỏ sự cần thiết cho c&aacute;c t&agrave;i khoản ng&acirc;n h&agrave;ng cụ thể cho từng loại ngoại tệ, do đ&oacute; l&agrave;m cho hệ thống kh&ocirc;ng chỉ an to&agrave;n hơn m&agrave; c&ograve;n hiệu quả hơn rất nhiều.</p>\r\n\r\n<p><img height=\"381\" sizes=\"(max-width: 800px) 100vw, 800px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/Ripple-xRapid-platform-800x421.jpg\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/Ripple-xRapid-platform-800x421.jpg 800w, https://blogtien.com/wp-content/uploads/2019/03/Ripple-xRapid-platform-800x421-300x143.jpg 300w, https://blogtien.com/wp-content/uploads/2019/03/Ripple-xRapid-platform-800x421-768x366.jpg 768w\" width=\"800\" /></p>\r\n\r\n<p>C&ocirc;ng nghệ xRapid của XRP</p>\r\n\r\n<h2>Liệu c&oacute; dễ?</h2>\r\n\r\n<p>C&acirc;u trả lời đơn giản th&igrave; hầu như l&agrave; kh&ocirc;ng, kh&ocirc;ng hề dễ ch&uacute;t n&agrave;o. Hiện tại, Ripple đang nỗ lực thuyết phục c&aacute;c đối t&aacute;c của m&igrave;nh thử nghiệm c&ocirc;ng nghệ mới n&agrave;y, v&igrave; hầu hết trong số họ vẫn đang sử dụng xCurrent. Theo nhiều b&aacute;o c&aacute;o, nhiều đối t&aacute;c đ&atilde; bắt đầu thử nghiệm sản phẩm mới v&agrave; c&aacute;c nghi&ecirc;n cứu ban đầu cho thấy rằng họ đ&atilde; b&aacute;o c&aacute;o tiết kiệm khoảng 40-70%.</p>\r\n\r\n<p>C&ocirc;ng ty dự định mở rộng phạm vi tiếp cận của m&igrave;nh v&agrave; xRapid sẽ tham gia v&agrave;o c&aacute;c hệ thống ng&acirc;n h&agrave;ng chủ đạo v&agrave;o cuối năm 2019. Tất nhi&ecirc;n, n&oacute;i dễ hơn l&agrave;m, chủ yếu v&igrave; kh&ocirc;ng phải tất cả c&aacute;c thể chế ng&acirc;n h&agrave;ng đều hướng tới sử dụng c&ocirc;ng nghệ blockchain. Nếu chiến lược n&agrave;y th&agrave;nh c&ocirc;ng, đ&acirc;y sẽ l&agrave; một điều tuyệt vời cho Ripple đơn giản bởi v&igrave; token XRP sẽ được sử dụng nhiều hơn, khối lượng giao dịch h&agrave;ng ng&agrave;y của XRP cũng rất c&oacute; thể sẽ tăng l&ecirc;n đ&aacute;ng kinh ngạc. Điều n&agrave;y chắc chắn sẽ l&agrave;m cho Ripple trở th&agrave;nh một c&ocirc;ng ty &ldquo;c&oacute; m&aacute;u mặt&rdquo; hơn trong lĩnh vực tiền m&atilde; h&oacute;a m&atilde; h&oacute;a đầy sự cạnh tranh v&agrave; th&uacute; vị.</p>\r\n\r\n<p><img height=\"505\" sizes=\"(max-width: 1200px) 100vw, 1200px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/ripple-1-1.jpg\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/ripple-1-1.jpg 1200w, https://blogtien.com/wp-content/uploads/2019/03/ripple-1-1-300x126.jpg 300w, https://blogtien.com/wp-content/uploads/2019/03/ripple-1-1-1024x431.jpg 1024w, https://blogtien.com/wp-content/uploads/2019/03/ripple-1-1-768x323.jpg 768w\" width=\"1200\" /></p>\r\n\r\n<h2>Kết luận</h2>\r\n\r\n<p>xRapid l&agrave; một bước tiến lớn đối với Ripple, cũng như đối với tương lai của c&aacute;c giao dịch xuy&ecirc;n bi&ecirc;n giới. Hệ thống n&agrave;y sử dụng token XRP để cung cấp t&iacute;nh thanh khoản theo y&ecirc;u cầu, giảm thiểu chi ph&iacute; v&agrave; cải thiện thời gian giao dịch.</p>\r\n\r\n<p>xRapid c&oacute; khả năng ho&agrave;n to&agrave;n giải quyết vấn đề của c&aacute;c khoản thanh to&aacute;n v&agrave;o c&aacute;c thị trường mới nổi, những nơi đ&ograve;i hỏi c&aacute;c t&agrave;i khoản tiền tệ địa phương được t&agrave;i trợ trước. Điều n&agrave;y ho&agrave;n to&agrave;n c&oacute; thể trở th&agrave;nh sự thật miễn l&agrave; cc đối t&aacute;c ng&acirc;n h&agrave;ng của Ripple sẵn s&agrave;ng cho n&oacute; một cơ hội. Cho đến nay, c&oacute; vẻ như mọi thứ đang diễn ra theo kế hoạch v&agrave; Ripple kh&aacute; lạc quan về việc mở rộng dịch vụ của m&igrave;nh trong tương lai.</p>', '17_Altcoin4.jpg', '3', '07-08-2021', '2021-08-06 11:26:45', '2021-08-06 11:26:45'),
(17, 'Blockchain có thể thay thế cho hàng triệu tấm sổ đỏ?', '459', '<p>H&atilde;ng tư vấn dịch vụ bất động sản Savills vừa đưa ra nhận định cho rằng, c&aacute;c nh&agrave; đầu tư bất động sản d&ugrave; ở bất cứ ph&acirc;n kh&uacute;c n&agrave;o đều d&egrave; chừng trước vấn đề giao dịch giả v&agrave; những tranh chấp về quyền sở hữu bất động sản. Theo đ&oacute;, chứng nhận sở hữu đất lu&ocirc;n l&agrave; một vấn đề nhức nhối với c&aacute;c nh&agrave; đầu tư tại c&aacute;c thị trường ph&aacute;t triển như ch&acirc;u &Aacute;.</p>', '<h2><strong>Chứng nhận sở hữu đất lu&ocirc;n l&agrave; một vấn đề nhức nhối với c&aacute;c nh&agrave; đầu tư tại c&aacute;c thị trường ph&aacute;t triển như ch&acirc;u &Aacute;. Tuy nhi&ecirc;n, theo Savills, c&ocirc;ng nghệ blockchain c&oacute; thể l&agrave; giải ph&aacute;p cho những điều n&agrave;y.</strong></h2>\r\n\r\n<ul>\r\n	<li><strong><a href=\"https://blogtien.com/lieu-bitcoin-da-hoan-toan-thoat-khoi-downtrend/\" rel=\"noopener\" target=\"_blank\">Liệu Bitcoin đ&atilde; ho&agrave;n to&agrave;n tho&aacute;t khỏi downtrend ?</a></strong></li>\r\n	<li><strong><a href=\"https://blogtien.com/gia-bitcoin-hom-nay-20-2-thong-tin-lon-gi-dang-anh-huong-den-gia-bitcoin-va-thi-truong/\" rel=\"noopener\" target=\"_blank\">Gi&aacute; bitcoin h&ocirc;m nay (20/2): Th&ocirc;ng tin lớn g&igrave; đang ảnh hưởng đến gi&aacute; bitcoin v&agrave; thị trường?</a></strong></li>\r\n	<li><strong><a href=\"https://blogtien.com/mot-satoshi-nakamoto-khac-xuat-hien-tren-twitter-tuyen-bo-craig-wright-chi-la-hang-fake/\" rel=\"noopener\" target=\"_blank\">Một Satoshi Nakamoto kh&aacute;c xuất hiện tr&ecirc;n Twitter, tuy&ecirc;n bố Craig Wright chỉ l&agrave; h&agrave;ng fake.</a></strong></li>\r\n</ul>\r\n\r\n<p>C&ocirc;ng nghệ Blockchain được cho rằng c&oacute; thể tăng hiệu quả của c&aacute;c giao dịch bất động sản.</p>\r\n\r\n<p>H&atilde;ng tư vấn dịch vụ bất động sản Savills vừa đưa ra nhận định cho rằng, c&aacute;c nh&agrave; đầu tư bất động sản d&ugrave; ở bất cứ ph&acirc;n kh&uacute;c n&agrave;o đều d&egrave; chừng trước vấn đề giao dịch giả v&agrave; những tranh chấp về quyền sở hữu bất động sản. Theo đ&oacute;, chứng nhận sở hữu đất lu&ocirc;n l&agrave; một vấn đề nhức nhối với c&aacute;c nh&agrave; đầu tư tại c&aacute;c thị trường ph&aacute;t triển như ch&acirc;u &Aacute;.</p>\r\n\r\n<p>Tuy nhi&ecirc;n,&nbsp;<a href=\"https://blogtien.com/cong-nghe-blockchain-can-3-5-nam-de-chung-minh-tac-dong/\" rel=\"noopener\" target=\"_blank\">c&ocirc;ng nghệ blockchain</a>&nbsp;c&oacute; thể l&agrave; giải ph&aacute;p cho những điều n&agrave;y. Blockchain l&agrave; một dạng bản ghi kỹ thuật số c&oacute; thể ghi nhận v&agrave; lưu trữ được những th&ocirc;ng tin về một giao dịch tr&ecirc;n một mạng lưới c&aacute;c m&aacute;y t&iacute;nh, bao gồm cả những người đ&atilde; từng tham gia v&agrave;o giao dịch đ&oacute; v&agrave; cung cấp một m&atilde; số ri&ecirc;ng biệt cho mỗi block.</p>\r\n\r\n<p>&ldquo;Blockchain c&oacute; thể l&agrave; giải ph&aacute;p nhằm thay đổi cục diện vấn đề sở hữu bất động sản. Nhờ việc số h&oacute;a c&aacute;c giao dịch, c&ocirc;ng nghệ n&agrave;y cho ph&eacute;p c&aacute;c th&ocirc;ng tin li&ecirc;n quan được lưu trữ th&agrave;nh dạng chuỗi, nhờ đ&oacute; c&oacute; thể cung đảm bảo t&iacute;nh minh bạch trong sở hữu bất động sản cũng như giảm thiểu thời gian v&agrave; chi ph&iacute;&rdquo; &ocirc;ng Chris Marriot, Gi&aacute;m đốc Savills Đ&ocirc;ng Nam &Aacute; cho biết.</p>\r\n\r\n<p>Savills cho rằng, lợi &iacute;ch của blockchain kh&ocirc;ng bị hạn chế với c&aacute;c nước đang ph&aacute;t triển, nơi m&agrave; th&ocirc;ng tin đăng k&yacute; đất đai kh&ocirc;ng đầy đủ. Ứng dụng của c&ocirc;ng nghệ n&agrave;y tới c&aacute;c giao dịch bất động sản trong tương lai sẽ tăng đ&aacute;ng kể, kh&ocirc;ng chỉ trong tốc độ thanh to&aacute;n m&agrave; c&ograve;n trong việc thanh khoản, từ đ&oacute; sẽ tăng gi&aacute; trị cho t&agrave;i sản so với c&aacute;c loại t&agrave;i sản kh&aacute;c.</p>\r\n\r\n<p>Ngo&agrave;i ra, ứng dụng blockchain c&ograve;n c&oacute; thể chia nhỏ quyền sở hữu của bất động sản c&aacute;c c&ocirc;ng ty bảo hiểm về quyền sở hữu, luật sư v&agrave; m&ocirc;i giới bất động sản, Savills cho biết.</p>\r\n\r\n<p><img height=\"336\" sizes=\"(max-width: 565px) 100vw, 565px\" src=\"https://blogtien.com/wp-content/uploads/2019/02/86786.jpg\" srcset=\"https://blogtien.com/wp-content/uploads/2019/02/86786.jpg 565w, https://blogtien.com/wp-content/uploads/2019/02/86786-300x178.jpg 300w\" width=\"565\" /></p>\r\n\r\n<p>Tuy vậy, hướng ứng dụng về việc chia nhỏ quyền sở hữu bất động sản đem lại rủi ro cho c&aacute;c nh&agrave; đầu tư, do sự biến động của thị trường tiền ảo. Trong khi đ&oacute;, c&aacute;c đơn vị quản l&yacute; tại Ch&acirc;u &Aacute; &ndash; Th&aacute;i B&igrave;nh Dương vẫn chưa thể nắm bắt r&otilde; được c&aacute;c loại tiền ảo. Ch&iacute;nh v&igrave; vậy, c&aacute;c giao dịch blockchain vẫn sẽ c&ograve;n kh&oacute; được chấp nhận trong thời gian trước mắt.</p>\r\n\r\n<p>Đ&aacute;nh gi&aacute; về tiềm năng của blockchain đối với thị trường bất động sản Việt Nam, &ocirc;ng Matthew Powell Gi&aacute;m đốc Savills H&agrave; Nội tin rằng c&ocirc;ng nghệ n&agrave;y c&oacute; thể tăng hiệu quả của c&aacute;c giao dịch bất động sản một c&aacute;ch đ&aacute;ng kể&rdquo;.</p>\r\n\r\n<p>Theo Matthew Powell, đặc th&ugrave; của c&aacute;c giao dịch bất động sản l&agrave; c&oacute; gi&aacute; trị lớn, v&igrave; vậy n&ecirc;n thường mất nhiều thời gian, chi ph&iacute; v&agrave; thủ tục giấy tờ để ho&agrave;n thiện. Điều n&agrave;y tạo ra vấn đề về thanh khoản v&agrave; minh bạch cho thị trường bất động sản.</p>\r\n\r\n<p>Blockchain c&oacute; tiềm năng để thay đổi cục diện: bằng c&aacute;ch số h&oacute;a giao dịch, c&ocirc;ng nghệ n&agrave;y giảm thiểu thời gian v&agrave; chi ph&iacute;, đồng thời tăng t&iacute;nh minh bạch v&agrave; an to&agrave;n. Tuy việc ứng dụng blockchain to&agrave;n cầu vẫn c&ograve;n đang ở giai đoạn đầu, t&ocirc;i tin rằng Việt Nam c&oacute; tiềm năng để nhanh ch&oacute;ng bắt kịp xu hướng của thế giới. Ch&uacute;ng ta c&oacute; cơ cấu d&acirc;n số trẻ v&agrave; ham học hỏi c&aacute;c c&ocirc;ng nghệ mới, b&ecirc;n cạnh đ&oacute; l&agrave; những ch&iacute;nh s&aacute;ch khuyến kh&iacute;ch, mang t&iacute;nh nh&igrave;n xa tr&ocirc;ng rộng của nh&agrave; nước,&rdquo; &ocirc;ng Powel n&oacute;i th&ecirc;m.</p>', '65_Blockchain1.jpg', '5', '07-08-2021', '2021-08-06 11:28:25', '2021-08-06 11:28:25'),
(18, 'Bộ ba bất khả thi của Blockchain là gì?', '120', '<p>Trong kinh tế học, c&oacute; bộ ba bất khả thi cho rằng kh&ocirc;ng thể thực hiện được c&ugrave;ng một l&uacute;c đồng thời ba ch&iacute;nh s&aacute;ch gồm: chế độ&nbsp;tỷ gi&aacute; hối đo&aacute;i&nbsp;cố định,&nbsp;ch&iacute;nh s&aacute;ch tiền tệ&nbsp;độc lập để ổn định gi&aacute; cả v&agrave; tự do&nbsp;lưu chuyển vốn.&nbsp;Chỉ c&oacute; thể thực hiện hai trong ba ch&iacute;nh s&aacute;ch n&agrave;y m&agrave; th&ocirc;i. Thuật ngữ n&agrave;y d&ugrave;ng để chỉ t&iacute;nh dễ đổ vỡ của một quốc gi&aacute; cố t&igrave;nh thi h&agrave;nh ba ch&iacute;nh s&aacute;ch n&agrave;y c&ugrave;ng một l&uacute;c.</p>', '<h2><strong>Trong Blockchain, cũng c&oacute; bộ ba bất khả thi bao gồm khả năng mở rộng (Scalability), vấn đề an ninh (Security) v&agrave; t&iacute;nh phi tập trung (Decentralization).</strong></h2>\r\n\r\n<ul>\r\n	<li><strong><a href=\"https://blogtien.com/day-la-cach-chung-ta-doi-mat-voi-fud/\" rel=\"noopener\" target=\"_blank\">Đ&acirc;y l&agrave; c&aacute;ch ch&uacute;ng ta đối mặt với FUD</a></strong></li>\r\n	<li><strong><a href=\"https://blogtien.com/nhung-dieu-ma-ban-can-luu-y-khi-tham-gia-thi-truong-crypto/\" rel=\"noopener\" target=\"_blank\">Những điều m&agrave; bạn cần lưu &yacute; khi tham gia thị trường crypto</a></strong></li>\r\n	<li><strong><a href=\"https://blogtien.com/3-bai-hoc-trader-co-duoc-tu-nhung-thi-truong-di-ngang/\" rel=\"noopener\" target=\"_blank\">3 b&agrave;i học Trader c&oacute; được từ những thị trường đi ngang</a></strong></li>\r\n</ul>\r\n\r\n<p>Trong kinh tế học, c&oacute; bộ ba bất khả thi cho rằng kh&ocirc;ng thể thực hiện được c&ugrave;ng một l&uacute;c đồng thời ba ch&iacute;nh s&aacute;ch gồm: chế độ&nbsp;tỷ gi&aacute; hối đo&aacute;i&nbsp;cố định,&nbsp;ch&iacute;nh s&aacute;ch tiền tệ&nbsp;độc lập để ổn định gi&aacute; cả v&agrave; tự do&nbsp;lưu chuyển vốn.&nbsp;Chỉ c&oacute; thể thực hiện hai trong ba ch&iacute;nh s&aacute;ch n&agrave;y m&agrave; th&ocirc;i. Thuật ngữ n&agrave;y d&ugrave;ng để chỉ t&iacute;nh dễ đổ vỡ của một quốc gi&aacute; cố t&igrave;nh thi h&agrave;nh ba ch&iacute;nh s&aacute;ch n&agrave;y c&ugrave;ng một l&uacute;c.</p>\r\n\r\n<p>C&ograve;n trong ng&agrave;nh&nbsp;<a href=\"https://blogtien.com/blockchain-la-gi/\" rel=\"noopener\" target=\"_blank\">Blockchain</a>, cũng c&oacute; một bộ ba bất khả thi như vậy bao gồm khả năng mở rộng (Scalability), vấn đề an ninh (Security) v&agrave; t&iacute;nh phi tập trung (Decentralization). Một Blockchain chỉ thực hiện được 2 trong 3 t&iacute;nh năng n&agrave;y.</p>\r\n\r\n<p>C&oacute; những v&iacute; dụ thực tế của bộ ba bất khả thi n&agrave;y. Khi ph&iacute; giao dịch trung b&igrave;nh đối với&nbsp;tiền ảo bitcoin&nbsp;tăng l&ecirc;n đều đặn sẽ xuất hiện c&aacute;c nghi vấn li&ecirc;n quan đến khả năng mở rộng của Bitcoin.</p>\r\n\r\n<p>Tương tự như vậy, c&aacute;c vấn đề về khả năng mở rộng của Ethereum đ&atilde; được đặt ra rất r&otilde; r&agrave;ng khi CryptoKitties (game x&acirc;y dựng dựa tr&ecirc;n nền tảng blockchain của Etherium) l&agrave;m chậm to&agrave;n bộ mạng Ethereum. Khả năng mở rộng, trong bối cảnh kiến tr&uacute;c hệ thống blockchain hiện tại, được xem x&eacute;t song song với c&aacute;c &yacute; tưởng về việc ph&acirc;n cấp v&agrave; n&acirc;ng cấp an ninh.</p>\r\n\r\n<p><em><strong>Phi tập trung hay ph&acirc;n cấp (Decentralization)</strong></em>&nbsp;l&agrave; một thuộc t&iacute;nh lu&ocirc;n cần c&oacute; ở c&aacute;c Blockchain hiện tại. Ph&acirc;n cấp c&oacute; thể kiểm duyệt v&agrave; cho ph&eacute;p bất kỳ ai cũng c&oacute; thể tham gia v&agrave;o một hệ sinh th&aacute;i phi tập trung m&agrave; kh&ocirc;ng g&acirc;y ảnh hưởng đến hệ thống.</p>\r\n\r\n<p><em><strong>Khả năng mở rộng (Scalability)</strong></em>&nbsp;li&ecirc;n quan đến khả năng xử l&yacute; giao dịch tr&ecirc;n bất kỳ mạng cụ thể n&agrave;o. Nếu public blockchain (như Bitcoin v&agrave; Ethereum) kh&ocirc;ng giới hạn đối tượng v&agrave; số người sử dụng, th&igrave; hệ thống nhất định phải được thiết lập khả năng xử l&yacute; t&igrave;nh huống nền tảng c&oacute; h&agrave;ng triệu người d&ugrave;ng internet tham gia.</p>\r\n\r\n<p><em><strong>Vấn đề an ninh (Security)</strong></em>&nbsp;li&ecirc;n quan đến việc đảm bảo sự an to&agrave;n cho hệ thống. Sức đề kh&aacute;ng trung b&igrave;nh tối thiểu của c&aacute;c Blockchain cần đạt được l&agrave; phải chống lại được c&aacute;c cuộc tấn c&ocirc;ng như tấn c&ocirc;ng Sybil, tấn c&ocirc;ng DDoS, v.v.</p>\r\n\r\n<p><img src=\"https://i0.wp.com/blogtienao.com/wp-content/uploads/2019/02/A%CC%89nh-chu%CC%A3p-Ma%CC%80n-hi%CC%80nh-2018-10-22-lu%CC%81c-13.55.11.jpg?resize=700,476\" /></p>\r\n\r\n<p>Bộ ba bất khả thi của Blockchain</p>\r\n\r\n<p>Hiện tại, c&aacute;c blockchain như Bitcoin v&agrave; Ethereum được n&acirc;ng cấp thiết kế tập trung v&agrave;o vấn đề ph&acirc;n cấp v&agrave; bảo mật. Mặt kh&aacute;c, cả hai blockchain đều c&oacute; thời gian xử l&yacute; giao dịch cực kỳ chậm do tất cả c&aacute;c n&uacute;t tr&ecirc;n c&aacute;c blockchain tương ứng phải đạt được sự đồng thuận trước khi giao dịch c&oacute; thể được xử l&yacute;. Ch&iacute;nh bởi cả hai vấn đề n&agrave;y n&ecirc;n đ&atilde; g&acirc;y ra ảnh hưởng về mặt chi ph&iacute; cho việc n&acirc;ng cấp khả năng mở rộng của hệ thống.</p>\r\n\r\n<p><a href=\"https://blogtien.com/tien-ao/ethereum/\" rel=\"noopener\" target=\"_blank\">Ethereum</a>&nbsp;c&oacute; thể xử l&yacute; khoảng 15 giao dịch mỗi gi&acirc;y, trong khi Bitcoin chỉ c&oacute; thể xử l&yacute; khoảng 7 giao dịch mỗi gi&acirc;y. Tuy nhi&ecirc;n, cả hai con số n&agrave;y đều nhỏ hơn rất nhiều so với dịch vụ thanh to&aacute;n VISA với khả năng xử l&yacute; tới 24.000 giao dịch mỗi gi&acirc;y. Thậm ch&iacute; ngay cả những đề xuất để giải quyết khả năng mở rộng của blockchain cũng một lần nữa lại trở n&ecirc;n bế tắc, tiến tho&aacute;i lưỡng nan.</p>\r\n\r\n<p>C&ograve;n với Blockchain của&nbsp;<a href=\"https://blogtien.com/ripple-la-gi/\" rel=\"noopener\" target=\"_blank\">Ripple</a>&nbsp;th&igrave; họ ưu ti&ecirc;n về vấn đề an ninh v&agrave; khả năng mở rộng, Ripple Blockchain cung cấp khả năng t&ugrave;y biến v&agrave; kiểm so&aacute;t tốt hơn, ch&iacute;nh họ quyết định được ai sẽ tham gia mạng. Điều đ&oacute; c&oacute; nghĩa l&agrave; sẽ c&oacute; nhiều t&agrave;i nguy&ecirc;n để đảm bảo cho sự an to&agrave;n cho mạng lưới. V&igrave; vậy, Blockchain của Ripple c&oacute; khả năng mở rộng tốt hơn của Bitcoin hay Ethereum. Tr&ecirc;n thực tế, Blockchain dạng n&agrave;y c&oacute; thể gi&uacute;p c&aacute;c tập đo&agrave;n lưu trữ th&ocirc;ng tin trong c&aacute;c n&uacute;t mạng m&agrave; họ tin cậy.</p>', '29_Blockchain2.jpg', '5', '07-08-2021', '2021-08-06 11:29:44', '2021-08-06 11:29:44'),
(19, 'Facebook tuyển dụng hàng loạt tài năng về blockchain', '695', '<p>Theo&nbsp;<em>Mashable</em>, Chainspace l&agrave; một nền tảng ứng dụng phi tập trung c&oacute; quy m&ocirc; to&agrave;n cầu, gần giống với&nbsp;<a href=\"https://blogtien.com/tien-ao/ethereum/\" rel=\"noopener\" target=\"_blank\">Ethereum</a>&nbsp;v&agrave; vẫn đang trong giai đoạn ph&aacute;t triển ban đầu. Gần đ&acirc;y c&ocirc;ng ty đ&atilde; đăng tải một ghi ch&uacute; tr&ecirc;n trang web của m&igrave;nh th&ocirc;ng b&aacute;o rằng đang chuyển sang một thứ mới mẻ hơn. C&aacute;c đoạn m&atilde; v&agrave; t&agrave;i liệu của Chainspace vẫn sẽ ở dạng nguồn mở v&agrave; tất cả c&aacute;c nghi&ecirc;n cứu học thuật được xuất bản trước đ&acirc;y sẽ vẫn c&oacute; sẵn cho người d&ugrave;ng truy cập.</p>', '<p>Facebook tiếp nhận 4 nh&acirc;n vật chủ chốt đứng đằng sau c&ocirc;ng ty khởi nghiệp Chainspace về chuỗi khối (blockchain), v&agrave; đang đầu tư những nỗ lực cũng như nguồn lực đ&aacute;ng kể v&agrave;o c&ocirc;ng nghệ n&agrave;y.</p>\r\n\r\n<ul>\r\n	<li><strong><a href=\"https://blogtien.com/phat-hien-ma-doc-danh-cap-tien-ma-hoa-ngay-tren-kho-ung-dung-google-play/\" rel=\"noopener\" target=\"_blank\">Ph&aacute;t hiện m&atilde; độc đ&aacute;nh cắp tiền m&atilde; h&oacute;a ngay tr&ecirc;n kho ứng dụng Google Play</a></strong></li>\r\n	<li><strong><a href=\"https://blogtien.com/gioi-dau-tu-tien-ma-hoa-thich-staking-de-kiem-loi/\" rel=\"noopener\" target=\"_blank\">Giới đầu tư tiền m&atilde; h&oacute;a th&iacute;ch &ldquo;staking&rdquo; để kiếm lời</a></strong></li>\r\n	<li><strong><a href=\"https://blogtien.com/bitcoin-etf-cryptomom-peirce-cua-sec-co-them-tin-tot-cho-tien-dien-tu/\" rel=\"noopener\" target=\"_blank\">Bitcoin ETF: CryptoMom Peirce của SEC c&oacute; th&ecirc;m tin tốt cho tiền điện tử.</a></strong></li>\r\n</ul>\r\n\r\n<p>Theo&nbsp;<em>Mashable</em>, Chainspace l&agrave; một nền tảng ứng dụng phi tập trung c&oacute; quy m&ocirc; to&agrave;n cầu, gần giống với&nbsp;<a href=\"https://blogtien.com/tien-ao/ethereum/\" rel=\"noopener\" target=\"_blank\">Ethereum</a>&nbsp;v&agrave; vẫn đang trong giai đoạn ph&aacute;t triển ban đầu. Gần đ&acirc;y c&ocirc;ng ty đ&atilde; đăng tải một ghi ch&uacute; tr&ecirc;n trang web của m&igrave;nh th&ocirc;ng b&aacute;o rằng đang chuyển sang một thứ mới mẻ hơn. C&aacute;c đoạn m&atilde; v&agrave; t&agrave;i liệu của Chainspace vẫn sẽ ở dạng nguồn mở v&agrave; tất cả c&aacute;c nghi&ecirc;n cứu học thuật được xuất bản trước đ&acirc;y sẽ vẫn c&oacute; sẵn cho người d&ugrave;ng truy cập.</p>\r\n\r\n<p>C&ocirc;ng ty khởi nghiệp n&agrave;y đang trong qu&aacute; tr&igrave;nh n&acirc;ng v&ograve;ng t&agrave;i trợ hạt giống l&ecirc;n mức 2,5 đến 3 triệu USD v&agrave; gần như đạt được mục ti&ecirc;u n&agrave;y khi c&aacute;c th&agrave;nh vi&ecirc;n chủ chốt của nh&oacute;m được Facebook thu&ecirc;.</p>\r\n\r\n<p>C&aacute;c nh&acirc;n sự chủ chốt của Chainspace đ&atilde; thay đổi hồ sơ LinkedIn cho thấy họ đang l&agrave;m việc cho Facebook. George Danezis, đồng s&aacute;ng lập Chainspace, hiện l&agrave; nh&agrave; nghi&ecirc;n cứu blockchain tại Facebook kể từ th&aacute;ng 2.2019. Đồng s&aacute;ng lập Shehar Bano v&agrave; Alberto Sonnino cũng đ&atilde; thay đổi hồ sơ LinkedIn với c&aacute;c th&ocirc;ng tin tương tự. Mặc d&ugrave; tin tức chưa được c&ocirc;ng bố ch&iacute;nh thức nhưng một số người từ Chainspace đ&atilde; th&ocirc;ng b&aacute;o c&ocirc;ng khai họ hiện l&agrave;m việc tại Facebook.</p>\r\n\r\n<p>Đối với Facebook, c&ocirc;ng ty tuy&ecirc;n bố c&ocirc;ng khai mối quan t&acirc;m của m&igrave;nh đối với blockchain nhiều lần, đ&aacute;ng ch&uacute; &yacute; nhất l&agrave; v&agrave;o th&aacute;ng 5.2018 khi cựu l&atilde;nh đạo David Marcus tuy&ecirc;n bố &ocirc;ng rời nh&oacute;m Messenger để l&atilde;nh đạo một nh&oacute;m mới nhằm kh&aacute;m ph&aacute; c&aacute;ch tận dụng tốt nhất&nbsp;<a href=\"https://blogtien.com/blockchain-la-gi/\" rel=\"noopener\" target=\"_blank\">blockchain</a>&nbsp;tr&ecirc;n Facebook bắt đầu từ con số kh&ocirc;ng. V&agrave; nhiều nguồn tin đ&aacute;ng tin cậy cho biết c&aacute;c cựu nh&acirc;n vi&ecirc;n của Chainspace hiện tham gia nh&oacute;m n&agrave;y.</p>\r\n\r\n<p>V&agrave;o th&aacute;ng 12.2018, Bloomberg b&aacute;o c&aacute;o rằng Facebook đang l&agrave;m việc về một loại tiền điện tử sẽ cho ph&eacute;p người d&ugrave;ng dịch vụ nhắn tin WhatsApp gửi v&agrave; nhận tiền.</p>\r\n\r\n<p>Ph&aacute;t ng&ocirc;n vi&ecirc;n của Facebook cho biết c&ocirc;ng ty đ&atilde; kh&ocirc;ng mua c&ocirc;ng nghệ của Chainspace m&agrave; chỉ nhắm đến c&aacute;c t&agrave;i năng của c&ocirc;ng ty n&agrave;y.</p>', '94_Blockchain3.jpg', '5', '07-08-2021', '2021-08-06 11:30:57', '2021-08-06 11:31:31'),
(20, 'Công nghệ blockchain cần 3-5 năm để chứng minh tác động', '426', '<p>Theo Bloomberg, người đứng đầu mảng nghi&ecirc;n cứu to&agrave;n cầu của&nbsp;<a href=\"https://blogtien.com/ngan-hang-jpmorgan-canh-bao-ve-bitcoin-va-tien-ma-hoa/\" rel=\"noopener\" target=\"_blank\">JPMorgan</a>, b&agrave; Joyce Chang, vừa cho hay: &ldquo;Blockchain sẽ kh&ocirc;ng t&aacute;i tạo hệ thống thanh to&aacute;n to&agrave;n cầu, nhưng sẽ cải tiến n&oacute;. T&aacute;c động &yacute; nghĩa nhất c&oacute; lẽ sẽ đến trong 3-5 năm nữa, chủ yếu trong mảng t&agrave;i ch&iacute;nh thương mại&rdquo;.</p>', '<p>Ng&acirc;n h&agrave;ng JPMorgan Chase nhận định t&aacute;c động của blockchain sẽ cần v&agrave;i năm để thể hiện r&otilde;, song c&ocirc;ng nghệ n&agrave;y sẽ c&oacute; một số tiến bộ nhất định ngay cả khi tiền m&atilde; h&oacute;a lao dốc.</p>\r\n\r\n<ul>\r\n	<li><strong><a href=\"https://blogtien.com/nem-foundation-dung-tren-bo-vuc-pha-san-ke-hoach-sa-thai-va-tai-cau-truc/\" rel=\"noopener\" target=\"_blank\">NEM Foundation đứng tr&ecirc;n bờ vực ph&aacute; sản, kế hoạch sa thải v&agrave; t&aacute;i cấu tr&uacute;c.</a></strong></li>\r\n	<li><strong><a href=\"https://blogtien.com/hoi-dong-vang-the-gioi-wgc-bitcoin-khong-giong-vang/\" rel=\"noopener\" target=\"_blank\">Hội đồng v&agrave;ng thế giới (WGC): Bitcoin kh&ocirc;ng giống v&agrave;ng</a></strong></li>\r\n	<li><strong><a href=\"https://blogtien.com/cryptopia-lai-bi-tan-cong-mot-lan-nua-17-000-vi-anh-huong/\" rel=\"noopener\" target=\"_blank\">Cryptopia lại bị tấn c&ocirc;ng một lần nữa, 17.000 v&iacute; ảnh hưởng.</a></strong></li>\r\n</ul>\r\n\r\n<p>Theo Bloomberg, người đứng đầu mảng nghi&ecirc;n cứu to&agrave;n cầu của&nbsp;<a href=\"https://blogtien.com/ngan-hang-jpmorgan-canh-bao-ve-bitcoin-va-tien-ma-hoa/\" rel=\"noopener\" target=\"_blank\">JPMorgan</a>, b&agrave; Joyce Chang, vừa cho hay: &ldquo;Blockchain sẽ kh&ocirc;ng t&aacute;i tạo hệ thống thanh to&aacute;n to&agrave;n cầu, nhưng sẽ cải tiến n&oacute;. T&aacute;c động &yacute; nghĩa nhất c&oacute; lẽ sẽ đến trong 3-5 năm nữa, chủ yếu trong mảng t&agrave;i ch&iacute;nh thương mại&rdquo;.</p>\r\n\r\n<p>Giải ph&aacute;p t&agrave;i ch&iacute;nh thương mại&nbsp;blockchain phổ biến hơn c&aacute;c giải ph&aacute;p kh&aacute;c v&igrave; nhiều lợi &iacute;ch đến từ kỹ thuật số h&oacute;a, theo b&aacute;o c&aacute;o m&agrave; ng&acirc;n h&agrave;ng Mỹ c&ocirc;ng bố h&ocirc;m 24.1. Bất chấp tiền m&atilde; h&oacute;a thu h&uacute;t nhiều sự ch&uacute; &yacute; nhất, c&ocirc;ng nghệ blockchain nền tảng mới l&agrave; thứ được sử dụng để x&aacute;c minh, ghi lại c&aacute;c giao dịch quan trọng.</p>\r\n\r\n<p>B&agrave; Chang nhắc đến Mạng th&ocirc;ng tin li&ecirc;n ng&acirc;n h&agrave;ng (INN) do Quorum cung ứng v&agrave; JPMorgan ph&aacute;t triển dựa tr&ecirc;n blockchain ethereum. INN hiện c&oacute; 157 ng&acirc;n h&agrave;ng từ khắp nơi tr&ecirc;n thế giới trong mạng lưới. N&oacute; đặt mục ti&ecirc;u giải quyết một số th&aacute;ch thức trong việc chia sẻ li&ecirc;n ng&acirc;n h&agrave;ng, gi&uacute;p thanh to&aacute;n sớm đến tay người thụ hưởng.</p>\r\n\r\n<p>Việc d&ugrave;ng&nbsp;<a href=\"https://blogtien.com/11-du-an-blockchain-co-nhieu-doi-tac-lien-ket-nhat/\" rel=\"noopener\" target=\"_blank\">blockchain</a>&nbsp;của c&aacute;c nh&agrave; băng T&acirc;y Ban Nha cũng đ&aacute;ng ch&uacute; &yacute;. Đơn cử, Banco Santander l&agrave; đơn vị ti&ecirc;n phong triển khai&nbsp;c&ocirc;ng nghệ blockchain&nbsp;trong ng&agrave;nh ng&acirc;n h&agrave;ng truyền thống, trong khi BBVA l&agrave; nh&agrave; băng đầu ti&ecirc;n tr&ecirc;n thế giới tận dụng c&ocirc;ng nghệ blockchain trong to&agrave;n bộ qu&aacute; tr&igrave;nh ph&aacute;t h&agrave;nh 75 triệu EUR, tương đương 86 triệu USD, khoản vay doanh nghiệp.</p>\r\n\r\n<p>D&ugrave; vậy, blockchain vẫn cần được cải thiện. B&agrave; Chang cho hay bốn th&aacute;ch thức của c&ocirc;ng nghệ n&agrave;y l&agrave; khả năng mở rộng quy m&ocirc;, t&iacute;ch hợp, hiệu quả chi ph&iacute; v&agrave; quy định, luật ph&aacute;p. Hiện ứng dụng blockchain nổi tiếng nhất v&agrave;o tiền m&atilde; h&oacute;a l&agrave; bitcoin kh&ocirc;ng mấy ấn tượng, khi&nbsp;<a href=\"https://blogtien.com/ty-gia/BTC/bitcoin/\" rel=\"noopener\" target=\"_blank\">gi&aacute; bitcoin</a>&nbsp;đ&atilde; giảm hơn 80% từ mức cao nhất đạt được v&agrave;o th&aacute;ng 12.2017. Cũng theo ước t&iacute;nh của JPMorgan, hiện chi ph&iacute; khai th&aacute;c bitcoin trung b&igrave;nh dưới gi&aacute; bitcoin.</p>\r\n\r\n<p>B&agrave; Chang cũng chỉ ra rằng khối lượng giao dịch tiền m&atilde; h&oacute;a đang giảm v&agrave; c&aacute;c nh&agrave; b&aacute;n lẻ lớn vẫn chưa ứng dụng ch&uacute;ng. &ldquo;Kh&oacute; l&ograve;ng vượt qua c&aacute;c vấn đề ph&aacute;p l&yacute;. Ch&uacute;ng ta cần t&aacute;ch blockchain ra khỏi tiền m&atilde; h&oacute;a&rdquo;, chuy&ecirc;n gia cho biết. Vấn đề ph&aacute;p l&yacute; ở đ&acirc;y gồm việc Ủy ban Chứng kho&aacute;n v&agrave; S&agrave;n giao dịch Mỹ (SEC) chưa chấp thuận quỹ ho&aacute;n đổi danh mục tiền m&atilde; h&oacute;a.</p>', '89_Blockchain4.jpg', '5', '07-08-2021', '2021-08-06 11:32:51', '2021-08-06 11:32:51');
INSERT INTO `posts` (`id`, `title`, `views`, `short_desc`, `desc`, `image`, `post_category_id`, `post_date`, `created_at`, `updated_at`) VALUES
(21, 'Ứng dụng của blockchain trong tương lai vượt bậc đến đâu?', '139', '<p><a href=\"https://blogtien.com/smart-contract-hop-dong-thong-minh-la-gi/\" rel=\"noopener\" target=\"_blank\">Hợp đồng th&ocirc;ng minh</a>&nbsp;được x&acirc;y dựng tr&ecirc;n nền tảng c&ocirc;ng nghệ blockchain c&oacute; thể loại bỏ sự cần thiết của nh&acirc;n lực trung gian. Lưới năng lượng c&oacute; thể sử dụng c&ocirc;ng nghệ n&agrave;y nhằm tăng cường an ninh mạng. C&aacute;c nh&agrave; cung cấp vận tải h&agrave;ng kh&ocirc;ng vũ trụ c&oacute; thể t&igrave;m đến blockchain như một khoản đầu tư tiềm năng nhằm theo d&otilde;i chuỗi cung ứng v&agrave; tăng hiệu quả kinh doanh.</p>', '<p>Ch&uacute;ng ta đ&atilde; nghe về c&ocirc;ng nghệ blockchain trong lĩnh vực tiền điện tử như bitcoin. Tuy nhi&ecirc;n, c&ocirc;ng nghệ mới n&agrave;y hiện c&oacute; ứng dụng kinh doanh tr&ecirc;n phạm vi rộng khi c&aacute;c mạng ngang h&agrave;ng trở n&ecirc;n phổ biến.</p>\r\n\r\n<ul>\r\n	<li><strong><a href=\"https://blogtien.com/ca-map-hoat-dong-rat-tich-cuc-trong-tuan-qua/\" rel=\"noopener\" target=\"_blank\">C&aacute; mập hoạt động rất t&iacute;ch cực trong tuần qua</a></strong></li>\r\n	<li><strong><a href=\"https://blogtien.com/bitcoin-co-gia-20-000-usd-trong-nam-2017-la-mot-dieu-bat-thuong/\" rel=\"noopener\" target=\"_blank\">Bitcoin c&oacute; gi&aacute; 20.000 USD trong năm 2017 l&agrave; một &lsquo;điều bất thường&rsquo;.</a></strong></li>\r\n	<li><strong><a href=\"https://blogtien.com/vinny-lingham-thi-truong-tien-dien-tu-se-khong-the-som-phuc-hoi/\" rel=\"noopener\" target=\"_blank\">Vinny Lingham: Thị trường tiền điện tử sẽ kh&ocirc;ng thể sớm phục hồi.</a></strong></li>\r\n</ul>\r\n\r\n<p><a href=\"https://blogtien.com/smart-contract-hop-dong-thong-minh-la-gi/\" rel=\"noopener\" target=\"_blank\">Hợp đồng th&ocirc;ng minh</a>&nbsp;được x&acirc;y dựng tr&ecirc;n nền tảng c&ocirc;ng nghệ blockchain c&oacute; thể loại bỏ sự cần thiết của nh&acirc;n lực trung gian. Lưới năng lượng c&oacute; thể sử dụng c&ocirc;ng nghệ n&agrave;y nhằm tăng cường an ninh mạng. C&aacute;c nh&agrave; cung cấp vận tải h&agrave;ng kh&ocirc;ng vũ trụ c&oacute; thể t&igrave;m đến blockchain như một khoản đầu tư tiềm năng nhằm theo d&otilde;i chuỗi cung ứng v&agrave; tăng hiệu quả kinh doanh.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, một trong ứng dụng bất ngờ nhất của blockchain phải kể đến n&ocirc;ng nghiệp.</p>\r\n\r\n<h2>Blockchain c&oacute; thể đảm bảo chất lượng rau củ</h2>\r\n\r\n<p><a href=\"https://blogtien.com/blockchain-la-gi/\" rel=\"noopener\" target=\"_blank\">Blockchain</a>&nbsp;l&agrave; sản phẩm phụ của bitcoin, nhưng phạm vi ứng dụng của n&oacute; lại v&ocirc; hạn. Blockchain c&oacute; t&aacute;c động đ&aacute;ng kể ở đời sống thật v&agrave; c&oacute; khả năng kh&aacute;i niệm n&agrave;y sẽ được &aacute;p dụng cho tất cả c&aacute;c ng&agrave;nh c&ocirc;ng nghiệp trong tương lai.</p>\r\n\r\n<p>Một v&iacute; dụ của tiềm năng tr&ecirc;n ch&iacute;nh l&agrave; đảm bảo chất lượng (QA), một qui tr&igrave;nh doanh nghiệp n&agrave;o cũng cần.</p>\r\n\r\n<p>Một v&iacute; dụ t&iacute;ch cực về t&aacute;c động của c&ocirc;ng nghệ blockchain đối với n&ocirc;ng nghiệp l&agrave; gia tăng khả năng truy nguy&ecirc;n nguồn gốc của c&aacute;c sản phẩm n&ocirc;ng nghiệp.</p>\r\n\r\n<p>Tại Aya-cho, Higashi Morokata-kun, Miyazaki Prefecture, Nhật Bản, thử nghiệm minh họa đ&atilde; được thực hiện bằng c&aacute;ch sử dụng c&ocirc;ng nghệ blockchain để đảm bảo chất lượng sản phẩm n&ocirc;ng nghiệp hữu cơ.</p>\r\n\r\n<p>Dữ liệu được ghi lại bao gồm người gieo trồng sản phẩm, t&igrave;nh trạng đất, t&iacute;nh chất của thuốc trừ s&acirc;u v&agrave; m&ocirc;i trường sản xuất. C&ocirc;ng nghệ blockchain sẽ kiểm so&aacute;t chất lượng của sản phẩm.</p>\r\n\r\n<p><img height=\"417\" sizes=\"(max-width: 685px) 100vw, 685px\" src=\"https://blogtien.com/wp-content/uploads/2019/01/0509_blockchain-agriculutre.jpg\" srcset=\"https://blogtien.com/wp-content/uploads/2019/01/0509_blockchain-agriculutre.jpg 685w, https://blogtien.com/wp-content/uploads/2019/01/0509_blockchain-agriculutre-300x183.jpg 300w\" width=\"685\" /></p>\r\n\r\n<p>C&aacute;c chuy&ecirc;n gia n&ocirc;ng nghiệp hiện cũng lưu &yacute; đến những sản phẩm n&agrave;y bởi ch&uacute;ng c&oacute; gi&aacute; b&aacute;n cao gấp hai lần gi&aacute; thị trường bởi t&iacute;nh chất đặc biệt trong qu&aacute; tr&igrave;nh sản xuất.</p>\r\n\r\n<p>Trong ng&agrave;nh c&ocirc;ng nghiệp sản xuất, khả năng truy nguy&ecirc;n nguồn gốc của linh kiện đ&atilde; tiến bộ đ&aacute;ng kể. Điều tương tự kh&ocirc;ng xảy ra với ng&agrave;nh phần mềm, mặc d&ugrave; vai tr&ograve; của phần mềm đối với sản phẩm v&agrave; dịch vụ sẽ gia tăng trong tương lai.</p>\r\n\r\n<p>Khi điều n&agrave;y xảy ra, c&aacute;c doanh nghiệp phải đảm bảo chất lượng của phần mềm bằng c&aacute;ch tiết lộ ai đ&atilde; viết chương tr&igrave;nh v&agrave; c&aacute;ch thức viết n&oacute;. C&ocirc;ng nghệ blockchain n&ecirc;n l&agrave; một phần quan trọng của qui tr&igrave;nh đảm bảo chất lượng n&agrave;y.</p>\r\n\r\n<h2>Lưu giữ th&ocirc;ng tin trong một kh&ocirc;ng gian ảo an to&agrave;n</h2>\r\n\r\n<p>Trong nhiều năm tới, sự sống c&ograve;n của một c&ocirc;ng ty sẽ phụ thuộc rất nhiều v&agrave;o số lượng th&ocirc;ng tin hữu &iacute;ch m&agrave; n&oacute; sở hữu.</p>\r\n\r\n<p>Một v&iacute; dụ ch&iacute;nh l&agrave; Uber, nh&agrave; cung cấp ứng dụng gọi xe. Họ đ&atilde; l&agrave; một doanh nghiệp nổi tiếng, nhưng như bất k&igrave; doanh nghiệp n&agrave;o kh&aacute;c, họ đang hướng về tương lai để nghi&ecirc;n cứu &ldquo;bằng c&aacute;ch n&agrave;o họ c&oacute; thể tối ưu h&oacute;a dữ liệu kh&aacute;ch h&agrave;ng khổng lồ m&agrave; m&igrave;nh sở hữu để tạo lợi thế kinh doanh mới&rdquo;.</p>\r\n\r\n<p>Uber chưa thay đổi ho&agrave;n to&agrave;n ng&agrave;nh hậu cần của thế giới bằng c&aacute;ch sử dụng dữ liệu &ldquo;chuyển động của con người&rdquo; được t&iacute;ch lũy bởi dịch vụ gọi xe của c&ocirc;ng ty.</p>\r\n\r\n<p>H&atilde;y tưởng tượng t&aacute;c động đến doanh nghiệp Nhật Bản nếu c&aacute;c nh&agrave; l&atilde;nh đạo suy nghĩ s&acirc;u sắc về sự li&ecirc;n quan giữa dữ liệu v&agrave; c&ocirc;ng nghệ trong tương lại.</p>\r\n\r\n<p>Kể từ khi giới thiệu IoT, số lượng cảm biến thu thập dữ liệu v&agrave; lượng th&ocirc;ng tin thu thập được từ ch&uacute;ng đ&atilde; tăng l&ecirc;n đ&aacute;ng kể. c&ocirc;ng nghệ IoT đ&atilde; trong tầm tay. Tuy nhi&ecirc;n, c&oacute; qu&aacute; nhiều c&ocirc;ng ty sử dụng hệ thống bảo mật thấp để quản l&iacute; th&ocirc;ng tin c&oacute; gi&aacute; trị.</p>\r\n\r\n<p>Nhật Bản l&agrave; một quốc gia ph&aacute;t triển với cơ sở sản xuất mạnh mẽ v&agrave; đ&acirc;y l&agrave; quốc gia duy nhất tạo được cơ sở hạ tầng x&atilde; hội ngay từ đầu. Nếu c&aacute;c c&ocirc;ng ty sản xuất Nhật Bản chuyển sang nghi&ecirc;n cứu v&agrave; lưu trữ dữ liệu tr&ecirc;n nền tảng blockchain, cuộc chuyển đổi n&agrave;y sẽ tạo ra gi&aacute; trị lớn. Y&ecirc;u cầu ph&acirc;n t&iacute;ch dữ liệu từ khắp nơi tr&ecirc;n thế giới sẽ đổ dồn về Nhật Bản.</p>\r\n\r\n<p>Hơn nữa, việc sử dụng dữ liệu n&agrave;y c&oacute; thể mang đến những cải tiến mới, chẳng hạn như tinh chế kim loại mạnh chưa từng thấy. Do đ&oacute;, để kh&ocirc;ng uổng ph&iacute; những dữ liệu n&agrave;y, ch&uacute;ng ta cần nơi an to&agrave;n để lưu trữ ch&uacute;ng. V&agrave; nơi đ&oacute; kh&ocirc;ng đ&acirc;u kh&aacute;c ch&iacute;nh l&agrave; blockchain.</p>', '38_Blockchain5.jpg', '5', '07-08-2021', '2021-08-06 11:34:03', '2021-08-06 11:34:03'),
(22, 'Phân tích giá Bitcoin 13/03: Quyết định nằm trong tay các trader.', '112', '<p>Đường SMA 100 nằm tr&ecirc;n đường SMA 200 d&agrave;i hạn chỉ ra rằng con đường &iacute;t kh&aacute;ng cự nhất l&agrave; tăng l&ecirc;n. Tuy nhi&ecirc;n, khoảng c&aacute;ch giữa c&aacute;c chỉ số đ&atilde; thu hẹp đủ để b&aacute;o hiệu đ&agrave; tăng đang dần yếu hơn v&agrave; sự giao nhau trong xu hướng giảm. Trong trường hợp đ&oacute;, gi&aacute; c&oacute; thể quay trở lại mức thấp 3.770 USD.</p>', '<p><strong>Bitcoin vẫn nằm quanh khu vực 3.900 USD nhưng n&oacute; đang c&oacute; xu hướng tho&aacute;t ra khỏi v&ugrave;ng n&agrave;y, c&oacute; thể nh&igrave;n thấy tr&ecirc;n biểu đồ 1 giờ. Điều n&agrave;y c&oacute; thể gi&uacute;p n&oacute; đi đ&uacute;ng hướng trong việc test c&aacute;c mục ti&ecirc;u tăng gi&aacute; tiếp theo được đ&aacute;nh dấu bởi Fibonacci.</strong></p>\r\n\r\n<ul>\r\n	<li><strong><a href=\"https://blogtien.com/facebook-se-thu-loi-lon-neu-tung-ra-dong-tien-ma-hoa-rieng-thanh-cong/\" rel=\"noopener noreferrer\" target=\"_blank\">Facebook sẽ thu lợi lớn nếu tung ra đồng tiền m&atilde; h&oacute;a ri&ecirc;ng th&agrave;nh c&ocirc;ng</a></strong></li>\r\n	<li><strong><a href=\"https://blogtien.com/cong-an-tp-da-nang-canh-bao-nan-lua-dao-duoi-hinh-thuc-dau-tu-tien-ma-hoa/\" rel=\"noopener noreferrer\" target=\"_blank\">C&ocirc;ng an TP Đ&agrave; Nẵng cảnh b&aacute;o nạn lừa đảo dưới h&igrave;nh thức đầu tư tiền m&atilde; h&oacute;a</a></strong></li>\r\n	<li><strong><a href=\"https://blogtien.com/tin-tot-chu-tich-sec-hoa-ky-xac-nhan-ethereum-khong-phai-chung-khoan/\" rel=\"noopener noreferrer\" target=\"_blank\">Tin tốt: Chủ tịch SEC Hoa Kỳ x&aacute;c nhận Ethereum kh&ocirc;ng phải chứng kho&aacute;n</a></strong></li>\r\n</ul>\r\n\r\n<p>Nếu gi&aacute; đ&oacute;ng cửa ở mức 4.000 USD th&igrave; đồng coin c&oacute; khả năng sẽ test mức Fib 50% &ndash; 4.040 USD, Fib 61.8% &ndash; 4.062 USD hoặc Fib 78.6% ở mức 4.100 USD v&agrave; mức cao nhất l&agrave; 4.168 USD.</p>\r\n\r\n<p>Đường SMA 100 nằm tr&ecirc;n đường SMA 200 d&agrave;i hạn chỉ ra rằng con đường &iacute;t kh&aacute;ng cự nhất l&agrave; tăng l&ecirc;n. Tuy nhi&ecirc;n, khoảng c&aacute;ch giữa c&aacute;c chỉ số đ&atilde; thu hẹp đủ để b&aacute;o hiệu đ&agrave; tăng đang dần yếu hơn v&agrave; sự giao nhau trong xu hướng giảm. Trong trường hợp đ&oacute;, gi&aacute; c&oacute; thể quay trở lại mức thấp 3.770 USD.</p>\r\n\r\n<p>RSI dường như đang đi về v&ugrave;ng qu&aacute; b&aacute;n ngay cả khi chưa chạm v&agrave;o v&ugrave;ng qu&aacute; mua, cho thấy b&ecirc;n b&aacute;n đang rất muốn quay trở lại. Stochastic cũng đang đi xuống v&ugrave;ng thấp hơn, điều đ&oacute; cho thấy gấu tr&agrave;n trề sức mạnh v&agrave; c&oacute; thể đưa Bitcoin về c&aacute;c mức thấp.</p>\r\n\r\n<p><img height=\"625\" sizes=\"(max-width: 1048px) 100vw, 1048px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/26-2.jpg\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/26-2.jpg 1048w, https://blogtien.com/wp-content/uploads/2019/03/26-2-300x179.jpg 300w, https://blogtien.com/wp-content/uploads/2019/03/26-2-1024x611.jpg 1024w, https://blogtien.com/wp-content/uploads/2019/03/26-2-768x458.jpg 768w\" width=\"1048\" /></p>\r\n\r\n<p>C&aacute;c trader dường như l&agrave; nh&acirc;n tố ch&iacute;nh giữ c&aacute;c chất x&uacute;c t&aacute;c để x&aacute;c nhận rằng đợt tăng gi&aacute; n&agrave;y vẫn chưa kết th&uacute;c.</p>\r\n\r\n<p>Hiện tại, trọng t&acirc;m dường như tập trung v&agrave;o việc khối lượng giao dịch đang giảm. Theo CoinMetrics.io, sự sụt giảm số lượng giao dịch Bitcoin đ&atilde; tr&ugrave;ng khớp với thời điểm kết th&uacute;c giai đoạn testnet của VeriBlock. Những t&agrave;i khoản của startup n&agrave;y chiếm 25-40% giao dịch Bitcoin, đ&acirc;y l&agrave; một số tiền kh&aacute; lớn đối với quy m&ocirc; của n&oacute;. Thử nghiệm kết th&uacute;c v&agrave;o ng&agrave;y 4 th&aacute;ng 3 v&agrave; số lượng giao dịch h&agrave;ng ng&agrave;y giảm 21% sau đ&oacute;.</p>', '95_Phân Tích Thị Trường1.jpg', '6', '07-08-2021', '2021-08-06 11:35:46', '2021-08-06 11:35:46'),
(23, 'Phân tích giá 12/03: Bitcoin, Ethereum, Ripple, Litecoin, EOS, Bitcoin Cash, Binance Coin, Stellar, TRON, Bitcoin SV.', '136', '<p>Động th&aacute;i tăng gi&aacute; của Bitcoin kh&ocirc;ng hề thuyết phục. N&oacute; thậm ch&iacute; kh&ocirc;ng thể th&aacute;ch thức mức kh&aacute;ng cự 4.000 USD v&agrave;o ng&agrave;y 9 th&aacute;ng 3. Hiện tại, gi&aacute; đ&atilde; quay trở lại EMA 20 ng&agrave;y, đ&acirc;y l&agrave; một v&ugrave;ng hỗ trợ quan trọng. Một sự phục hồi từ ngưỡng hỗ trợ n&agrave;y sẽ một lần nữa gi&uacute;p đồng coin cố gắng tho&aacute;t ra khỏi 4.000 USD v&agrave; đạt đến ngưỡng kh&aacute;ng cự quan trọng 4.255 USD.</p>', '<p dir=\"ltr\"><strong>Đ&agrave; giảm đ&atilde; xuất hiện v&agrave; phe b&ograve; tiếp tục r&uacute;t lui để nhường chỗ cho những ch&uacute; gấu, c&aacute;c đề xuất đ&atilde; c&oacute; sự điều chỉnh mới. Sau đ&acirc;y h&atilde;y c&ugrave;ng Blogtien đến với b&agrave;i ph&acirc;n t&iacute;ch gi&aacute; h&ocirc;m nay (12/03) nh&eacute;.</strong></p>\r\n\r\n<ul>\r\n	<li><a href=\"https://blogtien.com/phan-tich-gia-09-03-bitcoin-ethereum-ripple-eos-litecoin-bitcoin-cash-binance-coin-stellar-tron-bitcoin-sv/\" rel=\"noopener noreferrer\" target=\"_blank\"><strong>Ph&acirc;n t&iacute;ch gi&aacute; 09/03: Bitcoin, Ethereum, Ripple, EOS, Litecoin, Bitcoin Cash, Binance Coin, Stellar, TRON, Bitcoin SV.</strong></a></li>\r\n	<li><strong><a href=\"https://blogtien.com/phan-tich-gia-07-03-bitcoin-ethereum-ripple-eos-litecoin-bitcoin-cash-binance-coin-stellar-tron-bitcoin-sv/\" rel=\"noopener noreferrer\" target=\"_blank\">Ph&acirc;n t&iacute;ch gi&aacute; 07/03: Bitcoin, Ethereum, Ripple, EOS, Litecoin, Bitcoin Cash, Binance Coin, Stellar, Tron, Bitcoin SV.</a></strong></li>\r\n	<li><strong><a href=\"https://blogtien.com/phan-tich-gia-05-03-bitcoin-ethereum-ripple-eos-litecoin-bitcoin-cash-binance-coin-stellar-tron-bitcoin-sv/\" rel=\"noopener noreferrer\" target=\"_blank\">Ph&acirc;n t&iacute;ch gi&aacute; 05/03: Bitcoin, Ethereum, Ripple, EOS, Litecoin, Bitcoin Cash, Binance Coin, Stellar, Tron, Bitcoin SV.</a></strong></li>\r\n</ul>\r\n\r\n<h2><strong>BTC/USD</strong></h2>\r\n\r\n<p dir=\"ltr\">Động th&aacute;i tăng gi&aacute; của Bitcoin kh&ocirc;ng hề thuyết phục. N&oacute; thậm ch&iacute; kh&ocirc;ng thể th&aacute;ch thức mức kh&aacute;ng cự 4.000 USD v&agrave;o ng&agrave;y 9 th&aacute;ng 3. Điều n&agrave;y cho thấy sự đuối sức của những ch&uacute; b&ograve;. Hiện tại, gi&aacute; đ&atilde; quay trở lại EMA 20 ng&agrave;y, đ&acirc;y l&agrave; một v&ugrave;ng hỗ trợ quan trọng. Nếu phe b&ograve; đực muốn giữ lợi thế th&igrave; họ n&ecirc;n bảo vệ EMA 20 ng&agrave;y. Một sự phục hồi từ ngưỡng hỗ trợ n&agrave;y sẽ một lần nữa gi&uacute;p đồng coin cố gắng tho&aacute;t ra khỏi 4.000 USD v&agrave; đạt đến ngưỡng kh&aacute;ng cự quan trọng 4.255 USD.</p>\r\n\r\n<p dir=\"ltr\"><img height=\"501\" sizes=\"(max-width: 1234px) 100vw, 1234px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/11-1.png\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/11-1.png 1234w, https://blogtien.com/wp-content/uploads/2019/03/11-1-300x122.png 300w, https://blogtien.com/wp-content/uploads/2019/03/11-1-1024x416.png 1024w, https://blogtien.com/wp-content/uploads/2019/03/11-1-768x312.png 768w\" width=\"1234\" /></p>\r\n\r\n<p dir=\"ltr\">Nếu cặp BTC/USD duy tr&igrave; tr&ecirc;n 4.255 USD, n&oacute; sẽ ho&agrave;n th&agrave;nh m&ocirc; h&igrave;nh đ&aacute;y đ&ocirc;i với mục ti&ecirc;u l&agrave; 5.273 USD. Trader c&oacute; thể thiết lập c&aacute;c vị thế d&agrave;i tr&ecirc;n một breakout tr&ecirc;n 4.255 USD. Cho đến l&uacute;c đ&oacute;, c&aacute;c trader n&ecirc;n duy tr&igrave; c&aacute;c điểm dừng lỗ tại 3.236 USD.</p>\r\n\r\n<p dir=\"ltr\">Về ph&iacute;a giảm điểm, ở đ&oacute; c&oacute; sự hỗ trợ tại đường EMA 20 ng&agrave;y v&agrave; b&ecirc;n dưới đường n&agrave;y l&agrave; đường xu hướng tăng. Nếu hỗ trợ n&agrave;y bị ph&aacute; vỡ, th&igrave; ngưỡng hỗ trợ tiếp theo sẽ l&agrave; tại SMA 50 ng&agrave;y. Nếu mức hỗ trợ n&agrave;y cũng bị ph&aacute; vỡ, ch&uacute;ng t&ocirc;i dự đo&aacute;n đồng coin sẽ trượt nhanh đến v&ugrave;ng hỗ trợ cuối c&ugrave;ng l&agrave; 3.355 &ndash; 3.236 USD. C&aacute;c h&agrave;nh động gi&aacute; mờ nhạt chỉ ra rằng Bitcoin sẽ bị r&agrave;ng buộc trong phạm vi hẹp trong ngắn hạn.</p>\r\n\r\n<h2><strong>ETH/USD</strong></h2>\r\n\r\n<p dir=\"ltr\">Ethereum đ&atilde; ph&aacute; vỡ đường EMA 20 ng&agrave;y. Nếu kh&ocirc;ng t&igrave;m thấy sự hỗ trợ ở mức 134 USD, n&oacute; c&oacute; thể trượt đến SMA 50 ng&agrave;y. Dưới mức n&agrave;y, đồng coin c&oacute; thể điều chỉnh theo mức hỗ trợ quan trọng tại 116 USD. Do đ&oacute;, c&aacute;c trader c&oacute; thể giữ mức dừng lỗ tr&ecirc;n c&aacute;c vị thế d&agrave;i c&ograve;n lại tại 125 USD.</p>\r\n\r\n<p dir=\"ltr\"><img height=\"501\" sizes=\"(max-width: 1234px) 100vw, 1234px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/12-1.png\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/12-1.png 1234w, https://blogtien.com/wp-content/uploads/2019/03/12-1-300x122.png 300w, https://blogtien.com/wp-content/uploads/2019/03/12-1-1024x416.png 1024w, https://blogtien.com/wp-content/uploads/2019/03/12-1-768x312.png 768w\" width=\"1234\" /></p>\r\n\r\n<p dir=\"ltr\">Nếu cặp ETH/USD tho&aacute;t khỏi 134 USD hoặc SMA 50 ng&agrave;y, n&oacute; sẽ cố gắng đẩy gi&aacute; l&ecirc;n tr&ecirc;n 144 USD. Cặp coin c&oacute; khả năng lấy đ&agrave; tăng trưởng mới sau khi gi&aacute; duy tr&igrave; tr&ecirc;n ngưỡng n&agrave;y.</p>\r\n\r\n<p dir=\"ltr\">Tuy nhi&ecirc;n, với việc cả hai đường trung b&igrave;nh động đều bằng phẳng v&agrave; chỉ số RSI gần điểm giữa th&igrave; xu hướng trong ngắn hạn sẽ l&agrave; củng cố. Mức kh&aacute;ng cự của phạm vi c&oacute; thể nằm ngay tại đ&acirc;y 144 USD, trong khi mức hỗ trợ ch&uacute;ng ta sẽ xem x&eacute;t trong v&agrave;i ng&agrave;y tới.</p>\r\n\r\n<h2><strong>XRP/USD</strong></h2>\r\n\r\n<p dir=\"ltr\">Ripple đ&atilde; bị giới hạn trong phạm vi 0.275 &ndash; 0.331 USD. C&aacute;c đường trung b&igrave;nh động bằng phẳng v&agrave; chỉ số RSI gần 50 cho thấy sự hợp nhất c&oacute; thể sẽ tiếp tục trong v&agrave;i ng&agrave;y nữa.</p>\r\n\r\n<p dir=\"ltr\"><img height=\"501\" sizes=\"(max-width: 1234px) 100vw, 1234px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/13.png\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/13.png 1234w, https://blogtien.com/wp-content/uploads/2019/03/13-300x122.png 300w, https://blogtien.com/wp-content/uploads/2019/03/13-1024x416.png 1024w, https://blogtien.com/wp-content/uploads/2019/03/13-768x312.png 768w\" width=\"1234\" /></p>\r\n\r\n<p dir=\"ltr\">Một breakout tại phạm vi tr&ecirc;n sẽ đẩy cặp XRP/USD về ph&iacute;a ngưỡng kh&aacute;ng cự của k&ecirc;nh giảm dần. Nếu gi&aacute; duy tr&igrave; tr&ecirc;n k&ecirc;nh, n&oacute; sẽ cho thấy một sự đảo chiều trong xu hướng.</p>\r\n\r\n<p dir=\"ltr\">Tuy nhi&ecirc;n, nếu phe gấu nhấn ch&igrave;m đồng coin dưới 0.275 USD, n&oacute; c&oacute; thể giảm xuống mức thấp h&agrave;ng năm l&agrave; 0.245 USD. Do đ&oacute;, c&aacute;c trader c&oacute; thể giữ điểm dừng lỗ ngay dưới 0.275 USD.</p>\r\n\r\n<h2><strong>LTC/USD</strong></h2>\r\n\r\n<p dir=\"ltr\">Litecoin đ&atilde; một lần nữa đột ph&aacute; l&ecirc;n 56.91 USD v&agrave;o ng&agrave;y 9 th&aacute;ng 3, nhưng phe b&ograve; kh&ocirc;ng thể tận dụng cơ hội. Một lần nữa, gi&aacute; đ&atilde; điều chỉnh trở lại dưới 56.91 USD, đ&acirc;y l&agrave; một dấu hiệu ti&ecirc;u cực.</p>\r\n\r\n<p dir=\"ltr\"><img height=\"501\" sizes=\"(max-width: 1234px) 100vw, 1234px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/14-1.png\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/14-1.png 1234w, https://blogtien.com/wp-content/uploads/2019/03/14-1-300x122.png 300w, https://blogtien.com/wp-content/uploads/2019/03/14-1-1024x416.png 1024w, https://blogtien.com/wp-content/uploads/2019/03/14-1-768x312.png 768w\" width=\"1234\" /></p>\r\n\r\n<p dir=\"ltr\">Phe gấu đang cố gắng đưa cặp LTC/USD về EMA 20 ng&agrave;y, đ&acirc;y l&agrave; một v&ugrave;ng hỗ trợ quan trọng. Nếu cặp coin tho&aacute;t ra khỏi ngưỡng hỗ trợ n&agrave;y, phe b&ograve; sẽ đẩy gi&aacute; l&ecirc;n 60 USD. Mục ti&ecirc;u cần theo d&otilde;i ở đ&agrave; tăng l&agrave; 65.56 USD. Đường trung b&igrave;nh động tăng dần v&agrave; chỉ số RSI nằm trong v&ugrave;ng t&iacute;ch cực cho thấy b&ograve; đang c&oacute; lợi thế.</p>\r\n\r\n<p dir=\"ltr\">Tuy nhi&ecirc;n, RSI lại đang hướng đến v&ugrave;ng ti&ecirc;u cực l&agrave; điều khiến ch&uacute;ng t&ocirc;i lo lắng. Do đ&oacute;, ch&uacute;ng t&ocirc;i đề nghị c&aacute;c trader giữ điểm dừng lỗ ở mức 50 USD. Nếu phe gấu th&agrave;nh c&ocirc;ng đưa đồng tiền kỹ thuật số xuống dưới đường EMA 20 ng&agrave;y, n&oacute; c&oacute; thể trượt xuống 47.24 USD v&agrave; thấp hơn nữa l&agrave; đến SMA 50 ng&agrave;y.</p>\r\n\r\n<h2><strong>EOS/USD</strong></h2>\r\n\r\n<p dir=\"ltr\">EOS đ&atilde; kh&ocirc;ng thể tiến l&ecirc;n mức kh&aacute;ng cự tr&ecirc;n 3.87 USD. Sự thất bại của phe b&ograve; trong việc tăng l&ecirc;n tr&ecirc;n 3.87 USD l&agrave; một dấu hiệu ti&ecirc;u cực. Hiện tại, gi&aacute; đ&atilde; điều chỉnh về EMA 20 ng&agrave;y. Nếu hỗ trợ n&agrave;y được giữ, th&igrave; phe b&ograve; sẽ cố gắng đẩy đồng coin l&ecirc;n tr&ecirc;n 3.87 USD một lần nữa.</p>\r\n\r\n<p dir=\"ltr\"><img height=\"501\" sizes=\"(max-width: 1234px) 100vw, 1234px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/15-1.png\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/15-1.png 1234w, https://blogtien.com/wp-content/uploads/2019/03/15-1-300x122.png 300w, https://blogtien.com/wp-content/uploads/2019/03/15-1-1024x416.png 1024w, https://blogtien.com/wp-content/uploads/2019/03/15-1-768x312.png 768w\" width=\"1234\" /></p>\r\n\r\n<p dir=\"ltr\">Tuy nhi&ecirc;n, nếu EMA 20 ng&agrave;y bị ph&aacute; vỡ, cặp EOS/USD c&oacute; thể giảm xuống mức hỗ trợ tiếp theo l&agrave; 3.15 USD. SMA 50 ng&agrave;y cũng gần với mức n&agrave;y; do đ&oacute;, ch&uacute;ng t&ocirc;i hy vọng phe b&ograve; sẽ bảo vệ được ngưỡng n&agrave;y.</p>\r\n\r\n<p dir=\"ltr\">Thất bại trong việc tho&aacute;t khỏi mức hỗ trợ tại SMA 50 ng&agrave;y c&oacute; thể dẫn đến việc giảm xuống c&ograve;n 2.17 USD. Do đ&oacute;, c&aacute;c trader c&oacute; thể giữ c&aacute;c điểm dừng lỗ ở mức 3.10 USD.</p>\r\n\r\n<h2><strong>BCH/USD</strong></h2>\r\n\r\n<p dir=\"ltr\">Bitcoin Cash tiếp tục giao dịch trong phạm vi hẹp 120-140 USD. Điều n&agrave;y cho thấy sự c&acirc;n bằng giữa cung v&agrave; cầu.&nbsp;C&aacute;c đường trung b&igrave;nh động bằng phẳng v&agrave; chỉ số RSI gần v&ugrave;ng giữa cho thấy sự củng cố.</p>\r\n\r\n<p dir=\"ltr\"><img height=\"501\" sizes=\"(max-width: 1234px) 100vw, 1234px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/16.png\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/16.png 1234w, https://blogtien.com/wp-content/uploads/2019/03/16-300x122.png 300w, https://blogtien.com/wp-content/uploads/2019/03/16-1024x416.png 1024w, https://blogtien.com/wp-content/uploads/2019/03/16-768x312.png 768w\" width=\"1234\" /></p>\r\n\r\n<p dir=\"ltr\">Một đ&agrave; đột ph&aacute; hoặc ph&aacute; vỡ trong phạm vi n&agrave;y sẽ bắt đầu một xu hướng mới cho cặp BCH/USD. Tr&ecirc;n 140 USD, đ&agrave; tăng sẽ tiến đến khu vực tiếp theo l&agrave; 157 v&agrave; 163 USD. Nếu phạm vi n&agrave;y cũng vượt qua th&igrave; BCH c&oacute; thể di chuyển l&ecirc;n đến 175 USD. Do đ&oacute;, c&aacute;c trader c&oacute; thể duy tr&igrave; c&aacute;c điểm dừng lỗ tại 116 USD. Nếu phe gấu nhấn ch&igrave;m đồng coin dưới 120 USD, n&oacute; c&oacute; thể giảm xuống c&ograve;n 105 USD v&agrave; c&oacute; nguy cơ giảm về đến mức thấp h&agrave;ng năm l&agrave; 73.50 USD.</p>\r\n\r\n<h2><strong>BNB/USD</strong></h2>\r\n\r\n<p dir=\"ltr\">Binance Coin đ&atilde; củng cố kể từ khi tho&aacute;t ra khỏi 15 USD v&agrave;o ng&agrave;y 7 th&aacute;ng 3.</p>\r\n\r\n<p dir=\"ltr\"><img height=\"501\" sizes=\"(max-width: 1234px) 100vw, 1234px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/17-1.png\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/17-1.png 1234w, https://blogtien.com/wp-content/uploads/2019/03/17-1-300x122.png 300w, https://blogtien.com/wp-content/uploads/2019/03/17-1-1024x416.png 1024w, https://blogtien.com/wp-content/uploads/2019/03/17-1-768x312.png 768w\" width=\"1234\" /></p>\r\n\r\n<p dir=\"ltr\">Sau khi tăng mạnh, cặp BNB/USD đ&atilde; h&igrave;nh th&agrave;nh một cờ hiệu. Một sự đột ph&aacute; tại đ&acirc;y c&oacute; khả năng tiếp tục xu hướng tăng, với c&aacute;c mục ti&ecirc;u từ 18 USD trở l&ecirc;n. Do đ&oacute;, c&aacute;c trader c&oacute; thể giữ c&aacute;c vị thế d&agrave;i c&ograve;n lại với mức dừng lỗ tại mức h&ograve;a vốn.</p>\r\n\r\n<p dir=\"ltr\">Một đ&agrave; ph&aacute; vỡ tại đ&acirc;y sẽ b&aacute;o hiệu việc điều chỉnh s&acirc;u hơn. Mặc d&ugrave; mục ti&ecirc;u của đ&agrave; ph&aacute; vỡ l&agrave; 9 USD, nhưng ch&uacute;ng t&ocirc;i dự đo&aacute;n sẽ c&oacute; một sự hỗ trợ mạnh mẽ tại EMA 20 ng&agrave;y. Nhưng nếu mức n&agrave;y cũng kh&ocirc;ng giữ được, việc rơi xuống SMA 50 ng&agrave;y l&agrave; c&oacute; thể xảy ra.</p>\r\n\r\n<h2><strong>XLM/USD</strong></h2>\r\n\r\n<p dir=\"ltr\">Stellar đ&atilde; k&iacute;ch hoạt khuyến nghị mua của ch&uacute;ng t&ocirc;i trong ph&acirc;n t&iacute;ch trước. B&acirc;y giờ n&oacute; sẽ di chuyển l&ecirc;n mức kh&aacute;ng cự tiếp theo l&agrave; 0.342 USD.</p>\r\n\r\n<p dir=\"ltr\"><img height=\"501\" sizes=\"(max-width: 1234px) 100vw, 1234px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/18.png\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/18.png 1234w, https://blogtien.com/wp-content/uploads/2019/03/18-300x122.png 300w, https://blogtien.com/wp-content/uploads/2019/03/18-1024x416.png 1024w, https://blogtien.com/wp-content/uploads/2019/03/18-768x312.png 768w\" width=\"1234\" /></p>\r\n\r\n<p dir=\"ltr\">C&aacute;c đường trung b&igrave;nh động đ&atilde; ho&agrave;n th&agrave;nh một sự giao nhau trong xu hướng tăng, đ&oacute; l&agrave; một dấu hiệu tăng gi&aacute;.&nbsp;Đường EMA 20 ng&agrave;y đ&atilde; bắt đầu c&oacute; xu hướng tăng v&agrave; chỉ số RSI cũng tăng gần với v&ugrave;ng qu&aacute; mua. Điều n&agrave;y cho thấy phe b&ograve; c&oacute; lợi thế trong thời gian tới. Cặp&nbsp;XLM/USD c&oacute; thể phải đối mặt với mức kh&aacute;ng cự tại 0.116 USD, nhưng ch&uacute;ng t&ocirc;i hy vọng mức n&agrave;y sẽ được ph&aacute; vỡ.</p>\r\n\r\n<p dir=\"ltr\">Quan điểm tăng gi&aacute; tr&ecirc;n của ch&uacute;ng t&ocirc;i sẽ bị v&ocirc; hiệu nếu gi&aacute; giảm từ một trong những mức kh&aacute;ng cự ph&iacute;a tr&ecirc;n v&agrave; giảm mạnh xuống dưới 0.082 USD. Do đ&oacute;, c&aacute;c trader c&oacute; thể giữ mức dừng lỗ tại 0.08 USD. Ngo&agrave;i ra c&oacute; thể tăng mức dừng lỗ khi gi&aacute; di chuyển l&ecirc;n cao hơn.</p>\r\n\r\n<h2><strong>TRX/USD</strong></h2>\r\n\r\n<p dir=\"ltr\">Tron đang vật lộn để tho&aacute;t khỏi mức hỗ trợ quan trọng ở mức 0.023 USD. Điều n&agrave;y cho thấy sự thiếu hụt người mua ngay cả ở c&aacute;c ngưỡng n&agrave;y. Đường EMA 20 ng&agrave;y đang dốc xuống v&agrave; chỉ số RSI đang suy yếu trong v&ugrave;ng ti&ecirc;u cực, điều n&agrave;y cho thấy gấu đang c&oacute; lợi thế hơn.</p>\r\n\r\n<p dir=\"ltr\"><img height=\"501\" sizes=\"(max-width: 1234px) 100vw, 1234px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/19-1.png\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/19-1.png 1234w, https://blogtien.com/wp-content/uploads/2019/03/19-1-300x122.png 300w, https://blogtien.com/wp-content/uploads/2019/03/19-1-1024x416.png 1024w, https://blogtien.com/wp-content/uploads/2019/03/19-1-768x312.png 768w\" width=\"1234\" /></p>\r\n\r\n<p dir=\"ltr\">Một đ&agrave; ph&aacute; vỡ dưới 0.230 USD sẽ t&igrave;m thấy sự hỗ trợ ở mức 0.020 USD. Nhưng nếu mức n&agrave;y cũng bị ph&aacute; vỡ, đ&agrave; trượt c&oacute; thể đi s&acirc;u tới 0.008 USD, đ&acirc;y l&agrave; một mức hỗ trợ quan trọng.</p>\r\n\r\n<p dir=\"ltr\">Mặt kh&aacute;c, nếu cặp TRX/USD phục hồi mạnh từ mức hiện tại v&agrave; tăng l&ecirc;n tr&ecirc;n cả hai đường trung b&igrave;nh động, n&oacute; c&oacute; thể sẽ leo l&ecirc;n mức 0.028 USD. Ch&uacute;ng t&ocirc;i sẽ c&oacute; đề xuất kh&aacute;c nếu đồng coin ph&aacute; vỡ được phạm vi hiện tại.</p>\r\n\r\n<h2><strong>BSV/USD</strong></h2>\r\n\r\n<p dir=\"ltr\">Bitcoin SV đ&atilde; củng cố trong khoảng từ 71.41 đến 65.03 USD trong năm ng&agrave;y qua. Cả hai đường trung b&igrave;nh động đ&atilde; bị san phẳng v&agrave; chỉ số RSI cũng gần với điểm giữa. Điều n&agrave;y cho thấy sự c&acirc;n bằng giữa phe b&ograve; v&agrave; phe gấu.</p>\r\n\r\n<p dir=\"ltr\"><img height=\"501\" sizes=\"(max-width: 1234px) 100vw, 1234px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/20-1.png\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/20-1.png 1234w, https://blogtien.com/wp-content/uploads/2019/03/20-1-300x122.png 300w, https://blogtien.com/wp-content/uploads/2019/03/20-1-1024x416.png 1024w, https://blogtien.com/wp-content/uploads/2019/03/20-1-768x312.png 768w\" width=\"1234\" /></p>\r\n\r\n<p dir=\"ltr\">Động th&aacute;i tiếp theo sẽ bắt đầu sau khi gi&aacute; tho&aacute;t khỏi phạm vi n&agrave;y. Tr&ecirc;n 71.41 USD, đ&agrave; tăng c&oacute; thể tiến l&ecirc;n 102.58 USD với mức kh&aacute;ng cự nhỏ tại 77.03 USD. Do đ&oacute;, ch&uacute;ng t&ocirc;i sẽ giữ lại khuyến nghị mua được đưa ra trong kỳ ph&acirc;n t&iacute;ch trước.</p>\r\n\r\n<p dir=\"ltr\">Ngược lại, nếu cặp coin giảm xuống dưới v&ugrave;ng hỗ trợ 65.03 &ndash; 58 USD, n&oacute; c&oacute; thể test lại mức thấp 38.52 USD. Do đ&oacute;, c&aacute;c trader n&ecirc;n chờ đợi một breakout được x&aacute;c nhận sau đ&oacute; mới bắt đầu thiết lập c&aacute;c vị thế.</p>', '20_Phân Tích Thị Trường2.jpg', '6', '07-08-2021', '2021-08-06 11:37:56', '2021-08-07 07:16:17'),
(24, 'Phân tích giá 09/03: Bitcoin, Ethereum, Ripple, EOS, Litecoin, Bitcoin Cash, Binance Coin, Stellar, TRON, Bitcoin SV.', '140', '<p>Sau s&aacute;u ng&agrave;y dao động trong phạm vi hẹp, xu hướng đ&atilde; mở rộng sang ph&iacute;a giảm gi&aacute;, đ&oacute; l&agrave; một dấu hiệu ti&ecirc;u cực. Bitcoin đ&atilde; ph&aacute; vỡ đường EMA 20 ng&agrave;y v&agrave; hiện đang ở gần đường SMA 50 ng&agrave;y. Đường xu hướng giảm cũng nằm ở mức n&agrave;y. Nơi n&agrave;y trước đ&acirc;y đ&atilde; hoạt động như một kh&aacute;ng cự mạnh mẽ; do đ&oacute;, b&acirc;y giờ n&oacute; sẽ đ&oacute;ng vai tr&ograve; như l&agrave; một sự hỗ trợ mạnh mẽ.</p>', '<p dir=\"ltr\"><strong>Biển đỏ lại xuất hiện tr&ecirc;n thị trường, c&aacute;c đề xuất theo đ&oacute; đ&atilde; c&oacute; sự điều chỉnh mới. Sau đ&acirc;y h&atilde;y c&ugrave;ng Blogtien đến với b&agrave;i ph&acirc;n t&iacute;ch gi&aacute; h&ocirc;m nay (09/03) nh&eacute;.</strong></p>\r\n\r\n<ul>\r\n	<li><a href=\"https://blogtien.com/phan-tich-gia-07-03-bitcoin-ethereum-ripple-eos-litecoin-bitcoin-cash-binance-coin-stellar-tron-bitcoin-sv/\" rel=\"noopener noreferrer\" target=\"_blank\"><strong>Ph&acirc;n t&iacute;ch gi&aacute; 07/03: Bitcoin, Ethereum, Ripple, EOS, Litecoin, Bitcoin Cash, Binance Coin, Stellar, Tron, Bitcoin SV.</strong></a></li>\r\n	<li><strong><a href=\"https://blogtien.com/phan-tich-gia-05-03-bitcoin-ethereum-ripple-eos-litecoin-bitcoin-cash-binance-coin-stellar-tron-bitcoin-sv/\" rel=\"noopener noreferrer\" target=\"_blank\">Ph&acirc;n t&iacute;ch gi&aacute; 05/03: Bitcoin, Ethereum, Ripple, EOS, Litecoin, Bitcoin Cash, Binance Coin, Stellar, Tron, Bitcoin SV.</a></strong></li>\r\n	<li><strong><a href=\"https://blogtien.com/phan-tich-gia-02-03-bitcoin-ethereum-ripple-eos-litecoin-bitcoin-cash-stellar-tron-binance-coin-bitcoin-sv/\" rel=\"noopener noreferrer\" target=\"_blank\">Ph&acirc;n t&iacute;ch gi&aacute; 02/03: Bitcoin, Ethereum, Ripple, EOS, Litecoin, Bitcoin Cash, Stellar, Tron, Binance Coin, Bitcoin SV.</a></strong></li>\r\n</ul>\r\n\r\n<h2><strong>BTC/USD</strong></h2>\r\n\r\n<p dir=\"ltr\">Sau s&aacute;u ng&agrave;y dao động trong phạm vi hẹp, xu hướng đ&atilde; mở rộng sang ph&iacute;a giảm gi&aacute;, đ&oacute; l&agrave; một dấu hiệu ti&ecirc;u cực. Bitcoin đ&atilde; ph&aacute; vỡ đường EMA 20 ng&agrave;y v&agrave; hiện đang ở gần đường SMA 50 ng&agrave;y. Đường xu hướng giảm cũng nằm ở mức n&agrave;y. Nơi n&agrave;y trước đ&acirc;y đ&atilde; hoạt động như một kh&aacute;ng cự mạnh mẽ; do đ&oacute;, b&acirc;y giờ n&oacute; sẽ đ&oacute;ng vai tr&ograve; như l&agrave; một sự hỗ trợ mạnh mẽ.</p>\r\n\r\n<p dir=\"ltr\"><img height=\"501\" sizes=\"(max-width: 1234px) 100vw, 1234px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/51-1.png\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/51-1.png 1234w, https://blogtien.com/wp-content/uploads/2019/03/51-1-300x122.png 300w, https://blogtien.com/wp-content/uploads/2019/03/51-1-1024x416.png 1024w, https://blogtien.com/wp-content/uploads/2019/03/51-1-768x312.png 768w\" width=\"1234\" /></p>\r\n\r\n<p dir=\"ltr\">Việc ph&aacute; vỡ mức hỗ trợ quan trọng n&agrave;y c&oacute; thể khiến đồng coin giảm mạnh xuống mức hỗ trợ tiếp theo tại 3.355 USD v&agrave; nếu mức n&agrave;y cũng kh&ocirc;ng giữ được, th&igrave; ngưỡng hỗ trợ cuối c&ugrave;ng về ph&iacute;a giảm điểm l&agrave; 3.236 USD. Một mức thấp h&agrave;ng năm mới sẽ tiếp tục k&eacute;o d&agrave;i xu hướng giảm.</p>\r\n\r\n<p dir=\"ltr\">Ngược lại, nếu gi&aacute; t&igrave;m thấy sự hỗ trợ ở mức hiện tại, b&ograve; sẽ lại cố gắng đẩy gi&aacute; l&ecirc;n tr&ecirc;n 3.900 USD v&agrave; 4.255 USD. Một đ&agrave; đột ph&aacute; tr&ecirc;n mức kh&aacute;ng cự quan trọng n&agrave;y sẽ gợi &yacute; rằng đ&aacute;y đ&atilde; được h&igrave;nh th&agrave;nh. V&agrave;i ng&agrave;y tới l&agrave; rất quan trọng. C&aacute;c trader c&oacute; thể duy tr&igrave; mức dừng lỗ tr&ecirc;n c&aacute;c vị thế d&agrave;i dưới 3.236 USD.</p>\r\n\r\n<h2><strong>ETH/USD</strong></h2>\r\n\r\n<p dir=\"ltr\">Ethereum đ&atilde; ph&aacute; vỡ đường EMA 20 ng&agrave;y v&agrave; giảm xuống SMA 50 ng&agrave;y. Việc kh&ocirc;ng thể bật ra khỏi đường EMA 20 ng&agrave;y l&agrave; một dấu hiệu ti&ecirc;u cực. Nếu SMA 50 ng&agrave;y cũng kh&ocirc;ng thể ngăn chặn sự sụp đổ th&igrave; mức hỗ trợ tiếp theo về ph&iacute;a giảm điểm sẽ l&agrave; 116 USD. Do đ&oacute;, c&aacute;c trader n&ecirc;n giữ mức dừng lỗ 125 USD tr&ecirc;n c&aacute;c vị thế d&agrave;i c&ograve;n lại.</p>\r\n\r\n<p dir=\"ltr\"><img height=\"501\" sizes=\"(max-width: 1234px) 100vw, 1234px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/52-1.png\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/52-1.png 1234w, https://blogtien.com/wp-content/uploads/2019/03/52-1-300x122.png 300w, https://blogtien.com/wp-content/uploads/2019/03/52-1-1024x416.png 1024w, https://blogtien.com/wp-content/uploads/2019/03/52-1-768x312.png 768w\" width=\"1234\" /></p>\r\n\r\n<p dir=\"ltr\">Tr&aacute;i với giả định tr&ecirc;n, nếu ETH/USD tăng trở lại từ mức hiện tại, n&oacute; sẽ cố gắng tăng l&ecirc;n tr&ecirc;n 145 USD v&agrave; đạt đến ngưỡng kh&aacute;ng cự quan trọng ở mức 167 USD. Cặp coin sẽ lấy đ&agrave; tr&ecirc;n mức n&agrave;y. Hiện tại, cả hai đường trung b&igrave;nh động đều bằng phẳng v&agrave; chỉ số RSI nằm dưới 50. Điều n&agrave;y cho thấy đồng coin sẽ phải giao dịch trong phạm vi hẹp trong ngắn hạn.</p>\r\n\r\n<h2><strong>XRP/USD</strong></h2>\r\n\r\n<p dir=\"ltr\">Ripple đ&atilde; giảm xuống dưới mức trung b&igrave;nh động. N&oacute; đ&atilde; một lần nữa trượt xuống mức hỗ trợ 0.295 USD. Đ&acirc;y l&agrave; lần thứ tư gi&aacute; giảm xuống mức n&agrave;y. Việc test lại li&ecirc;n tục mức hỗ trợ sẽ l&agrave;m tăng khả năng ch&uacute;ng bị ph&aacute; vỡ. Nếu mức hỗ trợ n&agrave;y bị ph&aacute; vỡ, mức tiếp theo để theo d&otilde;i tr&ecirc;n ph&iacute;a giảm điểm l&agrave; 0.275 USD. Phe b&ograve; đ&atilde; giữ mức hỗ trợ n&agrave;y hai lần, một lần v&agrave;o giữa th&aacute;ng 12 năm ngo&aacute;i v&agrave; một lần v&agrave;o cuối th&aacute;ng 1 năm 2019. Do đ&oacute;, đ&acirc;y l&agrave; một mức quan trọng m&agrave; b&ograve; cần giữ vững. Một đ&agrave; ph&aacute; vỡ dưới mức n&agrave;y c&oacute; thể khiến đồng tiền kỹ thuật số giảm mạnh xuống mức thấp h&agrave;ng năm l&agrave; 0.245 USD.</p>\r\n\r\n<p dir=\"ltr\"><img height=\"501\" sizes=\"(max-width: 1234px) 100vw, 1234px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/53-XRP-1.png\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/53-XRP-1.png 1234w, https://blogtien.com/wp-content/uploads/2019/03/53-XRP-1-300x122.png 300w, https://blogtien.com/wp-content/uploads/2019/03/53-XRP-1-1024x416.png 1024w, https://blogtien.com/wp-content/uploads/2019/03/53-XRP-1-768x312.png 768w\" width=\"1234\" /></p>\r\n\r\n<p dir=\"ltr\">Nếu b&ecirc;n mua xuất hiện ở c&aacute;c mức hiện tại, cặp XRP/USD c&oacute; thể tăng l&ecirc;n đến 0.331 USD. Một sự đột ph&aacute; v&agrave; đ&oacute;ng cửa tr&ecirc;n mức n&agrave;y c&oacute; khả năng đẩy cặp coin đến đường kh&aacute;ng cự của k&ecirc;nh giảm dần, gần 0.40 USD. Do đ&oacute;, c&aacute;c trader n&ecirc;n bảo vệ c&aacute;c vị thế d&agrave;i của m&igrave;nh với mức dừng lỗ dưới 0.275 USD.</p>\r\n\r\n<h2><strong>EOS/USD</strong></h2>\r\n\r\n<p dir=\"ltr\">EOS đ&atilde; ph&aacute; vỡ dưới đường EMA 20 ng&agrave;y v&agrave; giảm mạnh xuống mức hỗ trợ tiếp theo l&agrave; 3.20 USD. Nếu phe b&ograve; kh&ocirc;ng bảo vệ được mức n&agrave;y, đ&agrave; sụt giảm c&oacute; thể k&eacute;o d&agrave;i đến SMA 50 ng&agrave;y. C&aacute;c trader c&oacute; thể giữ điểm dừng lỗ ở mức 2.90 USD tr&ecirc;n c&aacute;c vị thế d&agrave;i c&ograve;n lại.</p>\r\n\r\n<p dir=\"ltr\"><img height=\"501\" sizes=\"(max-width: 1234px) 100vw, 1234px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/54-1.png\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/54-1.png 1234w, https://blogtien.com/wp-content/uploads/2019/03/54-1-300x122.png 300w, https://blogtien.com/wp-content/uploads/2019/03/54-1-1024x416.png 1024w, https://blogtien.com/wp-content/uploads/2019/03/54-1-768x312.png 768w\" width=\"1234\" /></p>\r\n\r\n<p dir=\"ltr\">Việc tho&aacute;t khỏi c&aacute;c mức hiện tại sẽ một lần nữa tiếp th&ecirc;m động lực để phe b&ograve; cố gắng đẩy cặp EOS/USD l&ecirc;n tr&ecirc;n ngưỡng kh&aacute;ng cự 3.87 USD. Đường EMA 20 ng&agrave;y đang bị san phẳng v&agrave; chỉ số RSI cũng đ&atilde; giảm xuống gần khu vực trung lập. Điều n&agrave;y cho thấy sự c&acirc;n bằng giữa cung v&agrave; cầu.</p>\r\n\r\n<h2><strong>LTC/USD</strong></h2>\r\n\r\n<p dir=\"ltr\">Litecoin đ&atilde; tăng tr&ecirc;n 47.24 USD v&agrave;o ng&agrave;y 2 th&aacute;ng 3 nhưng những ch&uacute; b&ograve; đ&atilde; kh&ocirc;ng thể duy tr&igrave; tại mức cao hơn. Gi&aacute; đ&atilde; một lần nữa điều chỉnh theo EMA 20 ng&agrave;y, tạo th&agrave;nh một m&ocirc; h&igrave;nh đầu-vai, n&oacute; sẽ ho&agrave;n th&agrave;nh trong một đ&agrave; ph&aacute; vỡ v&agrave; đ&oacute;ng cửa b&ecirc;n dưới đường viền cổ. Đ&agrave; ph&aacute; vỡ đ&oacute; c&oacute; mục ti&ecirc;u 32 USD; tuy nhi&ecirc;n, mức 40.42 USD, SMA 50 ng&agrave;y v&agrave; đường xu hướng tăng c&oacute; thể đ&oacute;ng vai tr&ograve; l&agrave; sự hỗ trợ quan trọng.</p>\r\n\r\n<p dir=\"ltr\"><img height=\"501\" sizes=\"(max-width: 1234px) 100vw, 1234px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/55-1.png\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/55-1.png 1234w, https://blogtien.com/wp-content/uploads/2019/03/55-1-300x122.png 300w, https://blogtien.com/wp-content/uploads/2019/03/55-1-1024x416.png 1024w, https://blogtien.com/wp-content/uploads/2019/03/55-1-768x312.png 768w\" width=\"1234\" /></p>\r\n\r\n<p dir=\"ltr\">Đường EMA 20 ng&agrave;y đang bị san phẳng v&agrave; chỉ số RSI cũng đ&atilde; giảm xuống v&ugrave;ng giữa. Điều n&agrave;y cho thấy phe b&ograve; đực đang mất dần lợi thế trong ngắn hạn. Nhưng nếu cặp LTC/USD tăng trở lại từ EMA 20 ng&agrave;y hoặc từ đường viền cổ, n&oacute; sẽ cố gắng mở rộng l&ecirc;n tr&ecirc;n 50 USD v&agrave; k&eacute;o d&agrave;i sự phục hồi. C&aacute;c trader c&oacute; thể giữ mức dừng lỗ tại 40 USD tr&ecirc;n c&aacute;c vị thế d&agrave;i c&ograve;n lại.</p>\r\n\r\n<h2><strong>BCH/USD</strong></h2>\r\n\r\n<p dir=\"ltr\">Bitcoin Cash kh&ocirc;ng thể tho&aacute;t ra khỏi đường EMA 20 ng&agrave;y trong năm ng&agrave;y qua, điều n&agrave;y đ&atilde; thu h&uacute;t b&ecirc;n b&aacute;n. Gi&aacute; đ&atilde; ph&aacute; vỡ SMA 50 ng&agrave;y, đ&oacute; l&agrave; một dấu hiệu giảm gi&aacute;. B&acirc;y giờ n&oacute; c&oacute; thể giảm xuống 116 USD v&agrave; dưới mức 105 USD. C&aacute;c trader c&oacute; vị thế d&agrave;i c&oacute; thể giữ mức dừng lỗ tại 116 USD.</p>\r\n\r\n<p dir=\"ltr\"><img height=\"501\" sizes=\"(max-width: 1234px) 100vw, 1234px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/56-BCH.png\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/56-BCH.png 1234w, https://blogtien.com/wp-content/uploads/2019/03/56-BCH-300x122.png 300w, https://blogtien.com/wp-content/uploads/2019/03/56-BCH-1024x416.png 1024w, https://blogtien.com/wp-content/uploads/2019/03/56-BCH-768x312.png 768w\" width=\"1234\" /></p>\r\n\r\n<p dir=\"ltr\">Quan điểm tr&ecirc;n sẽ bị v&ocirc; hiệu nếu cặp BCH/USD đảo ngược hướng từ mức hiện tại v&agrave; vượt ra khỏi 140 USD. Cho đến l&uacute;c đ&oacute;, mọi sự tăng trưởng c&oacute; khả năng thu h&uacute;t b&ecirc;n b&aacute;n. Chỉ số RSI nằm trong v&ugrave;ng &acirc;m nhưng c&aacute;c đường trung b&igrave;nh động vẫn kh&ocirc;ng thay đổi, điều n&agrave;y chỉ ra sự củng cố ngắn hạn.</p>\r\n\r\n<h2><strong>BNB/USD</strong></h2>\r\n\r\n<p dir=\"ltr\">Binance Coin đ&atilde; đ&oacute;ng cửa tr&ecirc;n 12 USD v&agrave;o ng&agrave;y 2 th&aacute;ng 3.&nbsp;Tuy nhi&ecirc;n, mức đ&oacute;ng cửa tr&ecirc;n 12 USD đ&atilde; thu h&uacute;t b&ecirc;n b&aacute;n khiến đồng coin bị k&eacute;o trở lại v&ugrave;ng hỗ trợ quan trọng từ 10 đến 12 USD.</p>\r\n\r\n<p dir=\"ltr\"><img height=\"501\" sizes=\"(max-width: 1234px) 100vw, 1234px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/57-1.png\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/57-1.png 1234w, https://blogtien.com/wp-content/uploads/2019/03/57-1-300x122.png 300w, https://blogtien.com/wp-content/uploads/2019/03/57-1-1024x416.png 1024w, https://blogtien.com/wp-content/uploads/2019/03/57-1-768x312.png 768w\" width=\"1234\" /></p>\r\n\r\n<p dir=\"ltr\">Cả hai đường trung b&igrave;nh động đều dốc l&ecirc;n v&agrave; chỉ số RSI nằm trong v&ugrave;ng dương, điều n&agrave;y cho thấy xu hướng đang tăng. Hỗ trợ đầu ti&ecirc;n về ph&iacute;a giảm điểm l&agrave; EMA 20 ng&agrave;y v&agrave; dưới 9.24 USD. Nếu c&aacute;c mức hỗ trợ n&agrave;y cũng kh&ocirc;ng giữ được, đ&agrave; trượt c&oacute; thể mở rộng đến SMA 50 ng&agrave;y.</p>\r\n\r\n<p dir=\"ltr\">Ngược lại, nếu cặp BNB/USD tho&aacute;t ra khỏi đường EMA 20 ng&agrave;y v&agrave; vượt ra khỏi mức 12 USD, n&oacute; c&oacute; thể tăng l&ecirc;n 15 USD v&agrave; cao hơn nữa l&agrave; 18 USD. Do đ&oacute;, c&aacute;c trader c&oacute; c&aacute;c vị thế d&agrave;i c&oacute; thể giữ mức dừng lỗ tại 9 USD.</p>\r\n\r\n<h2><strong>XLM/USD</strong></h2>\r\n\r\n<p dir=\"ltr\">Stellar đ&atilde; cố gắng tho&aacute;t ra khỏi ngưỡng kh&aacute;ng cự tr&ecirc;n 0.092 USD v&agrave;o ng&agrave;y 3 th&aacute;ng 3 nhưng kh&ocirc;ng th&agrave;nh c&ocirc;ng. Hiện tại, gi&aacute; đ&atilde; quay v&ograve;ng v&agrave; ph&aacute; vỡ dưới mức trung b&igrave;nh động. Việc ph&aacute; vỡ dưới 0.081 USD c&oacute; thể dẫn đến việc test lại c&aacute;c mức thấp h&agrave;ng năm.</p>\r\n\r\n<p dir=\"ltr\"><img height=\"501\" sizes=\"(max-width: 1234px) 100vw, 1234px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/58-BNB.png\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/58-BNB.png 1234w, https://blogtien.com/wp-content/uploads/2019/03/58-BNB-300x122.png 300w, https://blogtien.com/wp-content/uploads/2019/03/58-BNB-1024x416.png 1024w, https://blogtien.com/wp-content/uploads/2019/03/58-BNB-768x312.png 768w\" width=\"1234\" /></p>\r\n\r\n<p dir=\"ltr\">Đường EMA 20 ng&agrave;y bằng phẳng v&agrave; SMA 50 ng&agrave;y đang dốc xuống.&nbsp;Chỉ số RSI cũng đ&atilde; giảm xuống dưới 50, điều n&agrave;y cho thấy b&ecirc;n b&aacute;n đang c&oacute; lợi thế.</p>\r\n\r\n<p dir=\"ltr\">Mặt kh&aacute;c, nếu cặp XLM/USD quay v&ograve;ng từ mức hiện tại v&agrave; vượt ra khỏi 0.10 USD. XLM n&agrave;y l&agrave; một coin k&eacute;m hiệu quả v&igrave; n&oacute; kh&ocirc;ng tham gia v&agrave;o đợt pullback gần đ&acirc;y. Điều n&agrave;y cho thấy sự thiếu quan t&acirc;m của những người đang nắm giữ loại tiền điện tử n&agrave;y. Ch&uacute;ng t&ocirc;i sẽ chờ đợi một sự đảo ngược trong xu hướng trước khi c&oacute; đề xuất kh&aacute;c.</p>\r\n\r\n<h2><strong>TRX/USD</strong></h2>\r\n\r\n<p dir=\"ltr\">Tron đ&atilde; ph&aacute; vỡ mức hỗ trợ 0.023 USD v&agrave; đ&oacute;ng cửa gần với mức hỗ trợ tiếp theo tại 0.021 USD. Nếu mức n&agrave;y cũng bị ph&aacute; vỡ, ngưỡng hỗ trợ tiếp theo về ph&iacute;a giảm điểm l&agrave; 0.018 USD. C&aacute;c đường trung b&igrave;nh động đ&atilde; giao nhau ở ph&iacute;a giảm xuống, đ&oacute; l&agrave; một dấu hiệu ti&ecirc;u cực.</p>\r\n\r\n<p dir=\"ltr\"><img height=\"501\" sizes=\"(max-width: 1234px) 100vw, 1234px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/59.png\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/59.png 1234w, https://blogtien.com/wp-content/uploads/2019/03/59-300x122.png 300w, https://blogtien.com/wp-content/uploads/2019/03/59-1024x416.png 1024w, https://blogtien.com/wp-content/uploads/2019/03/59-768x312.png 768w\" width=\"1234\" /></p>\r\n\r\n<p dir=\"ltr\">Nếu cặp TRX/USD giữ tr&ecirc;n 0.013 USD, n&oacute; c&oacute; khả năng k&eacute;o d&agrave;i thời gian lưu lại trong phạm vi v&agrave; c&oacute; thể cho ch&uacute;ng ta một cơ hội giao dịch. Tuy nhi&ecirc;n, nếu phe b&ograve; kh&ocirc;ng bảo vệ được mức hỗ trợ 0.008 USD, th&igrave; việc giảm s&acirc;u hơn về mức thấp h&agrave;ng năm l&agrave; c&oacute; thể xảy ra.</p>\r\n\r\n<p dir=\"ltr\">Quan điểm giảm gi&aacute; tr&ecirc;n của ch&uacute;ng t&ocirc;i sẽ bị v&ocirc; hiệu nếu xu hướng đảo chiều từ c&aacute;c mức hiện tại v&agrave; đ&agrave; tăng đưa gi&aacute; l&ecirc;n tr&ecirc;n cả hai đường trung b&igrave;nh động v&agrave; mức 0.028 USD.</p>\r\n\r\n<h2><strong>BSV/USD</strong></h2>\r\n\r\n<p dir=\"ltr\">Bitcoin SV kh&ocirc;ng giữ được đường trung b&igrave;nh động. N&oacute; hiện đang c&oacute; khả năng sẽ ph&aacute; vỡ dưới mức hỗ trợ 65.03 USD. Điều n&agrave;y cho thấy sự thiếu hụt của b&ecirc;n mua ở c&aacute;c mức cao hơn.</p>\r\n\r\n<p dir=\"ltr\"><img height=\"501\" sizes=\"(max-width: 1234px) 100vw, 1234px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/60-1.png\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/60-1.png 1234w, https://blogtien.com/wp-content/uploads/2019/03/60-1-300x122.png 300w, https://blogtien.com/wp-content/uploads/2019/03/60-1-1024x416.png 1024w, https://blogtien.com/wp-content/uploads/2019/03/60-1-768x312.png 768w\" width=\"1234\" /></p>\r\n\r\n<p dir=\"ltr\">V&ugrave;ng hỗ trợ ngay lập tức ở ph&iacute;a giảm điểm l&agrave; từ 60 đến 58 USD. Nếu cặp BSV/USD giảm xuống dưới v&ugrave;ng n&agrave;y, th&igrave; đồng coin c&oacute; thể giảm tiếp xuống mức thấp 38.52 USD. Với cả hai đường trung b&igrave;nh động đang dốc xuống v&agrave; chỉ số RSI trong v&ugrave;ng &acirc;m, điều n&agrave;y cho thấy sự ti&ecirc;u cực về ph&iacute;a giảm.</p>\r\n\r\n<p dir=\"ltr\">Ngược lại, nếu đồng coin n&agrave;y c&oacute; thể giữ tr&ecirc;n mức hỗ trợ, b&ograve; sẽ cố gắng đẩy gi&aacute; l&ecirc;n tr&ecirc;n 77.03 USD. Nếu th&agrave;nh c&ocirc;ng, n&oacute; c&oacute; thể tăng l&ecirc;n 102 USD v&agrave; cao hơn l&agrave; 123 USD. Do đ&oacute;, ch&uacute;ng t&ocirc;i c&oacute; thể sẽ đề xuất c&aacute;c vị thế d&agrave;i nếu gi&aacute; duy tr&igrave; tr&ecirc;n 78 USD.</p>', '37_Phân Tích Thị Trường3.jpg', '6', '07-08-2021', '2021-08-06 11:39:39', '2021-08-06 11:39:39');
INSERT INTO `posts` (`id`, `title`, `views`, `short_desc`, `desc`, `image`, `post_category_id`, `post_date`, `created_at`, `updated_at`) VALUES
(25, 'Phân tích giá Bitcoin 11/03: Bitcoin có lẽ vẫn chưa thể phá vỡ mốc 4.000 USD.', '110', '<p>Nếu Bitcoin tho&aacute;t khỏi mốc 4.00 USD, n&oacute; c&oacute; thể tăng l&ecirc;n đến 4.300 USD trong khi h&agrave;nh vi ph&aacute; vỡ mức thấp hơn c&oacute; thể th&uacute;c đẩy hiện tượng đảo ngược xu hướng. Mặc d&ugrave; vậy, SMA 100 nằm tr&ecirc;n SMA 200 d&agrave;i hạn; do đ&oacute;, con đường c&oacute; &iacute;t sự kh&aacute;ng cự nhất l&agrave; tăng l&ecirc;n. C&aacute;c đường trung b&igrave;nh động cũng đang nằm gần với mức hỗ trợ k&ecirc;nh, điều n&agrave;y sẽ tăng th&ecirc;m sức mạnh cho phe b&ograve;.</p>', '<p><strong>Bitcoin vẫn đang c&oacute; xu hướng tăng khi n&oacute; h&igrave;nh th&agrave;nh mức thấp cao hơn v&agrave; mức cao hơn trong một k&ecirc;nh tăng dần.&nbsp;</strong></p>\r\n\r\n<ul>\r\n	<li><strong><a href=\"https://blogtien.com/voi-khoi-luong-dat-muc-cao-nhat-ke-tu-nam-2017-cong-dong-dang-cho-doi-bitcoin-bung-no/\" rel=\"noopener noreferrer\" target=\"_blank\">Với khối lượng đạt mức cao nhất kể từ năm 2017, cộng đồng đang chờ đợi Bitcoin b&ugrave;ng nổ.</a></strong></li>\r\n	<li><strong><a href=\"https://blogtien.com/bo-tai-chinh-duc-de-nghi-dung-blockchain-de-quan-ly-chung-khoan/\" rel=\"noopener noreferrer\" target=\"_blank\">Bộ T&agrave;i ch&iacute;nh Đức đề nghị d&ugrave;ng blockchain để quản l&yacute; chứng kho&aacute;n</a></strong></li>\r\n	<li><strong><a href=\"https://blogtien.com/fidelity-mo-cua-phuc-vu-nha-dau-tu-bitcoin/\" rel=\"noopener noreferrer\" target=\"_blank\">Fidelity mở cửa phục vụ nh&agrave; đầu tư Bitcoin</a></strong></li>\r\n</ul>\r\n\r\n<p>Nếu Bitcoin tho&aacute;t khỏi mốc 4.00 USD, n&oacute; c&oacute; thể tăng l&ecirc;n đến 4.300 USD trong khi h&agrave;nh vi ph&aacute; vỡ mức thấp hơn c&oacute; thể th&uacute;c đẩy hiện tượng đảo ngược xu hướng. Mặc d&ugrave; vậy, SMA 100 nằm tr&ecirc;n SMA 200 d&agrave;i hạn; do đ&oacute;, con đường c&oacute; &iacute;t sự kh&aacute;ng cự nhất l&agrave; tăng l&ecirc;n. C&aacute;c đường trung b&igrave;nh động cũng đang nằm gần với mức hỗ trợ k&ecirc;nh, điều n&agrave;y sẽ tăng th&ecirc;m sức mạnh cho phe b&ograve;.</p>\r\n\r\n<p>RSI đang đi xuống b&aacute;o hiệu rằng &aacute;p lực b&aacute;n đang diễn ra. Điều n&agrave;y c&oacute; thể đủ để th&uacute;c đẩy một đ&agrave; ph&aacute; vỡ dưới khu vực 3.800 USD v&agrave; duy tr&igrave; đ&agrave; trượt đến khu vực thấp tiếp theo. Stochastic cũng đang đi xuống v&ugrave;ng thấp hơn sau khi đạt đến v&ugrave;ng qu&aacute; mua, cho thấy người mua đ&atilde; kiệt sức v&agrave; sẵn s&agrave;ng để người b&aacute;n tiếp quản thị trường.</p>\r\n\r\n<p><img height=\"626\" sizes=\"(max-width: 1048px) 100vw, 1048px\" src=\"https://blogtien.com/wp-content/uploads/2019/03/4-3.jpg\" srcset=\"https://blogtien.com/wp-content/uploads/2019/03/4-3.jpg 1048w, https://blogtien.com/wp-content/uploads/2019/03/4-3-300x179.jpg 300w, https://blogtien.com/wp-content/uploads/2019/03/4-3-1024x612.jpg 1024w, https://blogtien.com/wp-content/uploads/2019/03/4-3-768x459.jpg 768w\" width=\"1048\" /></p>\r\n\r\n<p>Bitcoin đang vật lộn để duy tr&igrave; c&aacute;c đ&agrave; tăng gần đ&acirc;y của n&oacute;. Vẫn c&ograve;n một số tin tức v&agrave; dự đo&aacute;n t&iacute;ch cực, nhưng những kỳ vọng n&agrave;y cần phải được hiện thực h&oacute;a với c&aacute;c cập nhật thực tế để cung cấp sức mạnh cho phe b&ograve;.</p>\r\n\r\n<p>Việc thiếu bất kỳ bản cập nhật quan trọng n&agrave;o trong tuần n&agrave;y cũng c&oacute; thể buộc những ch&uacute; b&ograve; phải nhường chỗ cho đ&agrave;n gấu. Dự b&aacute;o c&oacute; vẻ lộn xộn hơn trong những ng&agrave;y n&agrave;y, khi một số chuy&ecirc;n gia duy tr&igrave; dự đo&aacute;n 5.000 USD trong ngắn hạn v&agrave; một số người kh&aacute;c dự đo&aacute;n rằng n&oacute; c&oacute; thể giảm xuống mức1.850 USD nếu c&aacute;c mức hỗ trợ bị ph&aacute; vỡ.</p>\r\n\r\n<p>Ngo&agrave;i ra c&ograve;n c&oacute; một số b&aacute;o c&aacute;o tiết lộ rằng doanh thu của c&aacute;c c&ocirc;ng ty khai th&aacute;c Bitcoin đ&atilde; giảm xuống mức thấp mới trong th&aacute;ng 2, giảm 10% kể từ đầu năm.&nbsp;</p>', '37_Phân Tích Thị Trường4.jpg', '6', '07-08-2021', '2021-08-06 11:41:33', '2021-08-06 11:41:33');

